INSERT INTO `cve_general` VALUES ('CVE-2014-2097', '1', 'ffmpeg', '', '', 'buffer-overflow', 'tak_decode_frame', 'tak_decode_frame', '', 'The tak_decode_frame function in libavcodec/takdec.c in FFmpeg before 2.1.4 does not properly validate a certain bits-per-sample value, which allows remote attackers to cause a denial of service (out-of-bounds array access) or possibly have unspecified other impact via crafted TAK (aka Tom\'s lossless Audio Kompressor) data.', 'http://git.videolan.org/?p=ffmpeg.git;a=commit;h=f58eab151214d2d35ff0973f2b3e51c5eb372da4', 'CWE-20', '-1', '6.8', '', 'AV:N/AC:M/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2014-2098', '1', 'ffmpeg', '', '', 'memory_corruption', null, null, '', 'libavcodec/wmalosslessdec.c in FFmpeg before 2.1.4 uses an incorrect data-structure size for certain coefficients, which allows remote attackers to cause a denial of service (memory corruption) or possibly have unspecified other impact via crafted WMA data.', 'http://git.videolan.org/?p=ffmpeg.git;a=commit;h=ec9578d54d09b64bf112c2bf7a34b1ef3b93dbd3', 'CWE-119', '-1', '6.8', '', 'AV:N/AC:M/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2014-2099', '1', 'ffmpeg', '', '', 'buffer-overflow', 'msrle_decode_frame', 'msrle_decode_frame', '', 'The msrle_decode_frame function in libavcodec/msrle.c in FFmpeg before 2.1.4 does not properly calculate line sizes, which allows remote attackers to cause a denial of service (out-of-bounds array access) or possibly have unspecified other impact via crafted Microsoft RLE video data.', 'http://git.videolan.org/?p=ffmpeg.git;a=commit;h=c919e1ca2ecfc47d796382973ba0e48b8f6f92a2', 'CWE-189', '-1', '6.8', '', 'AV:N/AC:M/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2014-2263', '1', 'ffmpeg', '', '', 'buffer-overflow', 'mpegts_write_pmt', 'mpegts_write_pmt', '', 'The mpegts_write_pmt function in the MPEG2 transport stream (aka DVB) muxer (libavformat/mpegtsenc.c) in FFmpeg, possibly 2.1 and earlier, allows remote attackers to have unspecified impact and vectors, which trigger an out-of-bounds write.', 'http://git.videolan.org/?p=ffmpeg.git;a=commit;h=842b6c14bc', 'CWE-119', '-1', '6.8', '', 'AV:N/AC:M/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2014-3157', '1', 'ffmpeg', '', '', 'buffer-overflow', 'ffmpegvideodecoder::getvideobuffer', 'getvideobuffer', '', 'Heap-based buffer overflow in the FFmpegVideoDecoder::GetVideoBuffer function in media/filters/ffmpeg_video_decoder.cc in Google Chrome before 35.0.1916.153 allows remote attackers to cause a denial of service or possibly have unspecified other impact by leveraging VideoFrame data structures that are too small for proper interaction with an underlying FFmpeg library.', 'http://googlechromereleases.blogspot.com/2014/06/stable-channel-update.html###https://code.google.com/p/chromium/issues/detail?id=368980###https://src.chromium.org/viewvc/chrome?revision=268831&view=revision', 'CWE-119', '-1', '7.5', '', 'AV:N/AC:L/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2014-4610', '1', 'ffmpeg', '', '', 'integer-overflow', 'get_len', 'get_len', '', 'Integer overflow in the get_len function in libavutil/lzo.c in FFmpeg before 0.10.14, 1.1.x before 1.1.12, 1.2.x before 1.2.7, 2.0.x before 2.0.5, 2.1.x before 2.1.5, and 2.2.x before 2.2.4 allows remote attackers to execute arbitrary code via a crafted Literal Run.', 'http://blog.securitymouse.com/2014/06/raising-lazarus-20-year-old-bug-that.html###http://www.openwall.com/lists/oss-security/2014/06/26/23###https://www.ffmpeg.org/security.html', 'CWE-190', '8.8', '6.8', 'CVSS:3.1/AV:N/AC:L/PR:N/UI:R/S:U/C:H/I:H/A:H', 'AV:N/AC:M/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2014-5271', '1', 'ffmpeg', '', '', 'buffer-overflow', 'encode_slice', 'encode_slice', '', 'Heap-based buffer overflow in the encode_slice function in libavcodec/proresenc_kostya.c in FFMpeg before 1.1.14, 1.2.x before 1.2.8, 2.x before 2.2.7, and 2.3.x before 2.3.3 and Libav before 10.5 allows remote attackers to cause a denial of service (crash) or possibly execute arbitrary code via unspecified vectors.', 'http://git.videolan.org/?p=ffmpeg.git;a=commit;h=52b81ff4635c077b2bc8b8d3637d933b6629d803###https://www.ffmpeg.org/security.html', 'CWE-119', '-1', '7.5', '', 'AV:N/AC:L/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2014-5272', '1', 'ffmpeg', '', '', 'buffer-overflow', null, null, '', 'libavcodec/iff.c in FFMpeg before 1.1.14, 1.2.x before 1.2.8, 2.2.x before 2.2.7, and 2.3.x before 2.3.2 allows remote attackers to have unspecified impact via a crafted iff image, which triggers an out-of-bounds array access, related to the rgb8 and rgbn formats.', 'http://git.videolan.org/?p=ffmpeg.git;a=commit;h=abc1fa7c5a1dca1345b9471b81cfcda00c56220d###https://www.ffmpeg.org/security.html', 'CWE-119', '-1', '6.8', '', 'AV:N/AC:M/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2014-7933', '1', 'ffmpeg', '', '', 'use-after-free', 'matroska_read_seek', 'matroska_read_seek', '', 'Use-after-free vulnerability in the matroska_read_seek function in libavformat/matroskadec.c in FFmpeg before 2.5.1, as used in Google Chrome before 40.0.2214.91, allows remote attackers to cause a denial of service or possibly have unspecified other impact via a crafted Matroska file that triggers improper maintenance of tracks data.', 'http://git.videolan.org/?p=ffmpeg.git;a=commit;h=490a3ebf36821b81f73e34ad3f554cb523dd2682###http://googlechromereleases.blogspot.com/2015/01/stable-update.html###https://code.google.com/p/chromium/issues/detail?id=427266', 'NVD-CWE-Other', '-1', '7.5', '', 'AV:N/AC:L/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2014-7937', '1', 'ffmpeg', '', '', 'buffer-overflow', null, null, '', 'Multiple off-by-one errors in libavcodec/vorbisdec.c in FFmpeg before 2.4.2, as used in Google Chrome before 40.0.2214.91, allow remote attackers to cause a denial of service (use-after-free) or possibly have unspecified other impact via crafted Vorbis I data.', 'http://git.videolan.org/?p=ffmpeg.git;a=commit;h=8c50704ebf1777bee76772c4835d9760b3721057###http://googlechromereleases.blogspot.com/2015/01/stable-update.html###https://chromium.googlesource.com/chromium/third_party/ffmpeg/+/d4608b7c83f56b17f14fdd94990341f62bb52f92###https://code.google.com/p/chromium/issues/detail?id=419060', 'CWE-119', '-1', '7.5', '', 'AV:N/AC:L/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2014-8127', '1', 'libtiff', '', '4.0.3', 'buffer-overflow', 'checkinknamesstring', 'checkinknamesstring', '', 'LibTIFF 4.0.3 allows remote attackers to cause a denial of service (out-of-bounds read and crash) via a crafted TIFF image to the (1) checkInkNamesString function in tif_dir.c in the thumbnail tool, (2) compresscontig function in tiff2bw.c in the tiff2bw tool, (3) putcontig8bitCIELab function in tif_getimage.c in the tiff2rgba tool, LZWPreDecode function in tif_lzw.c in the (4) tiff2ps or (5) tiffdither tool, (6) NeXTDecode function in tif_next.c in the tiffmedian tool, or (7) TIFFWriteDirectoryTagLongLong8Array function in tif_dirwrite.c in the tiffset tool.', 'http://bugzilla.maptools.org/show_bug.cgi?id=2484###http://bugzilla.maptools.org/show_bug.cgi?id=2485###http://bugzilla.maptools.org/show_bug.cgi?id=2486###http://bugzilla.maptools.org/show_bug.cgi?id=2496###http://bugzilla.maptools.org/show_bug.cgi?id=2497###http://bugzilla.maptools.org/show_bug.cgi?id=2500###http://www.conostix.com/pub/adv/CVE-2014-8127-LibTIFF-Out-of-bounds_Reads.txt', 'CWE-125', '6.5', '4.3', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2014-8129', '1', 'libtiff', '', '4.0.3', 'buffer-overflow', 't2p_sample_lab_signed_to_unsigned', 't2p_sample_lab_signed_to_unsigned', '', 'LibTIFF 4.0.3 allows remote attackers to cause a denial of service (out-of-bounds write) or possibly have unspecified other impact via a crafted TIFF image, as demonstrated by failure of tif_next.c to verify that the BitsPerSample value is 2, and the t2p_sample_lab_signed_to_unsigned function in tiff2pdf.c.', 'http://support.apple.com/kb/HT204941###http://support.apple.com/kb/HT204942###https://bugzilla.redhat.com/show_bug.cgi?id=1185815###http://bugzilla.maptools.org/show_bug.cgi?id=2487###http://bugzilla.maptools.org/show_bug.cgi?id=2488###http://www.conostix.com/pub/adv/CVE-2014-8129-LibTIFF-Out-of-bounds_Reads_and_Writes.txt', 'CWE-787', '8.8', '6.8', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:R/S:U/C:H/I:H/A:H', 'AV:N/AC:M/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2014-8130', '1', 'libtiff', '', '4.0.3', 'FloatingPointException', '_tiffmalloc', '_tiffmalloc', '', 'The _TIFFmalloc function in tif_unix.c in LibTIFF 4.0.3 does not reject a zero size, which allows remote attackers to cause a denial of service (divide-by-zero error and application crash) via a crafted TIFF image that is mishandled by the TIFFWriteScanline function in tif_write.c, as demonstrated by tiffdither.', 'http://bugzilla.maptools.org/show_bug.cgi?id=2483###http://support.apple.com/kb/HT204941###http://support.apple.com/kb/HT204942###https://bugzilla.redhat.com/show_bug.cgi?id=1185817###https://github.com/vadz/libtiff/commit/3c5eb8b1be544e41d2c336191bc4936300ad7543###http://www.conostix.com/pub/adv/CVE-2014-8130-LibTIFF-Division_By_Zero.txt', 'CWE-369', '6.5', '4.3', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2014-8137', '1', 'jasper', '', '1.900.1', 'double-free', 'jas_iccattrval_destroy', 'jas_iccattrval_destroy', '', 'Double free vulnerability in the jas_iccattrval_destroy function in JasPer 1.900.1 and earlier allows remote attackers to cause a denial of service (crash) or possibly execute arbitrary code via a crafted ICC color profile in a JPEG 2000 image file.', 'http://advisories.mageia.org/MGASA-2014-0539.html###http://packetstormsecurity.com/files/129660/JasPer-1.900.1-Double-Free-Heap-Overflow.html###https://www.ocert.org/advisories/ocert-2014-012.html', 'NVD-CWE-Other', '-1', '6.8', '', 'AV:N/AC:M/Au:N/C:P/I:P/A:P');
