INSERT INTO `cve_general` VALUES ('CVE-2014-8484', '1', 'binutils', '', '', 'buffer-overflow', 'srec_scan', 'srec_scan', '', 'The srec_scan function in bfd/srec.c in libdbfd in GNU binutils before 2.25 allows remote attackers to cause a denial of service (out-of-bounds read) via a small S-record.', 'http://www.oracle.com/technetwork/topics/security/linuxbulletinoct2015-2719645.html###https://bugzilla.redhat.com/show_bug.cgi?id=1156272###https://sourceware.org/bugzilla/show_bug.cgi?id=17509###https://sourceware.org/git/?p=binutils-gdb.git;a=commit;h=bd25671c6f202c4a5108883caa2adb24ff6f361f', 'CWE-119', '-1', '5', '', 'AV:N/AC:L/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2014-8485', '1', 'binutils', '', '', null, 'setup_group', 'setup_group', '', 'The setup_group function in bfd/elf.c in libbfd in GNU binutils 2.24 and earlier allows remote attackers to cause a denial of service (crash) and possibly execute arbitrary code via crafted section group headers in an ELF file.', 'http://www.oracle.com/technetwork/topics/security/linuxbulletinoct2015-2719645.html###https://bugzilla.redhat.com/show_bug.cgi?id=1157276###https://sourceware.org/bugzilla/show_bug.cgi?id=17510###https://sourceware.org/git/gitweb.cgi?p=binutils-gdb.git;a=commit;h=493a33860c71cac998f1a56d6d87d6faa801fbaa###http://lcamtuf.blogspot.co.uk/2014/10/psa-dont-run-strings-on-untrusted-files.html', 'CWE-94', '-1', '7.5', '', 'AV:N/AC:L/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2014-8501', '1', 'binutils', '', '', 'buffer-overflow', '_bfd_xxi_swap_aouthdr_in', '_bfd_xxi_swap_aouthdr_in', '', 'The _bfd_XXi_swap_aouthdr_in function in bfd/peXXigen.c in GNU binutils 2.24 and earlier allows remote attackers to cause a denial of service (out-of-bounds write) and possibly have other unspecified impact via a crafted NumberOfRvaAndSizes field in the AOUT header in a PE executable.', 'http://www.oracle.com/technetwork/topics/security/linuxbulletinoct2015-2719645.html###https://bugzilla.redhat.com/show_bug.cgi?id=1162570###https://sourceware.org/bugzilla/show_bug.cgi?id=17512###https://sourceware.org/git/gitweb.cgi?p=binutils-gdb.git;h=7e1e19887abd24aeb15066b141cdff5541e0ec8e', 'CWE-119', '-1', '7.5', '', 'AV:N/AC:L/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2014-8502', '1', 'binutils', '', '', 'buffer-overflow', 'pe_print_edata', 'pe_print_edata', '', 'Heap-based buffer overflow in the pe_print_edata function in bfd/peXXigen.c in GNU binutils 2.24 and earlier allows remote attackers to cause a denial of service (crash) and possibly have other unspecified impact via a truncated export table in a PE file.', 'http://www.oracle.com/technetwork/topics/security/linuxbulletinoct2015-2719645.html###https://bugzilla.redhat.com/show_bug.cgi?id=1162594###https://sourceware.org/bugzilla/show_bug.cgi?id=17512###https://sourceware.org/git/gitweb.cgi?p=binutils-gdb.git;h=5a4b0ccc20ba30caef53b01bee2c0aaa5b855339', 'CWE-119', '-1', '7.5', '', 'AV:N/AC:L/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2014-8503', '1', 'binutils', '', '', 'stack-buffer-overflow', 'ihex_scan', 'ihex_scan', '', 'Stack-based buffer overflow in the ihex_scan function in bfd/ihex.c in GNU binutils 2.24 and earlier allows remote attackers to cause a denial of service (crash) and possibly have other unspecified impact via a crafted ihex file.', 'http://www.oracle.com/technetwork/topics/security/linuxbulletinoct2015-2719645.html###https://bugzilla.redhat.com/show_bug.cgi?id=1162607###https://sourceware.org/bugzilla/show_bug.cgi?id=17512###https://sourceware.org/git/gitweb.cgi?p=binutils-gdb.git;a=commit;h=0102ea8cec5fc509bba6c91df61b7ce23a799d32', 'CWE-119', '-1', '7.5', '', 'AV:N/AC:L/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2014-8504', '1', 'binutils', '', '', 'stack-buffer-overflow', 'srec_scan', 'srec_scan', '', 'Stack-based buffer overflow in the srec_scan function in bfd/srec.c in GNU binutils 2.24 and earlier allows remote attackers to cause a denial of service (crash) and possibly have other unspecified impact via a crafted file.', 'https://bugzilla.redhat.com/show_bug.cgi?id=1162621###https://sourceware.org/bugzilla/show_bug.cgi?id=17510###https://sourceware.org/git/gitweb.cgi?p=binutils-gdb.git;h=708d7d0d11f0f2d776171979aa3479e8e12a38a0', 'CWE-119', '-1', '7.5', '', 'AV:N/AC:L/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2014-8541', '1', 'ffmpeg', '', '', 'buffer-overflow', null, null, '', 'libavcodec/mjpegdec.c in FFmpeg before 2.4.2 considers only dimension differences, and not bits-per-pixel differences, when determining whether an image size has changed, which allows remote attackers to cause a denial of service (out-of-bounds access) or possibly have unspecified other impact via crafted MJPEG data.', 'http://git.videolan.org/?p=ffmpeg.git;a=commit;h=5c378d6a6df8243f06c87962b873bd563e58cd39###http://www.ffmpeg.org/security.html', 'CWE-119', '-1', '7.5', '', 'AV:N/AC:L/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2014-8542', '1', 'ffmpeg', '', '', 'buffer-overflow', null, null, '', 'libavcodec/utils.c in FFmpeg before 2.4.2 omits a certain codec ID during enforcement of alignment, which allows remote attackers to cause a denial of service (out-of-bounds access) or possibly have unspecified other impact via crafted JV data.', 'http://git.videolan.org/?p=ffmpeg.git;a=commit;h=105654e376a736d243aef4a1d121abebce912e6b###http://www.ffmpeg.org/security.html', 'CWE-119', '-1', '7.5', '', 'AV:N/AC:L/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2014-8543', '1', 'ffmpeg', '', '', 'buffer-overflow', null, null, '', 'libavcodec/mmvideo.c in FFmpeg before 2.4.2 does not consider all lines of HHV Intra blocks during validation of image height, which allows remote attackers to cause a denial of service (out-of-bounds access) or possibly have unspecified other impact via crafted MM video data.', 'http://git.videolan.org/?p=ffmpeg.git;a=commit;h=8b0e96e1f21b761ca15dbb470cd619a1ebf86c3e###http://www.ffmpeg.org/security.html', 'CWE-20', '-1', '7.5', '', 'AV:N/AC:L/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2014-8544', '1', 'ffmpeg', '', '', 'buffer-overflow', null, null, '', 'libavcodec/tiff.c in FFmpeg before 2.4.2 does not properly validate bits-per-pixel fields, which allows remote attackers to cause a denial of service (out-of-bounds access) or possibly have unspecified other impact via crafted TIFF data.', 'http://git.videolan.org/?p=ffmpeg.git;a=commit;h=e1c0cfaa419aa5d320540d5a1b3f8fd9b82ab7e5###http://www.ffmpeg.org/security.html', 'CWE-20', '-1', '7.5', '', 'AV:N/AC:L/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2014-8545', '1', 'ffmpeg', '', '', 'buffer-overflow', null, null, '', 'libavcodec/pngdec.c in FFmpeg before 2.4.2 accepts the monochrome-black format without verifying that the bits-per-pixel value is 1, which allows remote attackers to cause a denial of service (out-of-bounds access) or possibly have unspecified other impact via crafted PNG data.', 'http://git.videolan.org/?p=ffmpeg.git;a=commit;h=3e2b745020c2dbf0201fe7df3dad9e7e0b2e1bb6###http://www.ffmpeg.org/security.html', 'CWE-189', '-1', '7.5', '', 'AV:N/AC:L/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2014-8546', '1', 'ffmpeg', '', '', 'integer-underflow', null, null, '', 'Integer underflow in libavcodec/cinepak.c in FFmpeg before 2.4.2 allows remote attackers to cause a denial of service (out-of-bounds access) or possibly have unspecified other impact via crafted Cinepak video data.', 'http://git.videolan.org/?p=ffmpeg.git;a=commit;h=e7e5114c506957f40aafd794e06de1a7e341e9d5###http://www.ffmpeg.org/security.html', 'CWE-189', '-1', '7.5', '', 'AV:N/AC:L/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2014-8547', '1', 'ffmpeg', '', '', 'buffer-overflow', null, null, '', 'libavcodec/gifdec.c in FFmpeg before 2.4.2 does not properly compute image heights, which allows remote attackers to cause a denial of service (out-of-bounds access) or possibly have unspecified other impact via crafted GIF data.', 'http://git.videolan.org/?p=ffmpeg.git;a=commit;h=8f1457864be8fb9653643519dea1c6492f1dde57###http://www.ffmpeg.org/security.html', 'CWE-119', '-1', '7.5', '', 'AV:N/AC:L/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2014-8548', '1', 'ffmpeg', '', '', 'buffer-overflow', null, null, '', 'Off-by-one error in libavcodec/smc.c in FFmpeg before 2.4.2 allows remote attackers to cause a denial of service (out-of-bounds access) or possibly have unspecified other impact via crafted Quicktime Graphics (aka SMC) video data.', 'http://git.videolan.org/?p=ffmpeg.git;a=commit;h=c727401aa9d62335e89d118a5b4e202edf39d905###http://www.ffmpeg.org/security.html', 'CWE-119', '-1', '7.5', '', 'AV:N/AC:L/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2014-8549', '1', 'ffmpeg', '', '', 'buffer-overflow', null, null, '', 'libavcodec/on2avc.c in FFmpeg before 2.4.2 does not constrain the number of channels to at most 2, which allows remote attackers to cause a denial of service (out-of-bounds access) or possibly have unspecified other impact via crafted On2 data.', 'http://git.videolan.org/?p=ffmpeg.git;a=commit;h=550f3e9df3410b3dd975e590042c0d83e20a8da3###http://www.ffmpeg.org/security.html', 'CWE-189', '-1', '7.5', '', 'AV:N/AC:L/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2014-8737', '1', 'binutils', '', '', null, null, null, '', 'Multiple directory traversal vulnerabilities in GNU binutils 2.24 and earlier allow local users to delete arbitrary files via a .. (dot dot) or full path name in an archive to (1) strip or (2) objcopy or create arbitrary files via (3) a .. (dot dot) or full path name in an archive to ar.', 'http://www.oracle.com/technetwork/topics/security/linuxbulletinoct2015-2719645.html###https://bugzilla.redhat.com/show_bug.cgi?id=1162655###https://sourceware.org/bugzilla/show_bug.cgi?id=17533###https://sourceware.org/bugzilla/show_bug.cgi?id=17552###https://sourceware.org/git/gitweb.cgi?p=binutils-gdb.git;h=dd9b91de2149ee81d47f708e7b0bbf57da10ad42', 'CWE-22', '-1', '3.6', '', 'AV:L/AC:L/Au:N/C:N/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2014-8738', '1', 'binutils', '', '', 'SEGV', '_bfd_slurp_extended_name_table', '_bfd_slurp_extended_name_table', '', 'The _bfd_slurp_extended_name_table function in bfd/archive.c in GNU binutils 2.24 and earlier allows remote attackers to cause a denial of service (invalid write, segmentation fault, and crash) via a crafted extended name table in an archive.', 'http://www.oracle.com/technetwork/topics/security/linuxbulletinoct2015-2719645.html###https://sourceware.org/bugzilla/show_bug.cgi?id=17533###https://sourceware.org/git/gitweb.cgi?p=binutils-gdb.git;h=bb0d867169d7e9743d229804106a8fbcab7f3b3f', 'CWE-119', '-1', '5', '', 'AV:N/AC:L/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2014-8767', '1', 'tcpdump', '', '', 'integer-underflow', 'olsr_print', 'olsr_print', '', 'Integer underflow in the olsr_print function in tcpdump 3.9.6 through 4.6.2, when in verbose mode, allows remote attackers to cause a denial of service (crash) via a crafted length value in an OLSR frame.', 'http://advisories.mageia.org/MGASA-2014-0503.html###http://www.oracle.com/technetwork/topics/security/bulletinjul2015-2511963.html###https://support.apple.com/kb/HT205031###http://packetstormsecurity.com/files/129155/tcpdump-4.6.2-OSLR-Denial-Of-Service.html', 'CWE-189', '-1', '5', '', 'AV:N/AC:L/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2014-8768', '1', 'tcpdump', '', '', 'SEGV', 'geonet_print', 'geonet_print', '', 'Multiple Integer underflows in the geonet_print function in tcpdump 4.5.0 through 4.6.2, when in verbose mode, allow remote attackers to cause a denial of service (segmentation fault and crash) via a crafted length value in a Geonet frame.', 'http://www.oracle.com/technetwork/topics/security/bulletinjul2015-2511963.html###http://packetstormsecurity.com/files/129156/tcpdump-4.6.2-Geonet-Denial-Of-Service.html', 'CWE-191', '-1', '5', '', 'AV:N/AC:L/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2014-8769', '1', 'tcpdump', '', '', 'buffer-overflow', null, null, '', 'tcpdump 3.8 through 4.6.2 might allow remote attackers to obtain sensitive information from memory or cause a denial of service (packet loss or segmentation fault) via a crafted Ad hoc On-Demand Distance Vector (AODV) packet, which triggers an out-of-bounds memory access.', 'http://advisories.mageia.org/MGASA-2014-0503.html###http://www.oracle.com/technetwork/topics/security/bulletinjul2015-2511963.html###https://support.apple.com/kb/HT205031###http://packetstormsecurity.com/files/129157/tcpdump-4.6.2-AOVD-Unreliable-Output.html', 'CWE-119', '-1', '6.4', '', 'AV:N/AC:L/Au:N/C:P/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2014-9029', '1', 'jasper', '', '1.900.1', 'buffer-overflow', null, null, '', 'Multiple off-by-one errors in the (1) jpc_dec_cp_setfromcox and (2) jpc_dec_cp_setfromrgn functions in jpc/jpc_dec.c in JasPer 1.900.1 and earlier allow remote attackers to execute arbitrary code via a crafted jp2 file, which triggers a heap-based buffer overflow.', 'http://advisories.mageia.org/MGASA-2014-0514.html###http://www.oracle.com/technetwork/security-advisory/cpujul2018-4258247.html###https://bugzilla.redhat.com/show_bug.cgi?id=1167537###http://packetstormsecurity.com/files/129393/JasPer-1.900.1-Buffer-Overflow.html###http://www.ocert.org/advisories/ocert-2014-009.html', 'CWE-189', '-1', '7.5', '', 'AV:N/AC:L/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2014-9140', '1', 'tcpdump', '', '', 'buffer-overflow', 'ppp_hdlc', 'ppp_hdlc', '', 'Buffer overflow in the ppp_hdlc function in print-ppp.c in tcpdump 4.6.2 and earlier allows remote attackers to cause a denial of service (crash) cia a crafted PPP packet.', 'http://advisories.mageia.org/MGASA-2014-0511.html###https://github.com/the-tcpdump-group/tcpdump/commit/0f95d441e4b5d7512cc5c326c8668a120e048eda###https://support.apple.com/kb/HT205031###http://packetstormsecurity.com/files/130730/tcpdump-Denial-Of-Service-Code-Execution.html', 'CWE-119', '-1', '5', '', 'AV:N/AC:L/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2014-9316', '1', 'ffmpeg', '', '', 'buffer-overflow', 'mjpeg_decode_app', 'mjpeg_decode_app', '', 'The mjpeg_decode_app function in libavcodec/mjpegdec.c in FFMpeg before 2.1.6, 2.2.x through 2.3.x, and 2.4.x before 2.4.4 allows remote attackers to cause a denial of service (out-of-bounds heap access) and possibly have other unspecified impact via vectors related to LJIF tags in an MJPEG file.', 'http://git.videolan.org/?p=ffmpeg.git;a=commit;h=0eecf40935b22644e6cd74c586057237ecfd6844###https://www.ffmpeg.org/security.html', 'CWE-119', '-1', '7.5', '', 'AV:N/AC:L/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2014-9317', '1', 'ffmpeg', '', '', 'buffer-overflow', 'decode_ihdr_chunk', 'decode_ihdr_chunk', '', 'The decode_ihdr_chunk function in libavcodec/pngdec.c in FFMpeg before 2.1.6, 2.2.x through 2.3.x, and 2.4.x before 2.4.4 allows remote attackers to cause a denial of service (out-of-bounds heap access) and possibly have other unspecified impact via an IDAT before an IHDR in a PNG file.', 'http://git.videolan.org/?p=ffmpeg.git;a=commit;h=79ceaf827be0b070675d4cd0a55c3386542defd8###https://www.ffmpeg.org/security.html', 'CWE-119', '-1', '7.5', '', 'AV:N/AC:L/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2014-9318', '1', 'ffmpeg', '', '', 'buffer-overflow', 'raw_decode', 'raw_decode', '', 'The raw_decode function in libavcodec/rawdec.c in FFMpeg before 2.1.6, 2.2.x through 2.3.x, and 2.4.x before 2.4.4 allows remote attackers to cause a denial of service (out-of-bounds heap access) and possibly have other unspecified impact via a crafted .cine file that triggers the avpicture_get_size function to return a negative frame size.', 'http://git.videolan.org/?p=ffmpeg.git;a=commit;h=1d3a3b9f8907625b361420d48fe05716859620ff###https://www.ffmpeg.org/security.html', 'CWE-119', '-1', '7.5', '', 'AV:N/AC:L/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2014-9319', '1', 'ffmpeg', '', '', 'buffer-overflow', 'ff_hevc_decode_nal_sps', 'ff_hevc_decode_nal_sps', '', 'The ff_hevc_decode_nal_sps function in libavcodec/hevc_ps.c in FFMpeg before 2.1.6, 2.2.x through 2.3.x, and 2.4.x before 2.4.4 allows remote attackers to cause a denial of service (out-of-bounds access) via a crafted .bit file.', 'http://git.videolan.org/?p=ffmpeg.git;a=commit;h=ea38e5a6b75706477898eb1e6582d667dbb9946c###https://www.ffmpeg.org/security.html', 'CWE-119', '-1', '5', '', 'AV:N/AC:L/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2014-9330', '1', 'libtiff', '', '4.0.3', 'integer-overflow', null, null, '', 'Integer overflow in tif_packbits.c in bmp2tif in libtiff 4.0.3 allows remote attackers to cause a denial of service (crash) via crafted BMP image, related to dimensions, which triggers an out-of-bounds read.', 'http://bugzilla.maptools.org/show_bug.cgi?id=2494###http://www.oracle.com/technetwork/topics/security/linuxbulletinjul2016-3090544.html###http://www.oracle.com/technetwork/topics/security/ovmbulletinjul2016-3090546.html', 'CWE-189', '-1', '5', '', 'AV:N/AC:L/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2014-9449', '1', 'exiv2', '', '0.24', 'buffer-overflow', 'riffvideo::infotagshandler', 'infotagshandler', '', 'Buffer overflow in the RiffVideo::infoTagsHandler function in riffvideo.cpp in Exiv2 0.24 allows remote attackers to cause a denial of service (crash) via a long IKEY INFO tag value in an AVI file.', 'http://dev.exiv2.org/issues/960###http://dev.exiv2.org/projects/exiv2/repository/diff?rev=3264&rev_to=3263', 'CWE-119', '-1', '5', '', 'AV:N/AC:L/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2014-9602', '1', 'ffmpeg', '', '', 'buffer-overflow', null, null, '', 'libavcodec/xface.h in FFmpeg before 2.5.2 establishes certain digits and words array dimensions that do not satisfy a required mathematical relationship, which allows remote attackers to cause a denial of service (out-of-bounds array access) or possibly have unspecified other impact via crafted X-Face image data.', 'http://git.videolan.org/?p=ffmpeg.git;a=commit;h=93a5a16f136d095d23610f57bdad10ba88120fba', 'CWE-189', '-1', '7.5', '', 'AV:N/AC:L/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2014-9603', '1', 'ffmpeg', '', '', 'buffer-overflow', 'vmd_decode', 'vmd_decode', '', 'The vmd_decode function in libavcodec/vmdvideo.c in FFmpeg before 2.5.2 does not validate the relationship between a certain length value and the frame width, which allows remote attackers to cause a denial of service (out-of-bounds array access) or possibly have unspecified other impact via crafted Sierra VMD video data.', 'http://git.videolan.org/?p=ffmpeg.git;a=commit;h=3030fb7e0d41836f8add6399e9a7c7b740b48bfd', 'CWE-20', '-1', '7.5', '', 'AV:N/AC:L/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2014-9604', '1', 'ffmpeg', '', '', 'buffer-overflow', null, null, '', 'libavcodec/utvideodec.c in FFmpeg before 2.5.2 does not check for a zero value of a slice height, which allows remote attackers to cause a denial of service (out-of-bounds array access) or possibly have unspecified other impact via crafted Ut Video data, related to the (1) restore_median and (2) restore_median_il functions.', 'http://git.videolan.org/?p=ffmpeg.git;a=commit;h=3881606240953b9275a247a1c98a567f3c44890f', 'CWE-189', '-1', '7.5', '', 'AV:N/AC:L/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2014-9655', '1', 'libtiff', '', null, 'uninitialized_memory_access', 'putcontig8bitycbcr21tile', 'putcontig8bitycbcr21tile', '', 'The (1) putcontig8bitYCbCr21tile function in tif_getimage.c or (2) NeXTDecode function in tif_next.c in LibTIFF allows remote attackers to cause a denial of service (uninitialized memory access) via a crafted TIFF image, as demonstrated by libtiff-cvs-1.tif and libtiff-cvs-2.tif.', 'http://www.oracle.com/technetwork/topics/security/linuxbulletinjul2016-3090544.html###http://www.oracle.com/technetwork/topics/security/ovmbulletinjul2016-3090546.html', 'CWE-119', '6.5', '4.3', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2014-9676', '1', 'ffmpeg', '', '', 'use-after-free', 'seg_write_packet', 'seg_write_packet', '', 'The seg_write_packet function in libavformat/segment.c in ffmpeg 2.1.4 and earlier does not free the correct memory location, which allows remote attackers to cause a denial of service (\"invalid memory handler\") and possibly execute arbitrary code via a crafted video that triggers a use after free.', '', 'NVD-CWE-Other', '-1', '6.8', '', 'AV:N/AC:M/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2014-9939', '1', 'binutils', '', '', 'buffer-overflow', null, null, '', 'ihex.c in GNU Binutils before 2.26 contains a stack buffer overflow when printing bad bytes in Intel Hex objects.', 'https://sourceware.org/bugzilla/show_bug.cgi?id=18750###https://sourceware.org/git/gitweb.cgi?p=binutils-gdb.git;h=7e27a9d5f22f9f7ead11738b1546d0b5c737266b###http://www.openwall.com/lists/oss-security/2015/07/31/6', 'CWE-119', '9.8', '7.5', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:N/S:U/C:H/I:H/A:H', 'AV:N/AC:L/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2015-0261', '1', 'tcpdump', '', '', 'buffer-overflow', 'mobility_opt_print', 'mobility_opt_print', '', 'Integer signedness error in the mobility_opt_print function in the IPv6 mobility printer in tcpdump before 4.7.2 allows remote attackers to cause a denial of service (out-of-bounds read and crash) or possibly execute arbitrary code via a negative length value.', 'http://advisories.mageia.org/MGASA-2015-0114.html###http://www.oracle.com/technetwork/topics/security/bulletinjul2015-2511963.html###https://bugzilla.redhat.com/show_bug.cgi?id=1201792###http://packetstormsecurity.com/files/130730/tcpdump-Denial-Of-Service-Code-Execution.html', 'CWE-189', '-1', '7.5', '', 'AV:N/AC:L/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2015-1207', '1', 'ffmpeg', '', '', 'double-free', null, null, '', 'Double-free vulnerability in libavformat/mov.c in FFMPEG in Google Chrome 41.0.2251.0 allows remote attackers to cause a denial of service (memory corruption and crash) via a crafted .m4a file.', 'https://bugs.chromium.org/p/chromium/issues/detail?id=444539###https://gist.github.com/bittorrent3389/8fee7cdaa73d1d351ee9', 'CWE-415', '6.5', '4.3', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2015-1208', '1', 'ffmpeg', '', '', 'integer-underflow', 'mov_read_default', 'mov_read_default', '', 'Integer underflow in the mov_read_default function in libavformat/mov.c in FFmpeg before 2.4.6 allows remote attackers to obtain sensitive information from heap and/or stack memory via a crafted MP4 file.', 'http://git.videolan.org/?p=ffmpeg.git;a=commit;h=3ebd76a9c57558e284e94da367dd23b435e6a6d0###https://bugs.chromium.org/p/chromium/issues/detail?id=444546###https://github.com/FFmpeg/FFmpeg/blob/n2.4.6/Changelog', 'CWE-191', '5.5', '4.3', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:H/I:N/A:N', 'AV:N/AC:M/Au:N/C:P/I:N/A:N');
INSERT INTO `cve_general` VALUES ('CVE-2015-1547', '1', 'libtiff', '', null, 'uninitialized_memory_access', 'nextdecode', 'nextdecode', '', 'The NeXTDecode function in tif_next.c in LibTIFF allows remote attackers to cause a denial of service (uninitialized memory access) via a crafted TIFF image, as demonstrated by libtiff5.tif.', 'http://www.oracle.com/technetwork/topics/security/bulletinapr2016-2952098.html###http://www.oracle.com/technetwork/topics/security/linuxbulletinjul2016-3090544.html###http://www.oracle.com/technetwork/topics/security/ovmbulletinjul2016-3090546.html', 'CWE-119', '6.5', '4.3', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2015-1872', '1', 'ffmpeg', '', '', 'buffer-overflow', 'ff_mjpeg_decode_sof', 'ff_mjpeg_decode_sof', '', 'The ff_mjpeg_decode_sof function in libavcodec/mjpegdec.c in FFmpeg before 2.5.4 does not validate the number of components in a JPEG-LS Start Of Frame segment, which allows remote attackers to cause a denial of service (out-of-bounds array access) or possibly have unspecified other impact via crafted Motion JPEG data.', 'http://git.videolan.org/?p=ffmpeg.git;a=commit;h=fabbfaa095660982cc0bc63242c459561fa37037', 'CWE-119', '-1', '6.8', '', 'AV:N/AC:M/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2015-2153', '1', 'tcpdump', '', '', 'buffer-overflow', 'rpki_rtr_pdu_print', 'rpki_rtr_pdu_print', '', 'The rpki_rtr_pdu_print function in print-rpki-rtr.c in the TCP printer in tcpdump before 4.7.2 allows remote attackers to cause a denial of service (out-of-bounds read or write and crash) via a crafted header length in an RPKI-RTR Protocol Data Unit (PDU).', 'http://advisories.mageia.org/MGASA-2015-0114.html###http://www.oracle.com/technetwork/topics/security/bulletinjul2015-2511963.html###https://bugzilla.redhat.com/show_bug.cgi?id=1201795###http://packetstormsecurity.com/files/130730/tcpdump-Denial-Of-Service-Code-Execution.html', 'CWE-119', '-1', '5', '', 'AV:N/AC:L/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2015-2154', '1', 'tcpdump', '', '', 'buffer-overflow', 'osi_print_cksum', 'osi_print_cksum', '', 'The osi_print_cksum function in print-isoclns.c in the ethernet printer in tcpdump before 4.7.2 allows remote attackers to cause a denial of service (out-of-bounds read and crash) via a crafted (1) length, (2) offset, or (3) base pointer checksum value.', 'http://advisories.mageia.org/MGASA-2015-0114.html###http://www.oracle.com/technetwork/topics/security/bulletinjul2015-2511963.html###https://bugzilla.redhat.com/show_bug.cgi?id=1201797###http://packetstormsecurity.com/files/130730/tcpdump-Denial-Of-Service-Code-Execution.html', 'CWE-119', '-1', '5', '', 'AV:N/AC:L/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2015-2155', '1', 'tcpdump', '', '', null, null, null, '', 'The force printer in tcpdump before 4.7.2 allows remote attackers to cause a denial of service (crash) and possibly execute arbitrary code via unspecified vectors.', 'http://advisories.mageia.org/MGASA-2015-0114.html###http://www.oracle.com/technetwork/topics/security/bulletinjul2015-2511963.html###https://bugzilla.redhat.com/show_bug.cgi?id=1201798###http://packetstormsecurity.com/files/130730/tcpdump-Denial-Of-Service-Code-Execution.html', 'NVD-CWE-noinfo', '-1', '7.5', '', 'AV:N/AC:L/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2015-3138', '1', 'tcpdump', '', '', 'SEGV', null, null, '', 'print-wb.c in tcpdump before 4.7.4 allows remote attackers to cause a denial of service (segmentation fault and process crash).', 'https://bugzilla.redhat.com/show_bug.cgi?id=1212342###https://github.com/the-tcpdump-group/tcpdump/commit/3ed82f4ed0095768529afc22b923c8f7171fff70###https://github.com/the-tcpdump-group/tcpdump/issues/446', 'CWE-20', '7.5', '5', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:N/S:U/C:N/I:N/A:H', 'AV:N/AC:L/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2015-3395', '1', 'ffmpeg', '', '', 'buffer-overflow', 'msrle_decode_pal4', 'msrle_decode_pal4', '', 'The msrle_decode_pal4 function in msrledec.c in Libav before 10.7 and 11.x before 11.4 and FFmpeg before 2.0.7, 2.2.x before 2.2.15, 2.4.x before 2.4.8, 2.5.x before 2.5.6, and 2.6.x before 2.6.2 allows remote attackers to have unspecified impact via a crafted image, related to a pixel pointer, which triggers an out-of-bounds array access.', 'http://git.videolan.org/?p=ffmpeg.git;a=commitdiff;h=f7e1367f58263593e6cee3c282f7277d7ee9d553###https://git.libav.org/?p=libav.git;a=blob;f=Changelog;hb=refs/tags/v11.4###https://www.ffmpeg.org/security.html', 'CWE-119', '-1', '6.8', '', 'AV:N/AC:M/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2015-3414', '1', 'sqlite', '', '', 'uninitialized_memory_access', null, null, '', 'SQLite before 3.8.9 does not properly implement the dequoting of collation-sequence names, which allows context-dependent attackers to cause a denial of service (uninitialized memory access and application crash) or possibly have unspecified other impact via a crafted COLLATE clause, as demonstrated by COLLATE\"\"\"\"\"\"\"\" at the end of a SELECT statement.', 'http://www.oracle.com/technetwork/security-advisory/cpujul2018-4258247.html###http://www.oracle.com/technetwork/topics/security/bulletinapr2016-2952098.html###https://support.apple.com/HT205213###https://support.apple.com/HT205267###https://www.sqlite.org/src/info/eddc05e7bb31fae74daa86e0504a3478b99fa0f2', 'CWE-20', '-1', '7.5', '', 'AV:N/AC:L/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2015-3415', '1', 'sqlite', '', '', 'free_error', 'sqlite3vdbeexec', 'sqlite3vdbeexec', '', 'The sqlite3VdbeExec function in vdbe.c in SQLite before 3.8.9 does not properly implement comparison operators, which allows context-dependent attackers to cause a denial of service (invalid free operation) or possibly have unspecified other impact via a crafted CHECK clause, as demonstrated by CHECK(0&O>O) in a CREATE TABLE statement.', 'http://www.oracle.com/technetwork/security-advisory/cpujul2018-4258247.html###http://www.oracle.com/technetwork/topics/security/bulletinapr2016-2952098.html###https://support.apple.com/HT205213###https://support.apple.com/HT205267###https://www.sqlite.org/src/info/02e3c88fbf6abdcf3975fb0fb71972b0ab30da30', 'CWE-20', '-1', '7.5', '', 'AV:N/AC:L/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2015-3416', '1', 'sqlite', '', '', 'stack-buffer-overflow', 'sqlite3vxprintf', 'sqlite3vxprintf', '', 'The sqlite3VXPrintf function in printf.c in SQLite before 3.8.9 does not properly handle precision and width values during floating-point conversions, which allows context-dependent attackers to cause a denial of service (integer overflow and stack-based buffer overflow) or possibly have unspecified other impact via large integers in a crafted printf function call in a SELECT statement.', 'http://www.oracle.com/technetwork/security-advisory/cpujul2018-4258247.html###http://www.oracle.com/technetwork/topics/security/bulletinapr2016-2952098.html###http://www.sqlite.org/src/info/c494171f77dc2e5e04cb6d865e688448f04e5920###https://support.apple.com/HT205213###https://support.apple.com/HT205267', 'CWE-119', '-1', '7.5', '', 'AV:N/AC:L/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2015-3417', '1', 'ffmpeg', '', '', 'use-after-free', 'ff_h264_free_tables', 'ff_h264_free_tables', '', 'Use-after-free vulnerability in the ff_h264_free_tables function in libavcodec/h264.c in FFmpeg before 2.3.6 allows remote attackers to cause a denial of service or possibly have unspecified other impact via crafted H.264 data in an MP4 file, as demonstrated by an HTML VIDEO element that references H.264 data.', 'https://git.libav.org/?p=libav.git;a=blob;f=Changelog;hb=refs/tags/v11.4###https://github.com/FFmpeg/FFmpeg/commit/e8714f6f93d1a32f4e4655209960afcf4c185214', 'NVD-CWE-Other', '-1', '6.8', '', 'AV:N/AC:M/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2015-5203', '1', 'jasper', '', '1.900.17', 'double-free', 'jasper_image_stop_load', 'jasper_image_stop_load', '', 'Double free vulnerability in the jasper_image_stop_load function in JasPer 1.900.17 allows remote attackers to cause a denial of service (crash) via a crafted JPEG 2000 image file.', 'https://bugzilla.redhat.com/show_bug.cgi?id=1254242', 'CWE-415', '5.5', '4.3', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2015-5221', '1', 'jasper', '', '1.900.2', 'use-after-free', 'mif_process_cmpt', 'mif_process_cmpt', '', 'Use-after-free vulnerability in the mif_process_cmpt function in libjasper/mif/mif_cod.c in the JasPer JPEG-2000 library before 1.900.2 allows remote attackers to cause a denial of service (crash) via a crafted JPEG 2000 image file.', 'https://bugzilla.redhat.com/show_bug.cgi?id=1255710###https://github.com/mdadams/jasper/commit/df5d2867e8004e51e18b89865bc4aa69229227b3', 'CWE-416', '5.5', '4.3', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2015-6761', '1', 'ffmpeg', '', '', 'memory_corruption', 'update_dimensions', 'update_dimensions', '', 'The update_dimensions function in libavcodec/vp8.c in FFmpeg through 2.8.1, as used in Google Chrome before 46.0.2490.71 and other products, relies on a coefficient-partition count during multi-threaded operation, which allows remote attackers to cause a denial of service (race condition and memory corruption) or possibly have unspecified other impact via a crafted WebM file.', 'http://git.videolan.org/?p=ffmpeg.git;a=commit;h=dabea74d0e82ea80cd344f630497cafcb3ef872c###http://googlechromereleases.blogspot.com/2015/10/stable-channel-update.html###https://code.google.com/p/chromium/issues/detail?id=447860###https://code.google.com/p/chromium/issues/detail?id=532967###https://codereview.chromium.org/1376913003', 'CWE-362', '-1', '6.8', '', 'AV:N/AC:M/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2015-6818', '1', 'ffmpeg', '', '', 'buffer-overflow', 'decode_ihdr_chunk', 'decode_ihdr_chunk', '', 'The decode_ihdr_chunk function in libavcodec/pngdec.c in FFmpeg before 2.7.2 does not enforce uniqueness of the IHDR (aka image header) chunk in a PNG image, which allows remote attackers to cause a denial of service (out-of-bounds array access) or possibly have unspecified other impact via a crafted image with two or more of these chunks.', 'http://ffmpeg.org/security.html###http://git.videolan.org/?p=ffmpeg.git;a=commit;h=47f4e2d8960ca756ca153ab8e3e93d80449b8c91', 'CWE-17', '-1', '7.5', '', 'AV:N/AC:L/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2015-6819', '1', 'ffmpeg', '', '', 'buffer-overflow', 'ff_mjpeg_decode_frame', 'ff_mjpeg_decode_frame', '', 'Multiple integer underflows in the ff_mjpeg_decode_frame function in libavcodec/mjpegdec.c in FFmpeg before 2.7.2 allow remote attackers to cause a denial of service (out-of-bounds array access) or possibly have unspecified other impact via crafted MJPEG data.', 'http://ffmpeg.org/security.html###http://git.videolan.org/?p=ffmpeg.git;a=commit;h=84afc6b70d24fc0bf686e43138c96cf60a9445fe', 'CWE-189', '-1', '7.5', '', 'AV:N/AC:L/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2015-6820', '1', 'ffmpeg', '', '', 'buffer-overflow', 'ff_sbr_apply', 'ff_sbr_apply', '', 'The ff_sbr_apply function in libavcodec/aacsbr.c in FFmpeg before 2.7.2 does not check for a matching AAC frame syntax element before proceeding with Spectral Band Replication calculations, which allows remote attackers to cause a denial of service (out-of-bounds array access) or possibly have unspecified other impact via crafted AAC data.', 'http://ffmpeg.org/security.html###http://git.videolan.org/?p=ffmpeg.git;a=commit;h=79a98294da6cd85f8c86b34764c5e0c43b09eea3', 'CWE-119', '-1', '7.5', '', 'AV:N/AC:L/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2015-6821', '1', 'ffmpeg', '', '', 'SEGV', 'ff_mpv_common_init', 'ff_mpv_common_init', '', 'The ff_mpv_common_init function in libavcodec/mpegvideo.c in FFmpeg before 2.7.2 does not properly maintain the encoding context, which allows remote attackers to cause a denial of service (invalid pointer access) or possibly have unspecified other impact via crafted MPEG data.', 'http://ffmpeg.org/security.html###http://git.videolan.org/?p=ffmpeg.git;a=commit;h=b160fc290cf49b516c5b6ee0730fd9da7fc623b1', 'CWE-20', '-1', '7.5', '', 'AV:N/AC:L/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2015-6822', '1', 'ffmpeg', '', '', 'SEGV', 'destroy_buffers', 'destroy_buffers', '', 'The destroy_buffers function in libavcodec/sanm.c in FFmpeg before 2.7.2 does not properly maintain height and width values in the video context, which allows remote attackers to cause a denial of service (segmentation violation and application crash) or possibly have unspecified other impact via crafted LucasArts Smush video data.', 'http://ffmpeg.org/security.html###http://git.videolan.org/?p=ffmpeg.git;a=commit;h=39bbdebb1ed8eb9c9b0cd6db85afde6ba89d86e4', 'CWE-17', '-1', '7.5', '', 'AV:N/AC:L/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2015-6823', '1', 'ffmpeg', '', '', 'SEGV', 'allocate_buffers', 'allocate_buffers', '', 'The allocate_buffers function in libavcodec/alac.c in FFmpeg before 2.7.2 does not initialize certain context data, which allows remote attackers to cause a denial of service (segmentation violation) or possibly have unspecified other impact via crafted Apple Lossless Audio Codec (ALAC) data.', 'http://ffmpeg.org/security.html###http://git.videolan.org/?p=ffmpeg.git;a=commit;h=f7068bf277a37479aecde2832208d820682b35e6', 'CWE-17', '-1', '7.5', '', 'AV:N/AC:L/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2015-6824', '1', 'ffmpeg', '', '', 'SEGV', 'sws_init_context', 'sws_init_context', '', 'The sws_init_context function in libswscale/utils.c in FFmpeg before 2.7.2 does not initialize certain pixbuf data structures, which allows remote attackers to cause a denial of service (segmentation violation) or possibly have unspecified other impact via crafted video data.', 'http://ffmpeg.org/security.html###http://git.videolan.org/?p=ffmpeg.git;a=commit;h=a5d44d5c220e12ca0cb7a4eceb0f74759cb13111', 'CWE-20', '-1', '7.5', '', 'AV:N/AC:L/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2015-6825', '1', 'ffmpeg', '', '', 'SEGV', 'ff_frame_thread_init', 'ff_frame_thread_init', '', 'The ff_frame_thread_init function in libavcodec/pthread_frame.c in FFmpeg before 2.7.2 mishandles certain memory-allocation failures, which allows remote attackers to cause a denial of service (invalid pointer access) or possibly have unspecified other impact via a crafted file, as demonstrated by an AVI file.', 'http://ffmpeg.org/security.html###http://git.videolan.org/?p=ffmpeg.git;a=commit;h=f1a38264f20382731cf2cc75fdd98f4c9a84a626', 'CWE-20', '-1', '7.5', '', 'AV:N/AC:L/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2015-6826', '1', 'ffmpeg', '', '', 'SEGV', 'ff_rv34_decode_init_thread_copy', 'ff_rv34_decode_init_thread_copy', '', 'The ff_rv34_decode_init_thread_copy function in libavcodec/rv34.c in FFmpeg before 2.7.2 does not initialize certain structure members, which allows remote attackers to cause a denial of service (invalid pointer access) or possibly have unspecified other impact via crafted (1) RV30 or (2) RV40 RealVideo data.', 'http://ffmpeg.org/security.html###http://git.videolan.org/?p=ffmpeg.git;a=commit;h=3197c0aa87a3b7190e17d49e6fbc7b554e4b3f0a', 'CWE-20', '-1', '7.5', '', 'AV:N/AC:L/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2015-7313', '1', 'libtiff', '', null, 'excessive_memory_allocation', null, null, '', 'LibTIFF allows remote attackers to cause a denial of service (memory consumption and crash) via a crafted tiff file.', 'https://bugzilla.redhat.com/show_bug.cgi?id=1265998', 'CWE-399', '5.5', '4.3', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2015-7552', '1', 'gdk-pixbuf', '', '2.30.x', 'buffer-overflow', 'gdk_pixbuf_flip', 'gdk_pixbuf_flip', '', 'Heap-based buffer overflow in the gdk_pixbuf_flip function in gdk-pixbuf-scale.c in gdk-pixbuf 2.30.x allows remote attackers to cause a denial of service or possibly execute arbitrary code via a crafted BMP file.', 'https://bugzilla.suse.com/show_bug.cgi?id=958963', 'CWE-119', '7.8', '9.3', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:H/I:H/A:H', 'AV:N/AC:M/Au:N/C:C/I:C/A:C');
INSERT INTO `cve_general` VALUES ('CVE-2015-7554', '1', 'libtiff', '', '4.0.6', 'SEGV', '_tiffvgetfield', '_tiffvgetfield', '', 'The _TIFFVGetField function in tif_dir.c in libtiff 4.0.6 allows attackers to cause a denial of service (invalid memory write and crash) or possibly have unspecified other impact via crafted field data in an extension tag in a TIFF image.', 'http://www.oracle.com/technetwork/topics/security/linuxbulletinjul2016-3090544.html###http://www.oracle.com/technetwork/topics/security/ovmbulletinjul2016-3090546.html###http://packetstormsecurity.com/files/135078/libtiff-4.0.6-Invalid-Write.html', 'CWE-254', '9.8', '7.5', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:N/S:U/C:H/I:H/A:H', 'AV:N/AC:L/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2015-7673', '1', 'gdk-pixbuf', '', '2.32.0', 'buffer-overflow', null, null, '', 'io-tga.c in gdk-pixbuf before 2.32.0 uses heap memory after its allocation failed, which allows remote attackers to cause a denial of service (heap-based buffer overflow and application crash) and possibly execute arbitrary code via a crafted Truevision TGA (TARGA) file.', 'http://ftp.gnome.org/pub/gnome/sources/gdk-pixbuf/2.32/gdk-pixbuf-2.32.0.news###https://git.gnome.org/browse/gdk-pixbuf/commit/?id=19f9685dbff7d1f929c61cf99188df917a18811d###https://git.gnome.org/browse/gdk-pixbuf/commit/?id=6ddca835100107e6b5841ce9d56074f6d98c387e###https://git.gnome.org/browse/gdk-pixbuf/commit/?id=edf6fb8d856574bc3bb3a703037f56533229267c', 'CWE-119', '-1', '6.8', '', 'AV:N/AC:M/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2015-7674', '1', 'gdk-pixbuf', '', '2.32.1', 'integer-overflow', 'pixops_scale_nearest', 'pixops_scale_nearest', '', 'Integer overflow in the pixops_scale_nearest function in pixops/pixops.c in gdk-pixbuf before 2.32.1 allows remote attackers to cause a denial of service (application crash) and possibly execute arbitrary code via a crafted GIF image file, which triggers a heap-based buffer overflow.', 'http://ftp.gnome.org/pub/gnome/sources/gdk-pixbuf/2.32/gdk-pixbuf-2.32.1.news###https://git.gnome.org/browse/gdk-pixbuf/commit/?id=e9a5704edaa9aee9498f1fbf6e1b70fcce2e55aa', 'CWE-189', '-1', '6.8', '', 'AV:N/AC:M/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2015-8216', '1', 'ffmpeg', '', '', 'buffer-overflow', 'ljpeg_decode_yuv_scan', 'ljpeg_decode_yuv_scan', '', 'The ljpeg_decode_yuv_scan function in libavcodec/mjpegdec.c in FFmpeg before 2.8.2 omits certain width and height checks, which allows remote attackers to cause a denial of service (out-of-bounds array access) or possibly have unspecified other impact via crafted MJPEG data.', 'http://git.videolan.org/?p=ffmpeg.git;a=commit;h=d24888ef19ba38b787b11d1ee091a3d94920c76a', 'CWE-17', '-1', '7.5', '', 'AV:N/AC:L/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2015-8217', '1', 'ffmpeg', '', '', 'buffer-overflow', 'ff_hevc_parse_sps', 'ff_hevc_parse_sps', '', 'The ff_hevc_parse_sps function in libavcodec/hevc_ps.c in FFmpeg before 2.8.2 does not validate the Chroma Format Indicator, which allows remote attackers to cause a denial of service (out-of-bounds array access) or possibly have unspecified other impact via crafted High Efficiency Video Coding (HEVC) data.', 'http://git.videolan.org/?p=ffmpeg.git;a=commit;h=93f30f825c08477fe8f76be00539e96014cc83c8', 'CWE-20', '-1', '7.5', '', 'AV:N/AC:L/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2015-8218', '1', 'ffmpeg', '', '', 'buffer-overflow', 'decode_uncompressed', 'decode_uncompressed', '', 'The decode_uncompressed function in libavcodec/faxcompr.c in FFmpeg before 2.8.2 does not validate uncompressed runs, which allows remote attackers to cause a denial of service (out-of-bounds array access) or possibly have unspecified other impact via crafted CCITT FAX data.', 'http://git.videolan.org/?p=ffmpeg.git;a=commit;h=d4a731b84a08f0f3839eaaaf82e97d8d9c67da46', 'CWE-20', '-1', '6.8', '', 'AV:N/AC:M/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2015-8219', '1', 'ffmpeg', '', '', 'buffer-overflow', 'init_tile', 'init_tile', '', 'The init_tile function in libavcodec/jpeg2000dec.c in FFmpeg before 2.8.2 does not enforce minimum-value and maximum-value constraints on tile coordinates, which allows remote attackers to cause a denial of service (out-of-bounds array access) or possibly have unspecified other impact via crafted JPEG 2000 data.', 'http://git.videolan.org/?p=ffmpeg.git;a=commit;h=43492ff3ab68a343c1264801baa1d5a02de10167', 'CWE-20', '-1', '7.5', '', 'AV:N/AC:L/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2015-8363', '1', 'ffmpeg', '', '', 'buffer-overflow', 'jpeg2000_read_main_headers', 'jpeg2000_read_main_headers', '', 'The jpeg2000_read_main_headers function in libavcodec/jpeg2000dec.c in FFmpeg before 2.6.5, 2.7.x before 2.7.3, and 2.8.x through 2.8.2 does not enforce uniqueness of the SIZ marker in a JPEG 2000 image, which allows remote attackers to cause a denial of service (out-of-bounds heap-memory access) or possibly have unspecified other impact via a crafted image with two or more of these markers.', 'http://git.videolan.org/?p=ffmpeg.git;a=commit;h=44a7f17d0b20e6f8d836b2957e3e357b639f19a2', 'CWE-119', '-1', '6.8', '', 'AV:N/AC:M/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2015-8364', '1', 'ffmpeg', '', '', 'buffer-overflow', 'ff_ivi_init_planes', 'ff_ivi_init_planes', '', 'Integer overflow in the ff_ivi_init_planes function in libavcodec/ivi.c in FFmpeg before 2.6.5, 2.7.x before 2.7.3, and 2.8.x through 2.8.2 allows remote attackers to cause a denial of service (out-of-bounds heap-memory access) or possibly have unspecified other impact via crafted image dimensions in Indeo Video Interactive data.', 'http://git.videolan.org/?p=ffmpeg.git;a=commit;h=df91aa034b82b77a3c4e01791f4a2b2ff6c82066', 'CWE-189', '-1', '6.8', '', 'AV:N/AC:M/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2015-8365', '1', 'ffmpeg', '', '', 'buffer-overflow', 'smka_decode_frame', 'smka_decode_frame', '', 'The smka_decode_frame function in libavcodec/smacker.c in FFmpeg before 2.6.5, 2.7.x before 2.7.3, and 2.8.x through 2.8.2 does not verify that the data size is consistent with the number of channels, which allows remote attackers to cause a denial of service (out-of-bounds array access) or possibly have unspecified other impact via crafted Smacker data.', 'http://git.videolan.org/?p=ffmpeg.git;a=commit;h=4a9af07a49295e014b059c1ab624c40345af5892', 'CWE-119', '-1', '6.8', '', 'AV:N/AC:M/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2015-8480', '1', 'ffmpeg', '', '', 'buffer-overflow', 'videoframepool::poolimpl::createframe', 'createframe', '', 'The VideoFramePool::PoolImpl::CreateFrame function in media/base/video_frame_pool.cc in Google Chrome before 47.0.2526.73 does not initialize memory for a video-frame data structure, which might allow remote attackers to cause a denial of service (out-of-bounds memory access) or possibly have unspecified other impact by leveraging improper interaction with the vp3_h_loop_filter_c function in libavcodec/vp3dsp.c in FFmpeg.', 'http://googlechromereleases.blogspot.com/2015/12/stable-channel-update.html###https://code.google.com/p/chromium/issues/detail?id=514759', 'CWE-119', '-1', '10', '', 'AV:N/AC:L/Au:N/C:C/I:C/A:C');
INSERT INTO `cve_general` VALUES ('CVE-2015-8661', '1', 'ffmpeg', '', '', 'buffer-overflow', 'h264_slice_header_init', 'h264_slice_header_init', '', 'The h264_slice_header_init function in libavcodec/h264_slice.c in FFmpeg before 2.8.3 does not validate the relationship between the number of threads and the number of slices, which allows remote attackers to cause a denial of service (out-of-bounds array access) or possibly have unspecified other impact via crafted H.264 data.', 'http://git.videolan.org/?p=ffmpeg.git;a=commit;h=4ea4d2f438c9a7eba37980c9a87be4b34943e4d5', 'CWE-119', '8.3', '7.5', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:N/S:C/C:L/I:L/A:L', 'AV:N/AC:L/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2015-8662', '1', 'ffmpeg', '', '', 'buffer-overflow', 'ff_dwt_decode', 'ff_dwt_decode', '', 'The ff_dwt_decode function in libavcodec/jpeg2000dwt.c in FFmpeg before 2.8.4 does not validate the number of decomposition levels before proceeding with Discrete Wavelet Transform decoding, which allows remote attackers to cause a denial of service (out-of-bounds array access) or possibly have unspecified other impact via crafted JPEG 2000 data.', 'http://git.videolan.org/?p=ffmpeg.git;a=commit;h=75422280fbcdfbe9dc56bde5525b4d8b280f1bc5', 'CWE-119', '7.3', '7.5', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:N/S:U/C:L/I:L/A:L', 'AV:N/AC:L/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2015-8663', '1', 'ffmpeg', '', '', 'buffer-overflow', 'ff_get_buffer', 'ff_get_buffer', '', 'The ff_get_buffer function in libavcodec/utils.c in FFmpeg before 2.8.4 preserves width and height values after a failure, which allows remote attackers to cause a denial of service (out-of-bounds array access) or possibly have unspecified other impact via a crafted .mov file.', 'http://git.videolan.org/?p=ffmpeg.git;a=commit;h=abee0a1c60612e8638640a8a3738fffb65e16dbf', 'CWE-119', '8.3', '7.5', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:N/S:C/C:L/I:L/A:L', 'AV:N/AC:L/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2015-8665', '1', 'libtiff', '', '4.0.6', 'buffer-overflow', null, null, '', 'tif_getimage.c in LibTIFF 4.0.6 allows remote attackers to cause a denial of service (out-of-bounds read) via the SamplesPerPixel tag in a TIFF image.', 'http://www.oracle.com/technetwork/topics/security/bulletinapr2016-2952098.html###http://www.oracle.com/technetwork/topics/security/linuxbulletinjul2016-3090544.html###http://www.oracle.com/technetwork/topics/security/ovmbulletinjul2016-3090546.html', 'CWE-119', '5.5', '4.3', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2015-8668', '1', 'libtiff', '', '4.0.6', 'buffer-overflow', 'packbitspreencode', 'packbitspreencode', '', 'Heap-based buffer overflow in the PackBitsPreEncode function in tif_packbits.c in bmp2tiff in libtiff 4.0.6 and earlier allows remote attackers to execute arbitrary code or cause a denial of service via a large width field in a BMP image.', 'http://www.oracle.com/technetwork/topics/security/linuxbulletinjul2016-3090544.html###http://www.oracle.com/technetwork/topics/security/ovmbulletinjul2016-3090546.html###http://packetstormsecurity.com/files/135080/libtiff-4.0.6-Heap-Overflow.html', 'CWE-119', '9.8', '7.5', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:N/S:U/C:H/I:H/A:H', 'AV:N/AC:L/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2015-8683', '1', 'libtiff', '', '4.0.6', 'buffer-overflow', 'putcontig8bitcielab', 'putcontig8bitcielab', '', 'The putcontig8bitCIELab function in tif_getimage.c in LibTIFF 4.0.6 allows remote attackers to cause a denial of service (out-of-bounds read) via a packed TIFF image.', 'http://www.oracle.com/technetwork/topics/security/bulletinapr2016-2952098.html###http://www.oracle.com/technetwork/topics/security/linuxbulletinjul2016-3090544.html###http://www.oracle.com/technetwork/topics/security/ovmbulletinjul2016-3090546.html', 'CWE-119', '5.5', '4.3', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2015-8781', '1', 'libtiff', '', null, 'buffer-overflow', null, null, '', 'tif_luv.c in libtiff allows attackers to cause a denial of service (out-of-bounds write) via an invalid number of samples per pixel in a LogL compressed TIFF image, a different vulnerability than CVE-2015-8782.', 'http://bugzilla.maptools.org/show_bug.cgi?id=2522#c0###http://www.oracle.com/technetwork/topics/security/bulletinapr2016-2952098.html###http://www.oracle.com/technetwork/topics/security/linuxbulletinjul2016-3090544.html###http://www.oracle.com/technetwork/topics/security/ovmbulletinjul2016-3090546.html', 'CWE-787', '6.5', '4.3', 'CVSS:3.1/AV:N/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2015-8782', '1', 'libtiff', '', null, 'buffer-overflow', null, null, '', 'tif_luv.c in libtiff allows attackers to cause a denial of service (out-of-bounds writes) via a crafted TIFF image, a different vulnerability than CVE-2015-8781.', 'http://bugzilla.maptools.org/show_bug.cgi?id=2522###http://www.oracle.com/technetwork/topics/security/bulletinapr2016-2952098.html###http://www.oracle.com/technetwork/topics/security/linuxbulletinjul2016-3090544.html###http://www.oracle.com/technetwork/topics/security/ovmbulletinjul2016-3090546.html', 'CWE-787', '6.5', '4.3', 'CVSS:3.1/AV:N/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2015-8783', '1', 'libtiff', '', null, 'buffer-overflow', null, null, '', 'tif_luv.c in libtiff allows attackers to cause a denial of service (out-of-bounds reads) via a crafted TIFF image.', 'http://bugzilla.maptools.org/show_bug.cgi?id=2522###http://www.oracle.com/technetwork/topics/security/bulletinapr2016-2952098.html###http://www.oracle.com/technetwork/topics/security/linuxbulletinjul2016-3090544.html###http://www.oracle.com/technetwork/topics/security/ovmbulletinjul2016-3090546.html', 'CWE-125', '6.5', '4.3', 'CVSS:3.1/AV:N/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
