INSERT INTO `cve_general` VALUES ('CVE-2015-8863', '1', 'jq', '', '', 'buffer-overflow', 'tokenadd', 'tokenadd', '', 'Off-by-one error in the tokenadd function in jv_parse.c in jq allows remote attackers to cause a denial of service (crash) via a long JSON-encoded number, which triggers a heap-based buffer overflow.', 'https://bugs.debian.org/cgi-bin/bugreport.cgi?bug=802231###https://github.com/stedolan/jq/commit/8eb1367ca44e772963e704a700ef72ae2e12babd###https://github.com/stedolan/jq/issues/995', 'CWE-119', '9.8', '10', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:N/S:U/C:H/I:H/A:H', 'AV:N/AC:L/Au:N/C:C/I:C/A:C');
