INSERT INTO `cve_general` VALUES ('CVE-2016-10132', '1', 'mujs', '', '', 'null_pointer_dereference', null, null, '', 'regexp.c in Artifex Software, Inc. MuJS allows attackers to cause a denial of service (NULL pointer dereference and crash) via vectors related to regular expression compilation.', 'http://git.ghostscript.com/?p=mujs.git;h=fd003eceda531e13fbdd1aeb6e9c73156496e569###https://bugs.ghostscript.com/show_bug.cgi?id=697381', 'CWE-476', '7.5', '5', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:N/S:U/C:N/I:N/A:H', 'AV:N/AC:L/Au:N/C:N/I:N/A:P');
