INSERT INTO `cve_general` VALUES ('CVE-2016-1897', '1', 'ffmpeg', '', '', '', '', '', '', 'FFmpeg 2.x allows remote attackers to conduct cross-origin attacks and read arbitrary files by using the concat protocol in an HTTP Live Streaming (HLS) M3U8 file, leading to an external HTTP request in which the URL string contains the first line of a local file.', '', 'CWE-200', '5.5', '4.3', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:H/I:N/A:N', 'AV:N/AC:M/Au:N/C:P/I:N/A:N');
