INSERT INTO `cve_general` VALUES ('CVE-2017-11108', '1', 'tcpdump', '', '', 'buffer-overflow', 'extract_16bits', 'extract_16bits', '', 'tcpdump 4.9.0 allows remote attackers to cause a denial of service (heap-based buffer over-read and application crash) via crafted packet data. The crash occurs in the EXTRACT_16BITS function, called from the stp_print function for the Spanning Tree Protocol.', 'https://support.apple.com/HT208221###https://bugzilla.redhat.com/show_bug.cgi?id=1468504', 'CWE-125', '7.5', '5', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:N/S:U/C:N/I:N/A:H', 'AV:N/AC:L/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-11112', '1', 'ncurses', '', '', null, 'append_acs', 'append_acs', '', 'In ncurses 6.0, there is an attempted 0xffffffffffffffff access in the append_acs function of tinfo/parse_entry.c. It could lead to a remote denial of service attack if the terminfo library code is used to process untrusted terminfo data.', 'https://bugzilla.redhat.com/show_bug.cgi?id=1464686', 'CWE-20', '7.5', '5', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:N/S:U/C:N/I:N/A:H', 'AV:N/AC:L/Au:N/C:N/I:N/A:P');
