INSERT INTO `cve_general` VALUES ('CVE-2017-12447', '1', 'gdk-pixbuf', '', '2.32.2', 'memory_corruption', null, null, '', 'GdkPixBuf (aka gdk-pixbuf), possibly 2.32.2, as used by GNOME Nautilus 3.14.3 on Ubuntu 16.04, allows attackers to cause a denial of service (stack corruption) or possibly have unspecified other impact via a crafted file folder.', 'https://bugzilla.gnome.org/show_bug.cgi?id=785979###https://github.com/hackerlib/hackerlib-vul/tree/master/gnome', 'CWE-119', '7.8', '6.8', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:H/I:H/A:H', 'AV:N/AC:M/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-12448', '1', 'binutils', '', '', 'use-after-free', 'bfd_cache_close', 'bfd_cache_close', '', 'The bfd_cache_close function in bfd/cache.c in the Binary File Descriptor (BFD) library (aka libbfd), as distributed in GNU Binutils 2.29 and earlier, allows remote attackers to cause a heap use after free and possibly achieve code execution via a crafted nested archive file. This issue occurs because incorrect functions are called during an attempt to release memory. The issue can be addressed by better input validation in the bfd_generic_archive_p function in bfd/archive.c.', 'https://sourceware.org/bugzilla/show_bug.cgi?id=21787', 'CWE-416', '7.8', '6.8', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:H/I:H/A:H', 'AV:N/AC:M/Au:N/C:P/I:P/A:P');
