INSERT INTO `cve_general` VALUES ('CVE-2017-12450', '1', 'binutils', '', '', 'buffer-overflow', 'alpha_vms_object_p', 'alpha_vms_object_p', '', 'The alpha_vms_object_p function in bfd/vms-alpha.c in the Binary File Descriptor (BFD) library (aka libbfd), as distributed in GNU Binutils 2.29 and earlier, allows remote attackers to cause an out of bounds heap write and possibly achieve code execution via a crafted vms alpha file.', 'https://sourceware.org/bugzilla/show_bug.cgi?id=21813', 'CWE-787', '7.8', '6.8', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:H/I:H/A:H', 'AV:N/AC:M/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-12451', '1', 'binutils', '', '', 'buffer-overflow', '_bfd_xcoff_read_ar_hdr', '_bfd_xcoff_read_ar_hdr', '', 'The _bfd_xcoff_read_ar_hdr function in bfd/coff-rs6000.c and bfd/coff64-rs6000.c in the Binary File Descriptor (BFD) library (aka libbfd), as distributed in GNU Binutils 2.29 and earlier, allows remote attackers to cause an out of bounds stack read via a crafted COFF image file.', 'https://sourceware.org/bugzilla/show_bug.cgi?id=21786', 'CWE-125', '7.8', '6.8', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:H/I:H/A:H', 'AV:N/AC:M/Au:N/C:P/I:P/A:P');
