INSERT INTO `cve_general` VALUES ('CVE-2017-14333', '1', 'binutils', '', '', 'integer-overflow', 'process_version_sections', 'process_version_sections', '', 'The process_version_sections function in readelf.c in GNU Binutils 2.29 allows attackers to cause a denial of service (Integer Overflow, and hang because of a time-consuming loop) or possibly have unspecified other impact via a crafted binary file with invalid values of ent.vn_next, during \"readelf -a\" execution.', 'https://sourceware.org/bugzilla/show_bug.cgi?id=21990', 'CWE-190', '7.8', '4.3', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:H/I:H/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-14406', '1', 'mp3gain', '', '1.5.2', 'null_pointer_dereference', 'sync_buffer', 'sync_buffer', '', 'A NULL pointer dereference was discovered in sync_buffer in interface.c in mpglibDBL, as used in MP3Gain version 1.5.2. The vulnerability causes a segmentation fault and application crash, which leads to remote denial of service.', 'https://blogs.gentoo.org/ago/2017/09/08/mp3gain-null-pointer-dereference-in-sync_buffer-mpglibdblinterface-c/', 'CWE-476', '5.5', '4.3', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-14407', '1', 'mp3gain', '', '1.5.2', 'buffer-overflow', null, null, '', 'A stack-based buffer over-read was discovered in filterYule in gain_analysis.c in MP3Gain version 1.5.2. The vulnerability causes an application crash, which leads to remote denial of service.', 'https://blogs.gentoo.org/ago/2017/09/08/mp3gain-stack-based-buffer-overflow-in-filteryule-gain_analysis-c/', 'CWE-125', '5.5', '4.3', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-14408', '1', 'mp3gain', '', '1.5.2', 'buffer-overflow', null, null, '', 'A stack-based buffer over-read was discovered in dct36 in layer3.c in mpglibDBL, as used in MP3Gain version 1.5.2. The vulnerability causes an application crash, which leads to remote denial of service.', 'https://blogs.gentoo.org/ago/2017/09/08/mp3gain-stack-based-buffer-overflow-in-dct36-mpglibdbllayer3-c/', 'CWE-125', '5.5', '4.3', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-14409', '1', 'mp3gain', '', '1.5.2', 'buffer-overflow', 'iii_dequantize_sample', 'iii_dequantize_sample', '', 'A buffer overflow was discovered in III_dequantize_sample in layer3.c in mpglibDBL, as used in MP3Gain version 1.5.2. The vulnerability causes an out-of-bounds write, which leads to remote denial of service or possibly code execution.', 'https://blogs.gentoo.org/ago/2017/09/08/mp3gain-global-buffer-overflow-in-iii_dequantize_sample-mpglibdbllayer3-c/', 'CWE-787', '7.8', '6.8', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:H/I:H/A:H', 'AV:N/AC:M/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-14410', '1', 'mp3gain', '', '1.5.2', 'buffer-overflow', 'iii_i_stereo', 'iii_i_stereo', '', 'A buffer over-read was discovered in III_i_stereo in layer3.c in mpglibDBL, as used in MP3Gain version 1.5.2. The vulnerability causes an application crash, which leads to remote denial of service.', 'https://blogs.gentoo.org/ago/2017/09/08/mp3gain-global-buffer-overflow-in-iii_i_stereo-mpglibdbllayer3-c/', 'CWE-125', '5.5', '4.3', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-14411', '1', 'mp3gain', '', '1.5.2', 'buffer-overflow', 'copy_mp', 'copy_mp', '', 'A stack-based buffer overflow was discovered in copy_mp in interface.c in mpglibDBL, as used in MP3Gain version 1.5.2. The vulnerability causes an out-of-bounds write, which leads to remote denial of service or possibly code execution.', 'https://blogs.gentoo.org/ago/2017/09/08/mp3gain-stack-based-buffer-overflow-in-copy_mp-mpglibdblinterface-c/', 'CWE-787', '7.8', '6.8', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:H/I:H/A:H', 'AV:N/AC:M/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-14412', '1', 'mp3gain', '', '1.5.2', 'SEGV', 'copy_mp', 'copy_mp', '', 'An invalid memory write was discovered in copy_mp in interface.c in mpglibDBL, as used in MP3Gain version 1.5.2. The vulnerability causes a denial of service (segmentation fault and application crash) or possibly unspecified other impact.', 'https://blogs.gentoo.org/ago/2017/09/08/mp3gain-invalid-memory-write-in-copy_mp-mpglibdblinterface-c/', 'CWE-787', '7.8', '6.8', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:H/I:H/A:H', 'AV:N/AC:M/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-14528', '1', 'libtiff', '', '7.0.6', 'use-after-free', 'tiffsetprofiles', 'tiffsetprofiles', '', 'The TIFFSetProfiles function in coders/tiff.c in ImageMagick 7.0.6 has incorrect expectations about whether LibTIFF TIFFGetField return values imply that data validation has occurred, which allows remote attackers to cause a denial of service (use-after-free after an invalid call to TIFFSetField, and application crash) via a crafted file.', 'http://bugzilla.maptools.org/show_bug.cgi?id=2730###https://www.imagemagick.org/discourse-server/viewtopic.php?f=3&t=32560', 'CWE-416', '6.5', '4.3', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-14529', '1', 'binutils', '', '', 'buffer-overflow', 'pe_print_idata', 'pe_print_idata', '', 'The pe_print_idata function in peXXigen.c in the Binary File Descriptor (BFD) library (aka libbfd), as distributed in GNU Binutils 2.29, mishandles HintName vector entries, which allows remote attackers to cause a denial of service (heap-based buffer over-read and application crash) via a crafted PE file, related to the bfd_getl16 function.', 'https://sourceware.org/bugzilla/show_bug.cgi?id=22113###https://sourceware.org/git/gitweb.cgi?p=binutils-gdb.git;h=4d465c689a8fb27212ef358d0aee89d60dee69a6###https://sourceware.org/git/gitweb.cgi?p=binutils-gdb.git;h=dcaaca89e8618eba35193c27afcb1cfa54f74582', 'CWE-125', '5.5', '4.3', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-14638', '1', 'Bento4', '', '', 'null_pointer_dereference', 'ap4_atomfactory::createatomfromstream', 'createatomfromstream', '', 'AP4_AtomFactory::CreateAtomFromStream in Core/Ap4AtomFactory.cpp in Bento4 version 1.5.0-617 has missing NULL checks, leading to a NULL pointer dereference, segmentation fault, and application crash in AP4_Atom::SetType in Core/Ap4Atom.h.', 'https://blogs.gentoo.org/ago/2017/09/14/bento4-null-pointer-dereference-in-ap4_atomsettype-ap4atom-h/###https://github.com/axiomatic-systems/Bento4/commit/be7185faf7f52674028977dcf501c6039ff03aa5###https://github.com/axiomatic-systems/Bento4/issues/182', 'CWE-476', '6.5', '4.3', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
