INSERT INTO `cve_general` VALUES ('CVE-2017-14729', '1', 'binutils', '', '', 'buffer-overflow', null, null, '', 'The *_get_synthetic_symtab functions in the Binary File Descriptor (BFD) library (aka libbfd), as distributed in GNU Binutils 2.29, do not ensure a unique PLT entry for a symbol, which allows remote attackers to cause a denial of service (heap-based buffer overflow and application crash) or possibly have unspecified other impact via a crafted ELF file, related to elf32-i386.c and elf64-x86-64.c.', 'https://blogs.gentoo.org/ago/2017/09/25/binutils-heap-based-buffer-overflow-in-_bfd_x86_elf_get_synthetic_symtab-elfxx-x86-c/###https://sourceware.org/bugzilla/show_bug.cgi?id=22170###https://sourceware.org/git/gitweb.cgi?p=binutils-gdb.git;h=56933f9e3e90eebf1018ed7417d6c1184b91db6b###https://sourceware.org/git/gitweb.cgi?p=binutils-gdb.git;h=61e3bf5f83f7e505b6bc51ef65426e5b31e6e360', 'CWE-119', '7.8', '6.8', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:H/I:H/A:H', 'AV:N/AC:M/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-14745', '1', 'binutils', '', '', 'integer-overflow', null, null, '', 'The *_get_synthetic_symtab functions in the Binary File Descriptor (BFD) library (aka libbfd), as distributed in GNU Binutils 2.29, interpret a -1 value as a sorting count instead of an error flag, which allows remote attackers to cause a denial of service (integer overflow and application crash) or possibly have unspecified other impact via a crafted ELF file, related to elf32-i386.c and elf64-x86-64.c.', 'https://sourceware.org/bugzilla/show_bug.cgi?id=22148', 'CWE-190', '7.8', '6.8', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:H/I:H/A:H', 'AV:N/AC:M/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-14767', '1', 'ffmpeg', '', '', 'buffer-overflow', 'sdp_parse_fmtp_config_h264', 'sdp_parse_fmtp_config_h264', '', 'The sdp_parse_fmtp_config_h264 function in libavformat/rtpdec_h264.c in FFmpeg before 3.3.4 mishandles empty sprop-parameter-sets values, which allows remote attackers to cause a denial of service (heap buffer overflow) or possibly have unspecified other impact via a crafted sdp file.', 'https://github.com/FFmpeg/FFmpeg/commit/c42a1388a6d1bfd8001bf6a4241d8ca27e49326d', 'CWE-119', '8.8', '6.8', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:R/S:U/C:H/I:H/A:H', 'AV:N/AC:M/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-14795', '1', 'ffmpeg', '', '', 'buffer-overflow', 'hevc_write_frame', 'hevc_write_frame', '', 'The hevc_write_frame function in libbpg.c in libbpg 0.9.7 allows remote attackers to cause a denial of service (out-of-bounds read and application crash) or possibly have unspecified other impact via a crafted BPG file, related to improper interaction with hls_pcm_sample in hevc.c in libavcodec in FFmpeg and put_pcm_var in hevcdsp_template.c in libavcodec in FFmpeg.', 'https://github.com/leonzhao7/vulnerability/blob/master/An%20Out-of-Bounds%20Read%20%28DoS%29%20Vulnerability%20in%20hevc.c%20of%20libbpg.md', 'CWE-125', '8.8', '6.8', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:R/S:U/C:H/I:H/A:H', 'AV:N/AC:M/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-14796', '1', 'ffmpeg', '', '', 'integer-underflow', 'hevc_write_frame', 'hevc_write_frame', '', 'The hevc_write_frame function in libbpg.c in libbpg 0.9.7 allows remote attackers to cause a denial of service (integer underflow and application crash) or possibly have unspecified other impact via a crafted BPG file, related to improper interaction with copy_CTB_to_hv in hevc_filter.c in libavcodec in FFmpeg and sao_filter_CTB in hevc_filter.c in libavcodec in FFmpeg.', 'https://github.com/leonzhao7/vulnerability/blob/master/An%20integer%20underflow%20vulnerability%20in%20sao_filter_CTB%20of%20libbpg.md', 'CWE-191', '8.8', '6.8', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:R/S:U/C:H/I:H/A:H', 'AV:N/AC:M/Au:N/C:P/I:P/A:P');
