INSERT INTO `cve_general` VALUES ('CVE-2017-15045', '1', 'lame', '', '3.99.5', 'buffer-overflow', 'fill_buffer', 'fill_buffer', '', 'LAME 3.99.5 has a heap-based buffer over-read in fill_buffer in libmp3lame/util.c, related to lame_encode_buffer_sample_t in libmp3lame/lame.c, a different vulnerability than CVE-2017-9410.', 'https://sourceforge.net/p/lame/bugs/478/', 'CWE-125', '5.5', '4.3', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-15046', '1', 'lame', '', '3.99.5', 'buffer-overflow', 'unpack_read_samples', 'unpack_read_samples', '', 'LAME 3.99.5 has a stack-based buffer overflow in unpack_read_samples in frontend/get_audio.c, a different vulnerability than CVE-2017-9412.', 'https://sourceforge.net/p/lame/bugs/479/', 'CWE-119', '5.5', '4.3', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-15186', '1', 'ffmpeg', '', '', 'double-free', null, null, '', 'Double free vulnerability in FFmpeg 3.3.4 and earlier allows remote attackers to cause a denial of service via a crafted AVI file.', '', 'CWE-415', '6.5', '4.3', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-15225', '1', 'binutils', '', '', 'memory_leak', null, null, '', '_bfd_dwarf2_cleanup_debug_info in dwarf2.c in the Binary File Descriptor (BFD) library (aka libbfd), as distributed in GNU Binutils 2.29, allows remote attackers to cause a denial of service (memory leak) via a crafted ELF file.', 'https://sourceware.org/bugzilla/show_bug.cgi?id=22212###https://sourceware.org/git/gitweb.cgi?p=binutils-gdb.git;h=b55ec8b676ed05d93ee49d6c79ae0403616c4fb0', 'CWE-772', '5.5', '4.3', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-15286', '1', 'sqlite', '', '', 'null_pointer_dereference', null, null, '', 'SQLite 3.20.1 has a NULL pointer dereference in tableColumnList in shell.c because it fails to consider certain cases where `sqlite3_step(pStmt)==SQLITE_ROW` is false and a data structure is never initialized.', 'https://github.com/Ha0Team/crash-of-sqlite3/blob/master/poc.md', 'CWE-476', '7.5', '5', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:N/S:U/C:N/I:N/A:H', 'AV:N/AC:L/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-15672', '1', 'ffmpeg', '', '', 'buffer-overflow', 'read_header', 'read_header', '', 'The read_header function in libavcodec/ffv1dec.c in FFmpeg 3.3.4 and earlier allows remote attackers to have unspecified impact via a crafted MP4 file, which triggers an out-of-bounds read.', 'http://git.videolan.org/?p=ffmpeg.git;a=commitdiff;h=c20f4fcb74da2d0432c7b54499bb98f48236b904', 'CWE-125', '8.8', '6.8', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:R/S:U/C:H/I:H/A:H', 'AV:N/AC:M/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-15938', '1', 'binutils', '', '', 'SEGV', null, null, '', 'dwarf2.c in the Binary File Descriptor (BFD) library (aka libbfd), as distributed in GNU Binutils 2.29, miscalculates DW_FORM_ref_addr die refs in the case of a relocatable object file, which allows remote attackers to cause a denial of service (find_abstract_instance_name invalid memory read, segmentation fault, and application crash).', 'https://blogs.gentoo.org/ago/2017/10/24/binutils-invalid-memory-read-in-find_abstract_instance_name-dwarf2-c/###https://sourceware.org/bugzilla/show_bug.cgi?id=22209###https://sourceware.org/git/gitweb.cgi?p=binutils-gdb.git;h=1b86808a86077722ee4f42ff97f836b12420bb2a', 'CWE-119', '7.5', '5', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:N/S:U/C:N/I:N/A:H', 'AV:N/AC:L/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-15939', '1', 'binutils', '', '', 'null_pointer_dereference', null, null, '', 'dwarf2.c in the Binary File Descriptor (BFD) library (aka libbfd), as distributed in GNU Binutils 2.29, mishandles NULL files in a .debug_line file table, which allows remote attackers to cause a denial of service (NULL pointer dereference and application crash) via a crafted ELF file, related to concat_filename. NOTE: this issue is caused by an incomplete fix for CVE-2017-15023.', 'https://blogs.gentoo.org/ago/2017/10/24/binutils-null-pointer-dereference-in-concat_filename-dwarf2-c-incomplete-fix-for-cve-2017-15023/###https://sourceware.org/bugzilla/show_bug.cgi?id=22205###https://sourceware.org/git/gitweb.cgi?p=binutils-gdb.git;h=a54018b72d75abf2e74bf36016702da06399c1d9', 'CWE-476', '5.5', '4.3', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-15996', '1', 'binutils', '', '', 'excessive_memory_allocation', null, null, '', 'elfcomm.c in readelf in GNU Binutils 2.29 allows remote attackers to cause a denial of service (excessive memory allocation) or possibly have unspecified other impact via a crafted ELF file that triggers a \"buffer overflow on fuzzed archive header,\" related to an uninitialized variable, an improper conditional jump, and the get_archive_member_name, process_archive_index_and_symbols, and setup_archive functions.', 'https://sourceware.org/bugzilla/show_bug.cgi?id=22361###https://sourceware.org/git/gitweb.cgi?p=binutils-gdb.git;h=d91f0b20e561e326ee91a09a76206257bde8438b', 'CWE-119', '7.8', '6.8', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:H/I:H/A:H', 'AV:N/AC:M/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-16232', '1', 'libtiff', '', '4.0.8', 'memory_leak', null, null, '', '** DISPUTED ** LibTIFF 4.0.8 has multiple memory leak vulnerabilities, which allow attackers to cause a denial of service (memory consumption), as demonstrated by tif_open.c, tif_lzw.c, and tif_aux.c. NOTE: Third parties were unable to reproduce the issue.', 'http://packetstormsecurity.com/files/150896/LibTIFF-4.0.8-Memory-Leak.html###http://seclists.org/fulldisclosure/2018/Dec/32###http://seclists.org/fulldisclosure/2018/Dec/47###http://www.openwall.com/lists/oss-security/2017/11/01/11###http://www.openwall.com/lists/oss-security/2017/11/01/3###http://www.openwall.com/lists/oss-security/2017/11/01/7###http://www.openwall.com/lists/oss-security/2017/11/01/8', 'CWE-772', '7.5', '5', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:N/S:U/C:N/I:N/A:H', 'AV:N/AC:L/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-16711', '1', 'swftools', '', '0.9.2', 'null_pointer_dereference', 'swf_definelosslessbitstagtoimage', 'swf_definelosslessbitstagtoimage', '', 'The swf_DefineLosslessBitsTagToImage function in lib/modules/swfbits.c in SWFTools 0.9.2 mishandles an uncompress failure, which allows remote attackers to cause a denial of service (NULL pointer dereference and application crash) because of extractDefinitions in lib/readers/swf.c and fill_line_bitmap in lib/devices/render.c, as demonstrated by swfrender.', 'https://github.com/matthiaskramm/swftools/issues/46', 'CWE-476', '5.5', '4.3', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-16793', '1', 'swftools', '', '0.9.2', 'buffer-overflow', 'wav_convert2mono', 'wav_convert2mono', '', 'The wav_convert2mono function in lib/wav.c in SWFTools 0.9.2 does not properly validate WAV data, which allows remote attackers to cause a denial of service (incorrect malloc and heap-based buffer overflow) or possibly have unspecified other impact via a crafted file.', 'https://github.com/matthiaskramm/swftools/issues/47', 'CWE-119', '7.8', '6.8', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:H/I:H/A:H', 'AV:N/AC:M/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-16794', '1', 'swftools', '', '', 'buffer-overflow', 'png_load', 'png_load', '', 'The png_load function in lib/png.c in SWFTools 0.9.2 does not properly validate a multiplication of width and bits-per-pixel values, which allows remote attackers to cause a denial of service (heap-based buffer over-read and application crash) via a crafted file, as demonstrated by an erroneous png_load call that occurs because of incorrect integer data types in png2swf.', 'https://github.com/matthiaskramm/swftools/issues/50', 'CWE-125', '5.5', '4.3', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-16796', '1', 'swftools', '', '', null, 'png_load', 'png_load', '', 'In SWFTools 0.9.2, the png_load function in lib/png.c does not check the return value of a realloc call, which allows remote attackers to cause a denial of service (invalid write and application crash) or possibly have unspecified other impact via vectors involving an IDAT tag in a crafted PNG file.', 'https://github.com/matthiaskramm/swftools/issues/51', 'CWE-119', '7.8', '6.8', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:H/I:H/A:H', 'AV:N/AC:M/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-16797', '1', 'swftools', '', '', 'buffer-overflow', 'png_load', 'png_load', '', 'In SWFTools 0.9.2, the png_load function in lib/png.c does not properly validate an alloclen_64 multiplication of width and height values, which allows remote attackers to cause a denial of service (integer overflow, heap-based buffer overflow, and application crash) or possibly have unspecified other impact via a crafted PNG file.', 'https://github.com/matthiaskramm/swftools/issues/51', 'CWE-190', '7.8', '6.8', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:H/I:H/A:H', 'AV:N/AC:M/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-16808', '1', 'tcpdump', '', '', 'buffer-overflow', null, null, '', 'tcpdump before 4.9.3 has a heap-based buffer over-read related to aoe_print in print-aoe.c and lookup_emem in addrtoname.c.', 'https://github.com/the-tcpdump-group/tcpdump/issues/645###https://support.apple.com/kb/HT210788###http://packetstormsecurity.com/files/154710/Slackware-Security-Advisory-tcpdump-Updates.html###https://github.com/the-tcpdump-group/tcpdump/blob/tcpdump-4.9/CHANGES', 'CWE-125', '5.5', '4.3', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-16826', '1', 'binutils', '', '', 'SEGV', 'coff_slurp_line_table', 'coff_slurp_line_table', '', 'The coff_slurp_line_table function in coffcode.h in the Binary File Descriptor (BFD) library (aka libbfd), as distributed in GNU Binutils 2.29.1, allows remote attackers to cause a denial of service (invalid memory access and application crash) or possibly have unspecified other impact via a crafted PE file.', 'https://sourceware.org/bugzilla/show_bug.cgi?id=22376###https://sourceware.org/git/gitweb.cgi?p=binutils-gdb.git;h=a67d66eb97e7613a38ffe6622d837303b3ecd31d', 'CWE-119', '7.8', '6.8', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:H/I:H/A:H', 'AV:N/AC:M/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-16827', '1', 'binutils', '', '', 'free_error', 'aout_get_external_symbols', 'aout_get_external_symbols', '', 'The aout_get_external_symbols function in aoutx.h in the Binary File Descriptor (BFD) library (aka libbfd), as distributed in GNU Binutils 2.29.1, allows remote attackers to cause a denial of service (slurp_symtab invalid free and application crash) or possibly have unspecified other impact via a crafted ELF file.', 'https://sourceware.org/bugzilla/show_bug.cgi?id=22306###https://sourceware.org/git/gitweb.cgi?p=binutils-gdb.git;h=0301ce1486b1450f219202677f30d0fa97335419', 'CWE-119', '7.8', '6.8', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:H/I:H/A:H', 'AV:N/AC:M/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-16828', '1', 'binutils', '', '', 'buffer-overflow', 'display_debug_frames', 'display_debug_frames', '', 'The display_debug_frames function in dwarf.c in GNU Binutils 2.29.1 allows remote attackers to cause a denial of service (integer overflow and heap-based buffer over-read, and application crash) or possibly have unspecified other impact via a crafted ELF file, related to print_debug_frame.', 'https://sourceware.org/bugzilla/show_bug.cgi?id=22386###https://sourceware.org/git/gitweb.cgi?p=binutils-gdb.git;h=bf59c5d5f4f5b8b4da1f5f605cfa546f8029b43d', 'CWE-190', '7.8', '6.8', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:H/I:H/A:H', 'AV:N/AC:M/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-16829', '1', 'binutils', '', '', 'buffer-overflow', '_bfd_elf_parse_gnu_properties', '_bfd_elf_parse_gnu_properties', '', 'The _bfd_elf_parse_gnu_properties function in elf-properties.c in the Binary File Descriptor (BFD) library (aka libbfd), as distributed in GNU Binutils 2.29.1, does not prevent negative pointers, which allows remote attackers to cause a denial of service (out-of-bounds read and application crash) or possibly have unspecified other impact via a crafted ELF file.', 'https://sourceware.org/bugzilla/show_bug.cgi?id=22307###https://sourceware.org/git/gitweb.cgi?p=binutils-gdb.git;h=cf54ebff3b7361989712fd9c0128a9b255578163', 'CWE-125', '7.8', '6.8', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:H/I:H/A:H', 'AV:N/AC:M/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-16830', '1', 'binutils', '', '', 'SEGV', 'print_gnu_property_note', 'print_gnu_property_note', '', 'The print_gnu_property_note function in readelf.c in GNU Binutils 2.29.1 does not have integer-overflow protection on 32-bit platforms, which allows remote attackers to cause a denial of service (segmentation violation and application crash) or possibly have unspecified other impact via a crafted ELF file.', 'https://sourceware.org/bugzilla/show_bug.cgi?id=22384###https://sourceware.org/git/gitweb.cgi?p=binutils-gdb.git;h=6ab2c4ed51f9c4243691755e1b1d2149c6a426f4', 'CWE-190', '7.8', '6.8', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:H/I:H/A:H', 'AV:N/AC:M/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-16831', '1', 'binutils', '', '', 'excessive_memory_allocation', null, null, '', 'coffgen.c in the Binary File Descriptor (BFD) library (aka libbfd), as distributed in GNU Binutils 2.29.1, does not validate the symbol count, which allows remote attackers to cause a denial of service (integer overflow and application crash, or excessive memory allocation) or possibly have unspecified other impact via a crafted PE file.', 'https://sourceware.org/bugzilla/show_bug.cgi?id=22385###https://sourceware.org/git/gitweb.cgi?p=binutils-gdb.git;h=6cee897971d4d7cd37d2a686bb6d2aa3e759c8ca', 'CWE-190', '7.8', '6.8', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:H/I:H/A:H', 'AV:N/AC:M/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-16832', '1', 'binutils', '', '', 'SEGV', 'pe_bfd_read_buildid', 'pe_bfd_read_buildid', '', 'The pe_bfd_read_buildid function in peicode.h in the Binary File Descriptor (BFD) library (aka libbfd), as distributed in GNU Binutils 2.29.1, does not validate size and offset values in the data dictionary, which allows remote attackers to cause a denial of service (segmentation violation and application crash) or possibly have unspecified other impact via a crafted PE file.', 'https://sourceware.org/bugzilla/show_bug.cgi?id=22373###https://sourceware.org/git/gitweb.cgi?p=binutils-gdb.git;h=0bb6961f18b8e832d88b490d421ca56cea16c45b', 'CWE-190', '7.8', '6.8', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:H/I:H/A:H', 'AV:N/AC:M/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-16840', '1', 'ffmpeg', '', '', 'buffer-overflow', null, null, '', 'The VC-2 Video Compression encoder in FFmpeg 3.4 allows remote attackers to cause a denial of service (out-of-bounds read) because of incorrect buffer padding for non-Haar wavelets, related to libavcodec/vc2enc.c and libavcodec/vc2enc_dwt.c.', 'http://git.videolan.org/?p=ffmpeg.git;a=commit;h=a94cb36ab2ad99d3a1331c9f91831ef593d94f74', 'CWE-125', '9.8', '7.5', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:N/S:U/C:H/I:H/A:H', 'AV:N/AC:L/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-16868', '1', 'swftools', '', '', 'null_pointer_dereference', 'wav_convert2mono', 'wav_convert2mono', '', 'In SWFTools 0.9.2, the wav_convert2mono function in lib/wav.c does not properly restrict a multiplication within a malloc call, which allows remote attackers to cause a denial of service (integer overflow and NULL pointer dereference) via a crafted WAV file.', 'https://github.com/matthiaskramm/swftools/issues/52', 'CWE-476', '5.5', '4.3', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-16879', '1', 'ncurses', '', '', 'stack-buffer-overflow', '_nc_write_entry', '_nc_write_entry', '', 'Stack-based buffer overflow in the _nc_write_entry function in tinfo/write_entry.c in ncurses 6.0 allows attackers to cause a denial of service (application crash) or possibly execute arbitrary code via a crafted terminfo file, as demonstrated by tic.', 'http://invisible-island.net/ncurses/NEWS.html#t20171125###https://tools.cisco.com/security/center/viewAlert.x?alertId=57695###http://packetstormsecurity.com/files/145045/GNU-ncurses-6.0-tic-Denial-Of-Service.html', 'CWE-119', '7.8', '6.8', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:H/I:H/A:H', 'AV:N/AC:M/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-16890', '1', 'swftools', '', '', 'FloatingPointException', 'wav_convert2mono', 'wav_convert2mono', '', 'SWFTools 0.9.2 has a divide-by-zero error in the wav_convert2mono function in lib/wav.c because the align value may be zero.', 'https://github.com/matthiaskramm/swftools/issues/57', 'CWE-369', '5.5', '4.3', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-17080', '1', 'binutils', '', '', 'buffer-overflow', null, null, '', 'elf.c in the Binary File Descriptor (BFD) library (aka libbfd), as distributed in GNU Binutils 2.29.1, does not validate sizes of core notes, which allows remote attackers to cause a denial of service (bfd_getl32 heap-based buffer over-read and application crash) via a crafted object file, related to elfcore_grok_netbsd_procinfo, elfcore_grok_openbsd_procinfo, and elfcore_grok_nto_status.', 'https://sourceware.org/bugzilla/show_bug.cgi?id=22421', 'CWE-125', '5.5', '4.3', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-17081', '1', 'ffmpeg', '', '', 'buffer-overflow', 'gmc_mmx', 'gmc_mmx', '', 'The gmc_mmx function in libavcodec/x86/mpegvideodsp.c in FFmpeg 3.4 does not properly validate widths and heights, which allows remote attackers to cause a denial of service (integer signedness error and out-of-array read) via a crafted MPEG file.', 'https://bugs.chromium.org/p/oss-fuzz/issues/detail?id=3516#c1###https://github.com/FFmpeg/FFmpeg/commit/58cf31cee7a456057f337b3102a03206d833d5e8###https://lists.ffmpeg.org/pipermail/ffmpeg-devel/2017-November/219748.html', 'CWE-125', '6.5', '4.3', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-17095', '1', 'libtiff', '', '4.0.9', 'buffer-overflow', null, null, '', 'tools/pal2rgb.c in pal2rgb in LibTIFF 4.0.9 allows remote attackers to cause a denial of service (TIFFSetupStrips heap-based buffer overflow and application crash) or possibly have unspecified other impact via a crafted TIFF file.', 'http://bugzilla.maptools.org/show_bug.cgi?id=2750###http://www.openwall.com/lists/oss-security/2017/11/30/3', 'CWE-119', '8.8', '6.8', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:R/S:U/C:H/I:H/A:H', 'AV:N/AC:M/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-17121', '1', 'binutils', '', '', 'SEGV', null, null, '', 'The Binary File Descriptor (BFD) library (aka libbfd), as distributed in GNU Binutils 2.29.1, allows remote attackers to cause a denial of service (memory access violation) or possibly have unspecified other impact via a COFF binary in which a relocation refers to a location after the end of the to-be-relocated section.', 'https://sourceware.org/bugzilla/show_bug.cgi?id=22506###https://sourceware.org/git/gitweb.cgi?p=binutils-gdb.git;h=b23dc97fe237a1d9e850d7cbeee066183a00630b', 'CWE-119', '7.8', '6.8', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:H/I:H/A:H', 'AV:N/AC:M/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-17122', '1', 'binutils', '', '', 'excessive_memory_allocation', 'dump_relocs_in_section', 'dump_relocs_in_section', '', 'The dump_relocs_in_section function in objdump.c in GNU Binutils 2.29.1 does not check for reloc count integer overflows, which allows remote attackers to cause a denial of service (excessive memory allocation, or heap-based buffer overflow and application crash) or possibly have unspecified other impact via a crafted PE file.', 'https://sourceware.org/bugzilla/show_bug.cgi?id=22508###https://sourceware.org/git/gitweb.cgi?p=binutils-gdb.git;h=d785b7d4b877ed465d04072e17ca19d0f47d840f', 'CWE-190', '7.8', '6.8', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:H/I:H/A:H', 'AV:N/AC:M/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-17123', '1', 'binutils', '', '', 'null_pointer_dereference', 'coff_slurp_reloc_table', 'coff_slurp_reloc_table', '', 'The coff_slurp_reloc_table function in coffcode.h in the Binary File Descriptor (BFD) library (aka libbfd), as distributed in GNU Binutils 2.29.1, allows remote attackers to cause a denial of service (NULL pointer dereference and application crash) via a crafted COFF based file.', 'https://sourceware.org/bugzilla/show_bug.cgi?id=22509###https://sourceware.org/git/gitweb.cgi?p=binutils-gdb.git;h=4581a1c7d304ce14e714b27522ebf3d0188d6543', 'CWE-476', '5.5', '4.3', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-17124', '1', 'binutils', '', '', 'excessive_memory_allocation', '_bfd_coff_read_string_table', '_bfd_coff_read_string_table', '', 'The _bfd_coff_read_string_table function in coffgen.c in the Binary File Descriptor (BFD) library (aka libbfd), as distributed in GNU Binutils 2.29.1, does not properly validate the size of the external string table, which allows remote attackers to cause a denial of service (excessive memory consumption, or heap-based buffer overflow and application crash) or possibly have unspecified other impact via a crafted COFF binary.', 'https://sourceware.org/bugzilla/show_bug.cgi?id=22507###https://sourceware.org/git/gitweb.cgi?p=binutils-gdb.git;h=b0029dce6867de1a2828293177b0e030d2f0f03c', 'CWE-119', '7.8', '6.8', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:H/I:H/A:H', 'AV:N/AC:M/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-17125', '1', 'binutils', '', '', 'buffer-overflow', null, null, '', 'nm.c and objdump.c in GNU Binutils 2.29.1 mishandle certain global symbols, which allows remote attackers to cause a denial of service (_bfd_elf_get_symbol_version_string buffer over-read and application crash) or possibly have unspecified other impact via a crafted ELF file.', 'https://sourceware.org/bugzilla/show_bug.cgi?id=22443###https://sourceware.org/git/gitweb.cgi?p=binutils-gdb.git;h=160b1a618ad94988410dc81fce9189fcda5b7ff4', 'CWE-125', '7.8', '6.8', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:H/I:H/A:H', 'AV:N/AC:M/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-17126', '1', 'binutils', '', '', 'SEGV', 'load_debug_section', 'load_debug_section', '', 'The load_debug_section function in readelf.c in GNU Binutils 2.29.1 allows remote attackers to cause a denial of service (invalid memory access and application crash) or possibly have unspecified other impact via an ELF file that lacks section headers.', 'https://sourceware.org/bugzilla/show_bug.cgi?id=22510###https://sourceware.org/git/gitweb.cgi?p=binutils-gdb.git;h=f425ec6600b69e39eb605f3128806ff688137ea8', 'CWE-119', '7.8', '6.8', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:H/I:H/A:H', 'AV:N/AC:M/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-17555', '1', 'ffmpeg', '', '', 'null_pointer_dereference', 'swri_audio_convert', 'swri_audio_convert', '', 'The swri_audio_convert function in audioconvert.c in FFmpeg libswresample through 3.0.101, as used in FFmpeg 3.4.1, aubio 0.4.6, and other products, allows remote attackers to cause a denial of service (NULL pointer dereference and application crash) via a crafted audio file.', 'https://github.com/IvanCql/vulnerability/blob/master/An%20NULL%20pointer%20dereference(DoS)%20Vulnerability%20was%20found%20in%20function%20swri_audio_convert%20of%20ffmpeg%20libswresample.md', 'CWE-476', '6.5', '4.3', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-17669', '1', 'exiv2', '', '0.26', 'buffer-overflow', 'exiv2::internal::pngchunk::keytxtchunk', 'keytxtchunk', '', 'There is a heap-based buffer over-read in the Exiv2::Internal::PngChunk::keyTXTChunk function of pngchunk_int.cpp in Exiv2 0.26. A crafted PNG file will lead to a remote denial of service attack.', 'https://github.com/Exiv2/exiv2/issues/187', 'CWE-125', '5.5', '4.3', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-17722', '1', 'exiv2', '', '0.26', 'assertion_failure', 'readheader', 'readheader', '', 'In Exiv2 0.26, there is a reachable assertion in the readHeader function in bigtiffimage.cpp, which will lead to a remote denial of service attack via a crafted TIFF file.', 'https://bugzilla.redhat.com/show_bug.cgi?id=1524116', 'CWE-617', '6.5', '4.3', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-17723', '1', 'exiv2', '', '0.26', 'buffer-overflow', 'exiv2::image::byteswap4', 'byteswap4', '', 'In Exiv2 0.26, there is a heap-based buffer over-read in the Exiv2::Image::byteSwap4 function in image.cpp. Remote attackers can exploit this vulnerability to disclose memory data or cause a denial of service via a crafted TIFF file.', 'https://bugzilla.redhat.com/show_bug.cgi?id=1524104', 'CWE-125', '8.1', '5.8', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:R/S:U/C:H/I:N/A:H', 'AV:N/AC:M/Au:N/C:P/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-17724', '1', 'exiv2', '', '0.26', 'buffer-overflow', 'exiv2::iptcdata::printstructure', 'printstructure', '', 'In Exiv2 0.26, there is a heap-based buffer over-read in the Exiv2::IptcData::printStructure function in iptc.cpp, related to the \"!= 0x1c\" case. Remote attackers can exploit this vulnerability to cause a denial of service via a crafted TIFF file.', 'https://bugzilla.redhat.com/show_bug.cgi?id=1524107###https://github.com/Exiv2/exiv2/issues/263###https://github.com/xiaoqx/pocs/blob/master/exiv2/readme.md', 'CWE-125', '6.5', '4.3', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-17725', '1', 'exiv2', '', '0.26', 'integer-overflow', 'exiv2::getulong', 'getulong', '', 'In Exiv2 0.26, there is an integer overflow leading to a heap-based buffer over-read in the Exiv2::getULong function in types.cpp. Remote attackers can exploit the vulnerability to cause a denial of service via a crafted image file. Note that this vulnerability is different from CVE-2017-14864, which is an invalid memory address dereference.', 'https://bugzilla.redhat.com/show_bug.cgi?id=1525055###https://github.com/Exiv2/exiv2/issues/188', 'CWE-190', '6.5', '4.3', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-17942', '1', 'libtiff', '', '4.0.9', 'buffer-overflow', null, null, '', 'In LibTIFF 4.0.9, there is a heap-based buffer over-read in the function PackBitsEncode in tif_packbits.c.', 'http://bugzilla.maptools.org/show_bug.cgi?id=2767', 'CWE-125', '8.8', '6.8', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:R/S:U/C:H/I:H/A:H', 'AV:N/AC:M/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-17973', '1', 'libtiff', '', '4.0.8', 'use-after-free', 't2p_writeproc', 't2p_writeproc', '', '** DISPUTED ** In LibTIFF 4.0.8, there is a heap-based use-after-free in the t2p_writeproc function in tiff2pdf.c. NOTE: there is a third-party report of inability to reproduce this issue.', 'http://bugzilla.maptools.org/show_bug.cgi?id=2769###https://bugzilla.novell.com/show_bug.cgi?id=1074318###https://bugzilla.redhat.com/show_bug.cgi?id=1530912', 'CWE-416', '8.8', '6.8', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:R/S:U/C:H/I:H/A:H', 'AV:N/AC:M/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-18005', '1', 'exiv2', '', '0.26', 'null_pointer_dereference', 'exiv2::datavalue::tolong', 'tolong', '', 'Exiv2 0.26 has a Null Pointer Dereference in the Exiv2::DataValue::toLong function in value.cpp, related to crafted metadata in a TIFF file.', 'https://github.com/Exiv2/exiv2/issues/168', 'CWE-476', '5.5', '4.3', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-18013', '1', 'libtiff', '', '4.0.9', 'null_pointer_dereference', 'tif_print.c', 'tif_print.c', '', 'In LibTIFF 4.0.9, there is a Null-Pointer Dereference in the tif_print.c TIFFPrintDirectory function, as demonstrated by a tiffinfo crash.', 'http://bugzilla.maptools.org/show_bug.cgi?id=2770###https://gitlab.com/libtiff/libtiff/commit/c6f41df7b581402dfba3c19a1e3df4454c551a01', 'CWE-476', '6.5', '4.3', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-18416', '1', 'sqlite', '', '', '', '', '', '', 'cPanel before 67.9999.103 allows arbitrary file-overwrite operations during a Roundcube SQLite schema update (SEC-303).', '', 'CWE-284', '5.5', '3.6', 'CVSS:3.0/AV:L/AC:L/PR:L/UI:N/S:U/C:N/I:H/A:N', 'AV:L/AC:L/Au:N/C:N/I:P/A:P');
