INSERT INTO `cve_general` VALUES ('CVE-2017-2870', '1', 'gdk-pixbuf', '', '2.36.6', 'integer-overflow', 'tiff_image_parse', 'tiff_image_parse', '', 'An exploitable integer overflow vulnerability exists in the tiff_image_parse functionality of Gdk-Pixbuf 2.36.6 when compiled with Clang. A specially crafted tiff file can cause a heap-overflow resulting in remote code execution. An attacker can send a file or a URL to trigger this vulnerability.', 'https://www.talosintelligence.com/vulnerability_reports/TALOS-2017-0377', 'CWE-190', '7.8', '6.8', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:H/I:H/A:H', 'AV:N/AC:M/Au:N/C:P/I:P/A:P');
