INSERT INTO `cve_general` VALUES ('CVE-2017-6312', '1', 'gdk-pixbuf', '', null, 'integer-overflow', null, null, '', 'Integer overflow in io-ico.c in gdk-pixbuf allows context-dependent attackers to cause a denial of service (segmentation fault and application crash) via a crafted image entry offset in an ICO file, which triggers an out-of-bounds read, related to compiler optimizations.', 'http://mov.sx/2017/02/21/bug-hunting-gdk-pixbuf.html###https://bugzilla.gnome.org/show_bug.cgi?id=779012', 'CWE-190', '5.5', '4.3', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
