INSERT INTO `cve_general` VALUES ('CVE-2017-6314', '1', 'gdk-pixbuf', '', null, 'infinite_loop', 'make_available_at_least', 'make_available_at_least', '', 'The make_available_at_least function in io-tiff.c in gdk-pixbuf allows context-dependent attackers to cause a denial of service (infinite loop) via a large TIFF file.', 'http://mov.sx/2017/02/21/bug-hunting-gdk-pixbuf.html###https://bugzilla.gnome.org/show_bug.cgi?id=779020', 'CWE-835', '5.5', '4.3', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-6850', '1', 'jasper', '', '2.0.13', 'null_pointer_dereference', 'jp2_cdef_destroy', 'jp2_cdef_destroy', '', 'The jp2_cdef_destroy function in jp2_cod.c in JasPer before 2.0.13 allows remote attackers to cause a denial of service (NULL pointer dereference) via a crafted image.', 'https://github.com/mdadams/jasper/commit/e96fc4fdd525fa0ede28074a7e2b1caf94b58b0d###https://github.com/mdadams/jasper/issues/112###https://blogs.gentoo.org/ago/2017/01/25/jasper-null-pointer-dereference-in-jp2_cdef_destroy-jp2_cod-c/', 'CWE-476', '5.5', '4.3', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-6851', '1', 'jasper', '', '2.0.10', 'buffer-overflow', 'jas_matrix_bindsub', 'jas_matrix_bindsub', '', 'The jas_matrix_bindsub function in jas_seq.c in JasPer 2.0.10 allows remote attackers to cause a denial of service (invalid read) via a crafted image.', 'https://blogs.gentoo.org/ago/2017/01/25/jasper-invalid-memory-read-in-jas_matrix_bindsub-jas_seq-c/###https://github.com/mdadams/jasper/issues/113', 'CWE-125', '5.5', '4.3', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-6852', '1', 'jasper', '', '2.0.10', 'buffer-overflow', 'jpc_dec_decodepkt', 'jpc_dec_decodepkt', '', 'Heap-based buffer overflow in the jpc_dec_decodepkt function in jpc_t2dec.c in JasPer 2.0.10 allows remote attackers to have unspecified impact via a crafted image.', 'https://blogs.gentoo.org/ago/2017/01/25/jasper-heap-based-buffer-overflow-in-jpc_dec_decodepkt-jpc_t2dec-c/###https://github.com/mdadams/jasper/issues/114', 'CWE-119', '7.8', '6.8', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:H/I:H/A:H', 'AV:N/AC:M/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-6965', '1', 'binutils', '', '', 'buffer-overflow', null, null, '', 'readelf in GNU Binutils 2.28 writes to illegal addresses while processing corrupt input files containing symbol-difference relocations, leading to a heap-based buffer overflow.', 'https://sourceware.org/bugzilla/show_bug.cgi?id=21137', 'CWE-119', '5.5', '4.3', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-6966', '1', 'binutils', '', '', 'use-after-free', null, null, '', 'readelf in GNU Binutils 2.28 has a use-after-free (specifically read-after-free) error while processing multiple, relocated sections in an MSP430 binary. This is caused by mishandling of an invalid symbol index, and mishandling of state across invocations.', 'https://sourceware.org/bugzilla/show_bug.cgi?id=21139', 'CWE-416', '5.5', '4.3', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-6969', '1', 'binutils', '', '', 'information_leak', null, null, '', 'readelf in GNU Binutils 2.28 is vulnerable to a heap-based buffer over-read while processing corrupt RL78 binaries. The vulnerability can trigger program crashes. It may lead to an information leak as well.', 'https://sourceware.org/bugzilla/show_bug.cgi?id=21156', 'CWE-125', '9.1', '6.4', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:N/S:U/C:H/I:N/A:H', 'AV:N/AC:L/Au:N/C:P/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-7209', '1', 'binutils', '', '', 'null_pointer_dereference', 'dump_section_as_bytes', 'dump_section_as_bytes', '', 'The dump_section_as_bytes function in readelf in GNU Binutils 2.28 accesses a NULL pointer while reading section contents in a corrupt binary, leading to a program crash.', 'https://sourceware.org/bugzilla/show_bug.cgi?id=21135', 'CWE-476', '5.5', '4.3', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-7210', '1', 'binutils', '', '', 'buffer-overflow', null, null, '', 'objdump in GNU Binutils 2.28 is vulnerable to multiple heap-based buffer over-reads (of size 1 and size 8) while handling corrupt STABS enum type strings in a crafted object file, leading to program crash.', 'https://sourceware.org/bugzilla/show_bug.cgi?id=21157', 'CWE-119', '5.5', '4.3', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-7223', '1', 'binutils', '', '', 'buffer-overflow', null, null, '', 'GNU assembler in GNU Binutils 2.28 is vulnerable to a global buffer overflow (of size 1) while attempting to unget an EOF character from the input stream, potentially leading to a program crash.', 'https://sourceware.org/bugzilla/show_bug.cgi?id=20898', 'CWE-119', '7.5', '5', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:N/S:U/C:N/I:N/A:H', 'AV:N/AC:L/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-7224', '1', 'binutils', '', '', null, 'find_nearest_line', 'find_nearest_line', '', 'The find_nearest_line function in objdump in GNU Binutils 2.28 is vulnerable to an invalid write (of size 1) while disassembling a corrupt binary that contains an empty function name, leading to a program crash.', 'https://sourceware.org/bugzilla/show_bug.cgi?id=20892', 'CWE-787', '5.5', '4.3', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-7225', '1', 'binutils', '', '', 'null_pointer_dereference', 'find_nearest_line', 'find_nearest_line', '', 'The find_nearest_line function in addr2line in GNU Binutils 2.28 does not handle the case where the main file name and the directory name are both empty, triggering a NULL pointer dereference and an invalid write, and leading to a program crash.', 'https://sourceware.org/bugzilla/show_bug.cgi?id=20891', 'CWE-476', '7.5', '5', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:N/S:U/C:N/I:N/A:H', 'AV:N/AC:L/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-7226', '1', 'binutils', '', '', 'buffer-overflow', 'pe_ilf_object_p', 'pe_ilf_object_p', '', 'The pe_ILF_object_p function in the Binary File Descriptor (BFD) library (aka libbfd), as distributed in GNU Binutils 2.28, is vulnerable to a heap-based buffer over-read of size 4049 because it uses the strlen function instead of strnlen, leading to program crashes in several utilities such as addr2line, size, and strings. It could lead to information disclosure as well.', 'https://sourceware.org/bugzilla/show_bug.cgi?id=20905', 'CWE-125', '9.1', '6.4', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:N/S:U/C:H/I:N/A:H', 'AV:N/AC:L/Au:N/C:P/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-7227', '1', 'binutils', '', '', 'buffer-overflow', null, null, '', 'GNU linker (ld) in GNU Binutils 2.28 is vulnerable to a heap-based buffer overflow while processing a bogus input script, leading to a program crash. This relates to lack of \'\\0\' termination of a name field in ldlex.l.', 'https://sourceware.org/bugzilla/show_bug.cgi?id=20906', 'CWE-119', '7.5', '5', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:N/S:U/C:N/I:N/A:H', 'AV:N/AC:L/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-7299', '1', 'binutils', '', '', 'buffer-overflow', '###bfd_elf_final_link', '', '', 'The Binary File Descriptor (BFD) library (aka libbfd), as distributed in GNU Binutils 2.28, has an invalid read (of size 8) because the code to emit relocs (bfd_elf_final_link function in bfd/elflink.c) does not check the format of the input file before trying to read the ELF reloc section header. The vulnerability leads to a GNU linker (ld) program crash.', 'https://sourceware.org/bugzilla/show_bug.cgi?id=20908', 'CWE-125', '5.5', '4.3', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-7300', '1', 'binutils', '', '', 'buffer-overflow', 'aout_link_add_symbols', 'aout_link_add_symbols', '', 'The Binary File Descriptor (BFD) library (aka libbfd), as distributed in GNU Binutils 2.28, has an aout_link_add_symbols function in bfd/aoutx.h that is vulnerable to a heap-based buffer over-read (off-by-one) because of an incomplete check for invalid string offsets while loading symbols, leading to a GNU linker (ld) program crash.', 'https://sourceware.org/bugzilla/show_bug.cgi?id=20909', 'CWE-125', '7.5', '5', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:N/S:U/C:N/I:N/A:H', 'AV:N/AC:L/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-7301', '1', 'binutils', '', '', 'buffer-overflow', 'aout_link_add_symbols', 'aout_link_add_symbols', '', 'The Binary File Descriptor (BFD) library (aka libbfd), as distributed in GNU Binutils 2.28, has an aout_link_add_symbols function in bfd/aoutx.h that has an off-by-one vulnerability because it does not carefully check the string offset. The vulnerability could lead to a GNU linker (ld) program crash.', 'https://sourceware.org/bugzilla/show_bug.cgi?id=20924', 'CWE-20', '7.5', '5', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:N/S:U/C:N/I:N/A:H', 'AV:N/AC:L/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-7302', '1', 'binutils', '', '', 'buffer-overflow', 'swap_std_reloc_out', 'swap_std_reloc_out', '', 'The Binary File Descriptor (BFD) library (aka libbfd), as distributed in GNU Binutils 2.28, has a swap_std_reloc_out function in bfd/aoutx.h that is vulnerable to an invalid read (of size 4) because of missing checks for relocs that could not be recognised. This vulnerability causes Binutils utilities like strip to crash.', 'https://sourceware.org/bugzilla/show_bug.cgi?id=20921', 'CWE-125', '7.5', '5', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:N/S:U/C:N/I:N/A:H', 'AV:N/AC:L/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-7303', '1', 'binutils', '', '', 'buffer-overflow', null, null, '', 'The Binary File Descriptor (BFD) library (aka libbfd), as distributed in GNU Binutils 2.28, is vulnerable to an invalid read (of size 4) because of missing a check (in the find_link function) for null headers before attempting to match them. This vulnerability causes Binutils utilities like strip to crash.', 'https://sourceware.org/bugzilla/show_bug.cgi?id=20922', 'CWE-125', '7.5', '5', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:N/S:U/C:N/I:N/A:H', 'AV:N/AC:L/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-7304', '1', 'binutils', '', '', 'buffer-overflow', null, null, '', 'The Binary File Descriptor (BFD) library (aka libbfd), as distributed in GNU Binutils 2.28, is vulnerable to an invalid read (of size 8) because of missing a check (in the copy_special_section_fields function) for an invalid sh_link field before attempting to follow it. This vulnerability causes Binutils utilities like strip to crash.', 'https://sourceware.org/bugzilla/show_bug.cgi?id=20931', 'CWE-125', '7.5', '5', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:N/S:U/C:N/I:N/A:H', 'AV:N/AC:L/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-7592', '1', 'libtiff', '', '4.0.7', null, 'putagreytile', 'putagreytile', '', 'The putagreytile function in tif_getimage.c in LibTIFF 4.0.7 has a left-shift undefined behavior issue, which might allow remote attackers to cause a denial of service (application crash) or possibly have unspecified other impact via a crafted image.', 'http://bugzilla.maptools.org/show_bug.cgi?id=2658', 'CWE-20', '7.8', '6.8', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:H/I:H/A:H', 'AV:N/AC:M/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-7593', '1', 'libtiff', '', '4.0.7', null, null, null, '', 'tif_read.c in LibTIFF 4.0.7 does not ensure that tif_rawdata is properly initialized, which might allow remote attackers to obtain sensitive information from process memory via a crafted image.', 'http://bugzilla.maptools.org/show_bug.cgi?id=2651', 'CWE-119', '5.5', '4.3', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:H/I:N/A:N', 'AV:N/AC:M/Au:N/C:P/I:N/A:N');
INSERT INTO `cve_general` VALUES ('CVE-2017-7594', '1', 'libtiff', '', '4.0.7', 'memory_leak', 'ojpegreadheaderinfosectablesdctable', 'ojpegreadheaderinfosectablesdctable', '', 'The OJPEGReadHeaderInfoSecTablesDcTable function in tif_ojpeg.c in LibTIFF 4.0.7 allows remote attackers to cause a denial of service (memory leak) via a crafted image.', 'http://bugzilla.maptools.org/show_bug.cgi?id=2659', 'CWE-772', '5.5', '4.3', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-7595', '1', 'libtiff', '', '4.0.7', 'FloatingPointException', 'jpegsetupencode', 'jpegsetupencode', '', 'The JPEGSetupEncode function in tiff_jpeg.c in LibTIFF 4.0.7 allows remote attackers to cause a denial of service (divide-by-zero error and application crash) via a crafted image.', 'https://blogs.gentoo.org/ago/2017/04/01/libtiff-divide-by-zero-in-jpegsetupencode-tiff_jpeg-c', 'CWE-369', '5.5', '4.3', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-7596', '1', 'libtiff', '', '4.0.7', 'type_overflow', null, null, '', 'LibTIFF 4.0.7 has an \"outside the range of representable values of type float\" undefined behavior issue, which might allow remote attackers to cause a denial of service (application crash) or possibly have unspecified other impact via a crafted image.', 'https://blogs.gentoo.org/ago/2017/04/01/libtiff-multiple-ubsan-crashes', 'CWE-20', '7.8', '6.8', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:H/I:H/A:H', 'AV:N/AC:M/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-7597', '1', 'libtiff', '', '4.0.7', 'type_overflow', null, null, '', 'tif_dirread.c in LibTIFF 4.0.7 has an \"outside the range of representable values of type float\" undefined behavior issue, which might allow remote attackers to cause a denial of service (application crash) or possibly have unspecified other impact via a crafted image.', 'https://blogs.gentoo.org/ago/2017/04/01/libtiff-multiple-ubsan-crashes', 'CWE-20', '7.8', '6.8', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:H/I:H/A:H', 'AV:N/AC:M/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-7598', '1', 'libtiff', '', '4.0.7', 'FloatingPointException', null, null, '', 'tif_dirread.c in LibTIFF 4.0.7 might allow remote attackers to cause a denial of service (divide-by-zero error and application crash) via a crafted image.', 'https://blogs.gentoo.org/ago/2017/04/01/libtiff-multiple-ubsan-crashes', 'CWE-369', '7.8', '4.3', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:H/I:H/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-7599', '1', 'libtiff', '', '4.0.7', 'type_overflow', null, null, '', 'LibTIFF 4.0.7 has an \"outside the range of representable values of type short\" undefined behavior issue, which might allow remote attackers to cause a denial of service (application crash) or possibly have unspecified other impact via a crafted image.', 'https://blogs.gentoo.org/ago/2017/04/01/libtiff-multiple-ubsan-crashes', 'CWE-20', '7.8', '6.8', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:H/I:H/A:H', 'AV:N/AC:M/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-7600', '1', 'libtiff', '', '4.0.7', 'type_overflow', null, null, '', 'LibTIFF 4.0.7 has an \"outside the range of representable values of type unsigned char\" undefined behavior issue, which might allow remote attackers to cause a denial of service (application crash) or possibly have unspecified other impact via a crafted image.', 'https://blogs.gentoo.org/ago/2017/04/01/libtiff-multiple-ubsan-crashes', 'CWE-20', '7.8', '6.8', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:H/I:H/A:H', 'AV:N/AC:M/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-7601', '1', 'libtiff', '', '4.0.7', null, null, null, '', 'LibTIFF 4.0.7 has a \"shift exponent too large for 64-bit type long\" undefined behavior issue, which might allow remote attackers to cause a denial of service (application crash) or possibly have unspecified other impact via a crafted image.', 'https://blogs.gentoo.org/ago/2017/04/01/libtiff-multiple-ubsan-crashes', 'CWE-20', '7.8', '6.8', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:H/I:H/A:H', 'AV:N/AC:M/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-7602', '1', 'libtiff', '', '4.0.7', 'integer-overflow', null, null, '', 'LibTIFF 4.0.7 has a signed integer overflow, which might allow remote attackers to cause a denial of service (application crash) or possibly have unspecified other impact via a crafted image.', 'https://blogs.gentoo.org/ago/2017/04/01/libtiff-multiple-ubsan-crashes', 'CWE-190', '7.8', '6.8', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:H/I:H/A:H', 'AV:N/AC:M/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-7614', '1', 'binutils', '', '', 'null_pointer_dereference', 'main', 'main', '', 'elflink.c in the Binary File Descriptor (BFD) library (aka libbfd), as distributed in GNU Binutils 2.28, has a \"member access within null pointer\" undefined behavior issue, which might allow remote attackers to cause a denial of service (application crash) or possibly have unspecified other impact via an \"int main() {return 0;}\" program.', 'https://blogs.gentoo.org/ago/2017/04/05/binutils-two-null-pointer-dereference-in-elflink-c/', 'CWE-476', '9.8', '7.5', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:N/S:U/C:H/I:H/A:H', 'AV:N/AC:L/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-7698', '1', 'swftools', '', '', 'use-after-free', null, null, '', 'A Use After Free in the pdf2swf part of swftools 0.9.2 and earlier allows remote attackers to execute arbitrary code via a malformed PDF document, possibly a consequence of an error in Gfx.cc in Xpdf 3.02.', 'https://github.com/matthiaskramm/swftools/pull/19', 'CWE-416', '7.8', '6.8', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:H/I:H/A:H', 'AV:N/AC:M/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-7859', '1', 'ffmpeg', '', '', 'buffer-overflow', 'ff_h264_slice_context_init', 'ff_h264_slice_context_init', '', 'FFmpeg before 2017-03-05 has an out-of-bounds write caused by a heap-based buffer overflow related to the ff_h264_slice_context_init function in libavcodec/h264dec.c.', 'https://bugs.chromium.org/p/oss-fuzz/issues/detail?id=713', 'CWE-787', '9.8', '7.5', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:N/S:U/C:H/I:H/A:H', 'AV:N/AC:L/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-7862', '1', 'ffmpeg', '', '', 'buffer-overflow', 'decode_frame', 'decode_frame', '', 'FFmpeg before 2017-02-07 has an out-of-bounds write caused by a heap-based buffer overflow related to the decode_frame function in libavcodec/pictordec.c.', 'https://bugs.chromium.org/p/oss-fuzz/issues/detail?id=559###https://github.com/FFmpeg/FFmpeg/commit/8c2ea3030af7b40a3c4275696fb5c76cdb80950a', 'CWE-787', '9.8', '7.5', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:N/S:U/C:H/I:H/A:H', 'AV:N/AC:L/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-7863', '1', 'ffmpeg', '', '', 'buffer-overflow', 'decode_frame_common', 'decode_frame_common', '', 'FFmpeg before 2017-02-04 has an out-of-bounds write caused by a heap-based buffer overflow related to the decode_frame_common function in libavcodec/pngdec.c.', 'https://bugs.chromium.org/p/oss-fuzz/issues/detail?id=546###https://github.com/FFmpeg/FFmpeg/commit/e477f09d0b3619f3d29173b2cd593e17e2d1978e', 'CWE-787', '9.8', '7.5', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:N/S:U/C:H/I:H/A:H', 'AV:N/AC:L/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-7865', '1', 'ffmpeg', '', '', 'buffer-overflow', 'ipvideo_decode_block_opcode_0xa', 'ipvideo_decode_block_opcode_0xa', '', 'FFmpeg before 2017-01-24 has an out-of-bounds write caused by a heap-based buffer overflow related to the ipvideo_decode_block_opcode_0xA function in libavcodec/interplayvideo.c and the avcodec_align_dimensions2 function in libavcodec/utils.c.', 'https://bugs.chromium.org/p/oss-fuzz/issues/detail?id=452###https://github.com/FFmpeg/FFmpeg/commit/2080bc33717955a0e4268e738acf8c1eeddbf8cb', 'CWE-787', '9.8', '7.5', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:N/S:U/C:H/I:H/A:H', 'AV:N/AC:L/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-7866', '1', 'ffmpeg', '', '', 'stack-buffer-overflow', 'decode_zbuf', 'decode_zbuf', '', 'FFmpeg before 2017-01-23 has an out-of-bounds write caused by a stack-based buffer overflow related to the decode_zbuf function in libavcodec/pngdec.c.', 'https://bugs.chromium.org/p/oss-fuzz/issues/detail?id=444###https://github.com/FFmpeg/FFmpeg/commit/e371f031b942d73e02c090170975561fabd5c264', 'CWE-787', '9.8', '7.5', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:N/S:U/C:H/I:H/A:H', 'AV:N/AC:L/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-8392', '1', 'binutils', '', '', 'buffer-overflow', '_bfd_dwarf2_find_nearest_line', '_bfd_dwarf2_find_nearest_line', '', 'The Binary File Descriptor (BFD) library (aka libbfd), as distributed in GNU Binutils 2.28, is vulnerable to an invalid read of size 8 because of missing a check to determine whether symbols are NULL in the _bfd_dwarf2_find_nearest_line function. This vulnerability causes programs that conduct an analysis of binary programs using the libbfd library, such as objdump, to crash.', 'https://sourceware.org/bugzilla/show_bug.cgi?id=21409', 'CWE-476', '7.5', '5', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:N/S:U/C:N/I:N/A:H', 'AV:N/AC:L/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-8393', '1', 'binutils', '', '', 'buffer-overflow', null, null, '', 'The Binary File Descriptor (BFD) library (aka libbfd), as distributed in GNU Binutils 2.28, is vulnerable to a global buffer over-read error because of an assumption made by code that runs for objcopy and strip, that SHT_REL/SHR_RELA sections are always named starting with a .rel/.rela prefix. This vulnerability causes programs that conduct an analysis of binary programs using the libbfd library, such as objcopy and strip, to crash.', 'https://sourceware.org/bugzilla/show_bug.cgi?id=21412', 'CWE-125', '7.5', '5', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:N/S:U/C:N/I:N/A:H', 'AV:N/AC:L/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-8394', '1', 'binutils', '', '', 'null_pointer_dereference', null, null, '', 'The Binary File Descriptor (BFD) library (aka libbfd), as distributed in GNU Binutils 2.28, is vulnerable to an invalid read of size 4 due to NULL pointer dereferencing of _bfd_elf_large_com_section. This vulnerability causes programs that conduct an analysis of binary programs using the libbfd library, such as objcopy, to crash.', 'https://sourceware.org/bugzilla/show_bug.cgi?id=21414', 'CWE-476', '7.5', '5', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:N/S:U/C:N/I:N/A:H', 'AV:N/AC:L/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-8395', '1', 'binutils', '', '', null, 'malloc', 'malloc', '', 'The Binary File Descriptor (BFD) library (aka libbfd), as distributed in GNU Binutils 2.28, is vulnerable to an invalid write of size 8 because of missing a malloc() return-value check to see if memory had actually been allocated in the _bfd_generic_get_section_contents function. This vulnerability causes programs that conduct an analysis of binary programs using the libbfd library, such as objcopy, to crash.', 'https://sourceware.org/bugzilla/show_bug.cgi?id=21431', 'CWE-476', '7.5', '5', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:N/S:U/C:N/I:N/A:H', 'AV:N/AC:L/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-8396', '1', 'binutils', '', '', 'buffer-overflow', null, null, '', 'The Binary File Descriptor (BFD) library (aka libbfd), as distributed in GNU Binutils 2.28, is vulnerable to an invalid read of size 1 because the existing reloc offset range tests didn\'t catch small negative offsets less than the size of the reloc field. This vulnerability causes programs that conduct an analysis of binary programs using the libbfd library, such as objdump, to crash.', 'https://sourceware.org/bugzilla/show_bug.cgi?id=21432', 'CWE-20', '7.5', '5', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:N/S:U/C:N/I:N/A:H', 'AV:N/AC:L/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-8397', '1', 'binutils', '', '', 'buffer-overflow', null, null, '', 'The Binary File Descriptor (BFD) library (aka libbfd), as distributed in GNU Binutils 2.28, is vulnerable to an invalid read of size 1 and an invalid write of size 1 during processing of a corrupt binary containing reloc(s) with negative addresses. This vulnerability causes programs that conduct an analysis of binary programs using the libbfd library, such as objdump, to crash.', 'https://sourceware.org/bugzilla/show_bug.cgi?id=21434', 'CWE-119', '7.5', '5', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:N/S:U/C:N/I:N/A:H', 'AV:N/AC:L/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-8398', '1', 'binutils', '', '', 'buffer-overflow', null, null, '', 'dwarf.c in GNU Binutils 2.28 is vulnerable to an invalid read of size 1 during dumping of debug information from a corrupt binary. This vulnerability causes programs that conduct an analysis of binary programs, such as objdump and readelf, to crash.', 'https://sourceware.org/bugzilla/show_bug.cgi?id=21438', 'CWE-119', '7.5', '5', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:N/S:U/C:N/I:N/A:H', 'AV:N/AC:L/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-8400', '1', 'swftools', '', '', 'buffer-overflow', 'png_load', 'png_load', '', 'In SWFTools 0.9.2, an out-of-bounds write of heap data can occur in the function png_load() in lib/png.c:755. This issue can be triggered by a malformed PNG file that is mishandled by png2swf. Attackers could exploit this issue for DoS; it might cause arbitrary code execution.', 'https://github.com/matthiaskramm/swftools/issues/13', 'CWE-787', '8.8', '6.8', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:R/S:U/C:H/I:H/A:H', 'AV:N/AC:M/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-8401', '1', 'swftools', '', '', 'buffer-overflow', 'png_load', 'png_load', '', 'In SWFTools 0.9.2, an out-of-bounds read of heap data can occur in the function png_load() in lib/png.c:724. This issue can be triggered by a malformed PNG file that is mishandled by png2swf. Attackers could exploit this issue for DoS.', 'https://github.com/matthiaskramm/swftools/issues/14', 'CWE-125', '6.5', '4.3', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-8419', '1', 'lame', '', '3.99.5', 'buffer-overflow', null, null, '', 'LAME through 3.99.5 relies on the signed integer data type for values in a WAV or AIFF header, which allows remote attackers to cause a denial of service (stack-based buffer overflow or heap-based buffer overflow) or possibly have unspecified other impact via a crafted file, as demonstrated by mishandling of num_channels.', 'https://sourceforge.net/p/lame/bugs/458/', 'CWE-119', '7.8', '6.8', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:H/I:H/A:H', 'AV:N/AC:M/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-8420', '1', 'swftools', '', '', 'SEGV', null, null, '', 'SWFTools 2013-04-09-1007 on Windows has a \"Data from Faulting Address controls Branch Selection starting at image00000000_00400000+0x0000000000003e71\" issue. This issue can be triggered by a malformed TTF file that is mishandled by font2swf. Attackers could exploit this issue for DoS (Access Violation).', 'https://github.com/wlinzi/security_advisories/tree/master/CVE-2017-8420', 'CWE-119', '6.5', '4.3', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-8421', '1', 'binutils', '', '', 'memory_leak', 'dump_relocs_in_section', 'dump_relocs_in_section', '', 'The function coff_set_alignment_hook in coffcode.h in Binary File Descriptor (BFD) library (aka libbfd), as distributed in GNU Binutils 2.28, has a memory leak vulnerability which can cause memory exhaustion in objdump via a crafted PE file. Additional validation in dump_relocs_in_section in objdump.c can resolve this.', 'https://sourceware.org/bugzilla/show_bug.cgi?id=21440', 'CWE-772', '5.5', '7.1', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:C');
INSERT INTO `cve_general` VALUES ('CVE-2017-9038', '1', 'binutils', '', '', 'buffer-overflow', 'byte_get_little_endian', 'byte_get_little_endian', '', 'GNU Binutils 2.28 allows remote attackers to cause a denial of service (heap-based buffer over-read and application crash) via a crafted ELF file, related to the byte_get_little_endian function in elfcomm.c, the get_unwind_section_word function in readelf.c, and ARM unwind information that contains invalid word offsets.', 'https://blogs.gentoo.org/ago/2017/05/12/binutils-multiple-crashes/###https://sourceware.org/git/gitweb.cgi?p=binutils-gdb.git;h=f32ba72991d2406b21ab17edc234a2f3fa7fb23d', 'CWE-125', '5.5', '4.3', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-9039', '1', 'binutils', '', '', 'excessive_memory_allocation', 'get_program_headers', 'get_program_headers', '', 'GNU Binutils 2.28 allows remote attackers to cause a denial of service (memory consumption) via a crafted ELF file with many program headers, related to the get_program_headers function in readelf.c.', 'https://blogs.gentoo.org/ago/2017/05/12/binutils-multiple-crashes/###https://sourceware.org/git/gitweb.cgi?p=binutils-gdb.git;h=82156ab704b08b124d319c0decdbd48b3ca2dac5', 'CWE-770', '5.5', '4.3', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-9040', '1', 'binutils', '', '', 'null_pointer_dereference', 'process_mips_specific', 'process_mips_specific', '', 'GNU Binutils 2017-04-03 allows remote attackers to cause a denial of service (NULL pointer dereference and application crash), related to the process_mips_specific function in readelf.c, via a crafted ELF file that triggers a large memory-allocation attempt.', 'https://blogs.gentoo.org/ago/2017/05/12/binutils-multiple-crashes/###https://sourceware.org/git/gitweb.cgi?p=binutils-gdb.git;h=7296a62a2a237f6b1ad8db8c38b090e9f592c8cf', 'CWE-476', '5.5', '4.3', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-9041', '1', 'binutils', '', '', 'buffer-overflow', 'process_mips_specific', 'process_mips_specific', '', 'GNU Binutils 2.28 allows remote attackers to cause a denial of service (heap-based buffer over-read and application crash) via a crafted ELF file, related to MIPS GOT mishandling in the process_mips_specific function in readelf.c.', 'https://blogs.gentoo.org/ago/2017/05/12/binutils-multiple-crashes/###https://sourceware.org/git/gitweb.cgi?p=binutils-gdb.git;h=75ec1fdbb797a389e4fe4aaf2e15358a070dcc19###https://sourceware.org/git/gitweb.cgi?p=binutils-gdb.git;h=c4ab9505b53cdc899506ed421fddb7e1f8faf7a3', 'CWE-125', '5.5', '4.3', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-9042', '1', 'binutils', '', '', null, null, null, '', 'readelf.c in GNU Binutils 2017-04-12 has a \"cannot be represented in type long\" issue, which might allow remote attackers to cause a denial of service (application crash) or possibly have unspecified other impact via a crafted ELF file.', 'https://blogs.gentoo.org/ago/2017/05/12/binutils-multiple-crashes/###https://sourceware.org/git/gitweb.cgi?p=binutils-gdb.git;h=7296a62a2a237f6b1ad8db8c38b090e9f592c8cf', 'CWE-704', '7.8', '6.8', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:H/I:H/A:H', 'AV:N/AC:M/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-9043', '1', 'binutils', '', '', null, null, null, '', 'readelf.c in GNU Binutils 2017-04-12 has a \"shift exponent too large for type unsigned long\" issue, which might allow remote attackers to cause a denial of service (application crash) or possibly have unspecified other impact via a crafted ELF file.', 'https://blogs.gentoo.org/ago/2017/05/12/binutils-multiple-crashes/###https://sourceware.org/git/gitweb.cgi?p=binutils-gdb.git;h=ddef72cdc10d82ba011a7ff81cafbbd3466acf54', 'CWE-20', '7.8', '6.8', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:H/I:H/A:H', 'AV:N/AC:M/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-9044', '1', 'binutils', '', '', 'buffer-overflow', 'print_symbol_for_build_attribute', 'print_symbol_for_build_attribute', '', 'The print_symbol_for_build_attribute function in readelf.c in GNU Binutils 2017-04-12 allows remote attackers to cause a denial of service (invalid read and SEGV) via a crafted ELF file.', 'https://blogs.gentoo.org/ago/2017/05/12/binutils-multiple-crashes/', 'CWE-125', '5.5', '4.3', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-9117', '1', 'libtiff', '', '4.0.7', 'buffer-overflow', null, null, '', 'In LibTIFF 4.0.7, the program processes BMP images without verifying that biWidth and biHeight in the bitmap-information header match the actual input, leading to a heap-based buffer over-read in bmp2tiff.', 'http://bugzilla.maptools.org/show_bug.cgi?id=2690', 'CWE-125', '9.8', '7.5', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:N/S:U/C:H/I:H/A:H', 'AV:N/AC:L/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-9147', '1', 'libtiff', '', '4.0.7', 'buffer-overflow', '_tiffvgetfield', '_tiffvgetfield', '', 'LibTIFF 4.0.7 has an invalid read in the _TIFFVGetField function in tif_dir.c, which might allow remote attackers to cause a denial of service (crash) via a crafted TIFF file.', 'http://bugzilla.maptools.org/show_bug.cgi?id=2693', 'CWE-125', '6.5', '4.3', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-9239', '1', 'exiv2', '', '0.26', 'SEGV', 'pvalue', 'pvalue', '', 'An issue was discovered in Exiv2 0.26. When the data structure of the structure ifd is incorrect, the program assigns pValue_ to 0x0, and the value of pValue() is 0x0. TiffImageEntry::doWriteImage will use the value of pValue() to cause a segmentation fault. To exploit this vulnerability, someone must open a crafted tiff file.', 'http://dev.exiv2.org/issues/1295###https://github.com/lolo-pop/poc/tree/master/Segmentation%20fault%20in%20convert-test(exiv2)', 'CWE-369', '6.5', '4.3', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-9403', '1', 'libtiff', '', '4.0.7', 'memory_leak', null, null, '', 'In LibTIFF 4.0.7, a memory leak vulnerability was found in the function TIFFReadDirEntryLong8Array in tif_dirread.c, which allows attackers to cause a denial of service via a crafted file.', 'http://bugzilla.maptools.org/show_bug.cgi?id=2689', 'CWE-772', '6.5', '4.3', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-9404', '1', 'libtiff', '', '4.0.7', 'memory_leak', null, null, '', 'In LibTIFF 4.0.7, a memory leak vulnerability was found in the function OJPEGReadHeaderInfoSecTablesQTable in tif_ojpeg.c, which allows attackers to cause a denial of service via a crafted file.', 'http://bugzilla.maptools.org/show_bug.cgi?id=2688', 'CWE-772', '6.5', '4.3', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-9412', '1', 'lame', '', '3.99.5', 'SEGV', 'unpack_read_samples', 'unpack_read_samples', '', 'The unpack_read_samples function in frontend/get_audio.c in LAME 3.99.5 allows remote attackers to cause a denial of service (invalid memory read and application crash) via a crafted wav file.', 'http://seclists.org/fulldisclosure/2017/Jul/63', 'CWE-119', '5.5', '4.3', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-9608', '1', 'ffmpeg', '', '', 'null_pointer_dereference', null, null, '', 'The dnxhd decoder in FFmpeg before 3.2.6, and 3.3.x before 3.3.3 allows remote attackers to cause a denial of service (NULL pointer dereference) via a crafted mov file.', 'https://github.com/FFmpeg/FFmpeg/commit/0a709e2a10b8288a0cc383547924ecfe285cef89###https://github.com/FFmpeg/FFmpeg/commit/31c1c0b46a7021802c3d1d18039fca30dba5a14e###https://github.com/FFmpeg/FFmpeg/commit/611b35627488a8d0763e75c25ee0875c5b7987dd', 'CWE-476', '6.5', '4.3', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2017-9742', '1', 'binutils', '', '', 'buffer-overflow', 'score_opcodes', 'score_opcodes', '', 'The score_opcodes function in opcodes/score7-dis.c in GNU Binutils 2.28 allows remote attackers to cause a denial of service (buffer overflow and application crash) or possibly have unspecified other impact via a crafted binary file, as demonstrated by mishandling of this file during \"objdump -D\" execution.', 'https://sourceware.org/bugzilla/show_bug.cgi?id=21576', 'CWE-119', '7.8', '6.8', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:H/I:H/A:H', 'AV:N/AC:M/Au:N/C:P/I:P/A:P');
