INSERT INTO `cve_general` VALUES ('CVE-2018-16335', '1', 'libtiff', '', '4.0.9', 'buffer-overflow', null, null, '', 'newoffsets handling in ChopUpSingleUncompressedStrip in tif_dirread.c in LibTIFF 4.0.9 allows remote attackers to cause a denial of service (heap-based buffer overflow and application crash) or possibly have unspecified other impact via a crafted TIFF file, as demonstrated by tiff2pdf. This is a different vulnerability than CVE-2018-15209.', 'http://bugzilla.maptools.org/show_bug.cgi?id=2809', 'CWE-119', '8.8', '6.8', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:R/S:U/C:H/I:H/A:H', 'AV:N/AC:M/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2018-16336', '1', 'exiv2', '', null, 'buffer-overflow', 'exiv2::internal::pngchunk::parsetxtchunk', 'parsetxtchunk', '', 'Exiv2::Internal::PngChunk::parseTXTChunk in Exiv2 v0.26 allows remote attackers to cause a denial of service (heap-based buffer over-read) via a crafted image file, a different vulnerability than CVE-2018-10999.', 'https://github.com/Exiv2/exiv2/issues/400', 'CWE-125', '6.5', '4.3', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2018-16368', '1', 'xpdf', '', '', 'buffer-overflow', 'splashxpath::strokeadjust', 'strokeadjust', '', 'SplashXPath::strokeAdjust in splash/SplashXPath.cc in Xpdf 4.00 allows remote attackers to cause a denial of service (heap-based buffer over-read) via a crafted pdf file, as demonstrated by pdftoppm.', 'https://github.com/TeamSeri0us/pocs/tree/master/xpdf', 'CWE-125', '5.5', '4.3', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2018-16369', '1', 'xpdf', '', '', 'stack-overflow', 'xref::fetch', 'fetch', '', 'XRef::fetch in XRef.cc in Xpdf 4.00 allows remote attackers to cause a denial of service (stack consumption) via a crafted pdf file, related to AcroForm::scanField, as demonstrated by pdftohtml. NOTE: this might overlap CVE-2018-7453.', 'https://github.com/TeamSeri0us/pocs/tree/master/xpdf', 'NVD-CWE-noinfo', '5.5', '4.3', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2018-16451', '1', 'tcpdump', '', '', 'buffer-overflow', 'print_trans', 'print_trans', '', 'The SMB parser in tcpdump before 4.9.3 has buffer over-reads in print-smb.c:print_trans() for \\MAILSLOT\\BROWSE and \\PIPE\\LANMAN.', 'https://github.com/the-tcpdump-group/tcpdump/commit/96480ab95308cd9234b4f09b175ebf60e17792c6###https://security.netapp.com/advisory/ntap-20200120-0001/###https://support.apple.com/kb/HT210788###https://github.com/the-tcpdump-group/tcpdump/blob/tcpdump-4.9/CHANGES', 'CWE-125', '7.5', '5', 'CVSS:3.1/AV:N/AC:L/PR:N/UI:N/S:U/C:N/I:N/A:H', 'AV:N/AC:L/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2018-16452', '1', 'tcpdump', '', '', 'stack-overflow', 'smb_fdata', 'smb_fdata', '', 'The SMB parser in tcpdump before 4.9.3 has stack exhaustion in smbutil.c:smb_fdata() via recursion.', 'https://github.com/the-tcpdump-group/tcpdump/commit/24182d959f661327525a20d9a94c98a8ec016778###https://security.netapp.com/advisory/ntap-20200120-0001/###https://support.apple.com/kb/HT210788###https://github.com/the-tcpdump-group/tcpdump/blob/tcpdump-4.9/CHANGES', 'CWE-674', '7.5', '5', 'CVSS:3.1/AV:N/AC:L/PR:N/UI:N/S:U/C:N/I:N/A:H', 'AV:N/AC:L/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2018-16554', '1', 'jhead', '', '3.00', null, 'processgpsinfo', 'processgpsinfo', '', 'The ProcessGpsInfo function of the gpsinfo.c file of jhead 3.00 may allow a remote attacker to cause a denial-of-service attack or unspecified other impact via a malicious JPEG file, because of inconsistency between float and double in a sprintf format string during TAG_GPS_ALT handling.', 'https://bugs.debian.org/cgi-bin/bugreport.cgi?bug=908176###https://nimo-zhang.github.io/2018/09/07/bug-analysis-1/#more', 'CWE-134', '7.8', '6.8', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:H/I:H/A:H', 'AV:N/AC:M/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2018-17000', '1', 'libtiff', '', '4.0.9', 'null_pointer_dereference', null, null, '', 'A NULL pointer dereference in the function _TIFFmemcmp at tif_unix.c (called from TIFFWriteDirectoryTagTransferfunction) in LibTIFF 4.0.9 allows an attacker to cause a denial-of-service through a crafted tiff file. This vulnerability can be triggered by the executable tiffcp.', 'http://bugzilla.maptools.org/show_bug.cgi?id=2811', 'CWE-476', '6.5', '4.3', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2018-17088', '1', 'jhead', '', '3.00', 'integer-overflow', 'processgpsinfo', 'processgpsinfo', '', 'The ProcessGpsInfo function of the gpsinfo.c file of jhead 3.00 may allow a remote attacker to cause a denial-of-service attack or unspecified other impact via a malicious JPEG file, because there is an integer overflow during a check for whether a location exceeds the EXIF data length. This is analogous to the CVE-2016-3822 integer overflow in exif.c. This gpsinfo.c vulnerability is unrelated to the CVE-2018-16554 gpsinfo.c vulnerability.', 'https://bugs.debian.org/cgi-bin/bugreport.cgi?bug=907925', 'CWE-190', '7.8', '6.8', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:H/I:H/A:H', 'AV:N/AC:M/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2018-17100', '1', 'libtiff', '', '4.0.9', 'integer-overflow', 'multiply_ms', 'multiply_ms', '', 'An issue was discovered in LibTIFF 4.0.9. There is a int32 overflow in multiply_ms in tools/ppm2tiff.c, which can cause a denial of service (crash) or possibly have unspecified other impact via a crafted image file.', 'http://bugzilla.maptools.org/show_bug.cgi?id=2810###https://gitlab.com/libtiff/libtiff/merge_requests/33/diffs?commit_id=6da1fb3f64d43be37e640efbec60400d1f1ac39e', 'CWE-190', '8.8', '6.8', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:R/S:U/C:H/I:H/A:H', 'AV:N/AC:M/Au:N/C:P/I:P/A:P');
