INSERT INTO `cve_general` VALUES ('CVE-2018-20505', '1', 'sqlite', '', '', null, null, null, '', 'SQLite 3.25.2, when queries are run on a table with a malformed PRIMARY KEY, allows remote attackers to cause a denial of service (application crash) by leveraging the ability to run arbitrary SQL statements (such as in certain WebSQL use cases).', 'https://security.netapp.com/advisory/ntap-20190502-0004/###http://seclists.org/fulldisclosure/2019/Jan/62###http://seclists.org/fulldisclosure/2019/Jan/64###http://seclists.org/fulldisclosure/2019/Jan/66###http://seclists.org/fulldisclosure/2019/Jan/67###http://seclists.org/fulldisclosure/2019/Jan/68###http://seclists.org/fulldisclosure/2019/Jan/69###https://seclists.org/bugtraq/2019/Jan/28###https://seclists.org/bugtraq/2019/Jan/29###https://seclists.org/bugtraq/2019/Jan/31###https://seclists.org/bugtraq/2019/Jan/32###https://seclists.org/bugtraq/2019/Jan/33###https://seclists.org/bugtraq/2019/Jan/39###https://sqlite.org/src/info/1a84668dcfdebaf12415d###https://support.apple.com/kb/HT209443###https://support.apple.com/kb/HT209446###https://support.apple.com/kb/HT209447###https://support.apple.com/kb/HT209448###https://support.apple.com/kb/HT209450###https://support.apple.com/kb/HT209451', 'CWE-89', '7.5', '5', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:N/S:U/C:N/I:N/A:H', 'AV:N/AC:L/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2018-20506', '1', 'sqlite', '', '', 'buffer-overflow', null, null, '', 'SQLite before 3.25.3, when the FTS3 extension is enabled, encounters an integer overflow (and resultant buffer overflow) for FTS3 queries in a \"merge\" operation that occurs after crafted changes to FTS3 shadow tables, allowing remote attackers to execute arbitrary code by leveraging the ability to run arbitrary SQL statements (such as in certain WebSQL use cases). This is a different vulnerability than CVE-2018-20346.', 'https://security.netapp.com/advisory/ntap-20190502-0004/###http://seclists.org/fulldisclosure/2019/Jan/62###http://seclists.org/fulldisclosure/2019/Jan/64###http://seclists.org/fulldisclosure/2019/Jan/66###http://seclists.org/fulldisclosure/2019/Jan/67###http://seclists.org/fulldisclosure/2019/Jan/68###http://seclists.org/fulldisclosure/2019/Jan/69###https://seclists.org/bugtraq/2019/Jan/28###https://seclists.org/bugtraq/2019/Jan/29###https://seclists.org/bugtraq/2019/Jan/31###https://seclists.org/bugtraq/2019/Jan/32###https://seclists.org/bugtraq/2019/Jan/33###https://seclists.org/bugtraq/2019/Jan/39###https://sqlite.org/src/info/940f2adc8541a838###https://support.apple.com/kb/HT209443###https://support.apple.com/kb/HT209446###https://support.apple.com/kb/HT209447###https://support.apple.com/kb/HT209448###https://support.apple.com/kb/HT209450###https://support.apple.com/kb/HT209451', 'CWE-190', '8.1', '6.8', 'CVSS:3.0/AV:N/AC:H/PR:N/UI:N/S:U/C:H/I:H/A:H', 'AV:N/AC:M/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2018-20570', '1', 'jasper', '', '2.0.14', 'buffer-overflow', null, null, '', 'jp2_encode in jp2/jp2_enc.c in JasPer 2.0.14 has a heap-based buffer over-read.', 'https://github.com/mdadams/jasper/issues/191', 'CWE-125', '6.5', '4.3', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
