INSERT INTO `cve_general` VALUES ('CVE-2018-20623', '1', 'binutils', '', '', 'use-after-free', 'error', 'error', '', 'In GNU Binutils 2.31.1, there is a use-after-free in the error function in elfcomm.c when called from the process_archive function in readelf.c via a crafted ELF file.', 'https://support.f5.com/csp/article/K38336243###https://sourceware.org/bugzilla/show_bug.cgi?id=24049', 'CWE-416', '5.5', '4.3', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2018-20651', '1', 'binutils', '', '', 'null_pointer_dereference', 'elf_link_add_object_symbols', 'elf_link_add_object_symbols', '', 'A NULL pointer dereference was discovered in elf_link_add_object_symbols in elflink.c in the Binary File Descriptor (BFD) library (aka libbfd), as distributed in GNU Binutils 2.31.1. This occurs for a crafted ET_DYN with no program headers. A specially crafted ELF file allows remote attackers to cause a denial of service, as demonstrated by ld.', 'https://support.f5.com/csp/article/K38336243###https://sourceware.org/bugzilla/show_bug.cgi?id=24041###https://sourceware.org/git/gitweb.cgi?p=binutils-gdb.git;h=54025d5812ff100f5f0654eb7e1ffd50f2e37f5f', 'CWE-476', '5.5', '4.3', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2018-20657', '1', 'binutils', '', '', 'memory_leak', 'demangle_template', 'demangle_template', '', 'The demangle_template function in cplus-dem.c in GNU libiberty, as distributed in GNU Binutils 2.31.1, has a memory leak via a crafted string, leading to a denial of service (memory consumption), as demonstrated by cxxfilt, a related issue to CVE-2018-12698.', 'https://support.f5.com/csp/article/K62602089###https://gcc.gnu.org/bugzilla/show_bug.cgi?id=88539', 'CWE-772', '7.5', '5', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:N/S:U/C:N/I:N/A:H', 'AV:N/AC:L/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2018-20659', '1', 'Bento4', '', '', 'excessive_memory_allocation', 'ap4_atomfactory::createatomfromstream', 'createatomfromstream', '', 'An issue was discovered in Bento4 1.5.1-627. The AP4_StcoAtom class in Core/Ap4StcoAtom.cpp has an attempted excessive memory allocation when called from AP4_AtomFactory::CreateAtomFromStream in Core/Ap4AtomFactory.cpp, as demonstrated by mp42hls.', 'https://github.com/axiomatic-systems/Bento4/issues/350', 'CWE-770', '6.5', '4.3', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2018-20671', '1', 'binutils', '', '', 'buffer-overflow', null, null, '', 'load_specific_debug_section in objdump.c in GNU Binutils through 2.31.1 contains an integer overflow vulnerability that can trigger a heap-based buffer overflow via a crafted section size.', 'https://sourceware.org/bugzilla/show_bug.cgi?id=24005###https://sourceware.org/git/gitweb.cgi?p=binutils-gdb.git;h=11fa9f134fd658075c6f74499c780df045d9e9ca', 'CWE-190', '5.5', '4.3', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2018-20673', '1', 'binutils', '', '', 'buffer-overflow', 'demangle_template', 'demangle_template', '', 'The demangle_template function in cplus-dem.c in GNU libiberty, as distributed in GNU Binutils 2.31.1, contains an integer overflow vulnerability (for \"Create an array for saving the template argument values\") that can trigger a heap-based buffer overflow, as demonstrated by nm.', 'https://sourceware.org/bugzilla/show_bug.cgi?id=24039', 'CWE-190', '5.5', '4.3', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2018-20712', '1', 'binutils', '', '', 'buffer-overflow', null, null, '', 'A heap-based buffer over-read exists in the function d_expression_1 in cp-demangle.c in GNU libiberty, as distributed in GNU Binutils 2.31.1. A crafted input can cause segmentation faults, leading to denial-of-service, as demonstrated by c++filt.', 'https://support.f5.com/csp/article/K38336243###https://gcc.gnu.org/bugzilla/show_bug.cgi?id=88629###https://sourceware.org/bugzilla/show_bug.cgi?id=24043', 'CWE-125', '6.5', '4.3', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2018-3854', '1', 'sqlite', '', '', '', '', '', '', 'An exploitable information disclosure vulnerability exists in the password protection functionality of Quicken Deluxe 2018 for Mac version 5.2.2. A specially crafted sqlite3 request can cause the removal of the password protection, allowing an attacker to access and modify the data without knowing the password. An attacker needs to have access to the password-protected files to trigger this vulnerability.', '', 'CWE-200', '7.1', '3.6', 'CVSS:3.0/AV:L/AC:L/PR:L/UI:N/S:U/C:H/I:H/A:N', 'AV:L/AC:L/Au:N/C:P/I:P/A:N');
