INSERT INTO `cve_general` VALUES ('CVE-2018-8740', '1', 'sqlite', '', '', 'null_pointer_dereference', null, null, '', 'In SQLite through 3.22.0, databases whose schema is corrupted using a CREATE TABLE AS statement could cause a NULL pointer dereference, related to build.c and prepare.c.', 'https://bugs.chromium.org/p/oss-fuzz/issues/detail?id=6964###https://bugs.launchpad.net/ubuntu/+source/sqlite3/+bug/1756349###https://www.sqlite.org/cgi/src/timeline?r=corrupt-schema###https://www.sqlite.org/cgi/src/vdiff?from=1774f1c3baf0bc3d&to=d75e67654aa9620b', 'CWE-476', '7.5', '5', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:N/S:U/C:N/I:N/A:H', 'AV:N/AC:L/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2018-8905', '1', 'libtiff', '', '4.0.9', 'buffer-overflow', null, null, '', 'In LibTIFF 4.0.9, a heap-based buffer overflow occurs in the function LZWDecodeCompat in tif_lzw.c via a crafted TIFF file, as demonstrated by tiff2ps.', 'https://gitlab.com/libtiff/libtiff/commit/58a898cb4459055bb488ca815c23b880c242a27d###http://bugzilla.maptools.org/show_bug.cgi?id=2780###https://github.com/halfbitteam/POCs/tree/master/libtiff-4.08_tiff2ps_heap_overflow', 'CWE-119', '8.8', '6.8', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:R/S:U/C:H/I:H/A:H', 'AV:N/AC:M/Au:N/C:P/I:P/A:P');
