INSERT INTO `cve_general` VALUES ('CVE-2018-9138', '1', 'binutils', '', '', 'stack-overflow', null, null, '', 'An issue was discovered in cplus-dem.c in GNU libiberty, as distributed in GNU Binutils 2.29 and 2.30. Stack Exhaustion occurs in the C++ demangling functions provided by libiberty, and there are recursive stack frames: demangle_nested_args, demangle_args, do_arg, and do_type.', 'https://sourceware.org/bugzilla/show_bug.cgi?id=23008', 'CWE-674', '5.5', '4.3', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2018-9144', '1', 'exiv2', '', '0.26', 'buffer-overflow', 'exiv2::internal::binarytostring', 'binarytostring', '', 'In Exiv2 0.26, there is an out-of-bounds read in Exiv2::Internal::binaryToString in image.cpp. It could result in denial of service or information disclosure.', 'https://github.com/Exiv2/exiv2/issues/254###https://github.com/xiaoqx/pocs/tree/master/exiv2', 'CWE-125', '8.1', '5.8', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:R/S:U/C:H/I:N/A:H', 'AV:N/AC:M/Au:N/C:P/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2018-9145', '1', 'exiv2', '', '0.26', 'SIGABRT', null, null, '', 'In the DataBuf class in include/exiv2/types.hpp in Exiv2 0.26, an issue exists in the constructor with an initial buffer size. A large size value may lead to a SIGABRT during an attempt at memory allocation. NOTE: some third parties have been unable to reproduce the SIGABRT when using the 4-DataBuf-abort-1 PoC file.', 'https://bugzilla.novell.com/show_bug.cgi?id=1087879###https://bugzilla.redhat.com/show_bug.cgi?id=1564281###https://github.com/xiaoqx/pocs/tree/master/exiv2', 'CWE-20', '6.5', '4.3', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2018-9154', '1', 'jasper', '', '2.0.14', 'assertion_failure', null, null, '', 'There is a reachable abort in the function jpc_dec_process_sot in libjasper/jpc/jpc_dec.c of JasPer 2.0.14 that will lead to a remote denial of service attack by triggering an unexpected jas_alloc2 return value, a different vulnerability than CVE-2017-13745.', 'https://drive.google.com/drive/u/2/folders/1YuxdfbZrw79kfzoQz0PpxIutZ7pkf_kW', 'CWE-20', '7.5', '5', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:N/S:U/C:N/I:N/A:H', 'AV:N/AC:L/Au:N/C:N/I:N/A:P');
