INSERT INTO `cve_general` VALUES ('CVE-2019-1010204', '1', 'binutils', '', '', 'buffer-overflow', null, null, '', 'GNU binutils gold gold v1.11-v1.16 (GNU binutils v2.21-v2.31.1) is affected by: Improper Input Validation, Signed/Unsigned Comparison, Out-of-bounds Read. The impact is: Denial of service. The component is: gold/fileread.cc:497, elfcpp/elfcpp_file.h:644. The attack vector is: An ELF file with an invalid e_shoff header field must be opened.', 'https://security.netapp.com/advisory/ntap-20190822-0001/###https://support.f5.com/csp/article/K05032915?utm_source=f5support&amp;utm_medium=RSS###https://sourceware.org/bugzilla/show_bug.cgi?id=23765', 'CWE-125/CWE-20', '5.5', '4.3', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2019-1010220', '1', 'tcpdump', '', '', 'buffer-overflow', null, null, '', 'tcpdump.org tcpdump 4.9.2 is affected by: CWE-126: Buffer Over-read. The impact is: May expose Saved Frame Pointer, Return Address etc. on stack. The component is: line 234: \"ND_PRINT((ndo, \"%s\", buf));\", in function named \"print_prefix\", in \"print-hncp.c\". The attack vector is: The victim must open a specially crafted pcap file.', 'https://github.com/the-tcpdump-group/tcpdump/blob/master/print-hncp.c###https://github.com/the-tcpdump-group/tcpdump/blob/tcpdump-4.9.2/print-hncp.c###https://github.com/the-tcpdump-group/tcpdump/commits/master/print-hncp.c', 'CWE-125', '3.3', '4.3', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:L/I:N/A:N', 'AV:N/AC:M/Au:N/C:P/I:N/A:N');
INSERT INTO `cve_general` VALUES ('CVE-2019-1010301', '1', 'jhead', '', '3.03', 'buffer-overflow', 'processgpsinfo', 'processgpsinfo', '', 'jhead 3.03 is affected by: Buffer Overflow. The impact is: Denial of service. The component is: gpsinfo.c Line 151 ProcessGpsInfo(). The attack vector is: Open a specially crafted JPEG file.', 'https://bugs.launchpad.net/ubuntu/+source/jhead/+bug/1838251###https://bugzilla.redhat.com/show_bug.cgi?id=1679952###https://launchpadlibrarian.net/435112680/32_crash_in_gpsinfo', 'CWE-119', '5.5', '4.3', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
