INSERT INTO `cve_general` VALUES ('CVE-2019-10752', '1', 'sqlite', '', '', '', '', '', '', 'Sequelize, all versions prior to version 4.44.3 and 5.15.1, is vulnerable to SQL Injection due to sequelize.json() helper function not escaping values properly when formatting sub paths for JSON queries for MySQL, MariaDB and SQLite.', '', 'CWE-89', '9.8', '7.5', 'CVSS:3.1/AV:N/AC:L/PR:N/UI:N/S:U/C:H/I:H/A:H', 'AV:N/AC:L/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2019-11338', '1', 'ffmpeg', '', '', 'null_pointer_dereference', null, null, '', 'libavcodec/hevcdec.c in FFmpeg 4.1.2 mishandles detection of duplicate first slices, which allows remote attackers to cause a denial of service (NULL pointer dereference and out-of-array access) or possibly have unspecified other impact via crafted HEVC data.', 'https://github.com/FFmpeg/FFmpeg/commit/54655623a82632e7624714d7b2a3e039dc5faa7e', 'CWE-476', '8.8', '6.8', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:R/S:U/C:H/I:H/A:H', 'AV:N/AC:M/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2019-11339', '1', 'ffmpeg', '', '', 'buffer-overflow', null, null, '', 'The studio profile decoder in libavcodec/mpeg4videodec.c in FFmpeg 4.0 before 4.0.4 and 4.1 before 4.1.2 allows remote attackers to cause a denial of service (out-of-array access) or possibly have unspecified other impact via crafted MPEG-4 video data.', 'https://github.com/FFmpeg/FFmpeg/commit/1f686d023b95219db933394a7704ad9aa5f01cbb###https://github.com/FFmpeg/FFmpeg/commit/d227ed5d598340e719eff7156b1aa0a4469e9a6a', 'CWE-125', '8.8', '6.8', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:R/S:U/C:H/I:H/A:H', 'AV:N/AC:M/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2019-11411', '1', 'mujs', '', '', 'stack-buffer-overflow', 'number#tofixed', 'number#tofixed', '', 'An issue was discovered in Artifex MuJS 1.0.5. The Number#toFixed() and numtostr implementations in jsnumber.c have a stack-based buffer overflow.', 'http://www.ghostscript.com/cgi-bin/findgit.cgi?da632ca08f240590d2dec786722ed08486ce1be6###https://bugs.ghostscript.com/show_bug.cgi?id=700938###https://github.com/ccxvii/mujs/commit/da632ca08f240590d2dec786722ed08486ce1be6', 'CWE-119', '9.8', '7.5', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:N/S:U/C:H/I:H/A:H', 'AV:N/AC:L/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2019-11412', '1', 'mujs', '', '', null, null, null, '', 'An issue was discovered in Artifex MuJS 1.0.5. jscompile.c can cause a denial of service (invalid stack-frame jump) because it lacks an ENDTRY opcode call.', 'http://www.ghostscript.com/cgi-bin/findgit.cgi?1e5479084bc9852854feb1ba9bf68b52cd127e02###https://bugs.ghostscript.com/show_bug.cgi?id=700947###https://github.com/ccxvii/mujs/commit/1e5479084bc9852854feb1ba9bf68b52cd127e02', 'CWE-119', '7.5', '5', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:N/S:U/C:N/I:N/A:H', 'AV:N/AC:L/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2019-11413', '1', 'mujs', '', '', 'stack-overflow', 'match', 'match', '', 'An issue was discovered in Artifex MuJS 1.0.5. It has unlimited recursion because the match function in regexp.c lacks a depth check.', 'http://www.ghostscript.com/cgi-bin/findgit.cgi?00d4606c3baf813b7b1c176823b2729bf51002a2###https://bugs.ghostscript.com/show_bug.cgi?id=700937###https://github.com/ccxvii/mujs/commit/00d4606c3baf813b7b1c176823b2729bf51002a2', 'CWE-400', '7.5', '5', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:N/S:U/C:N/I:N/A:H', 'AV:N/AC:L/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2019-12360', '1', 'xpdf', '', '', 'stack-buffer-overflow', 'fofitruetype::dumpstring', 'dumpstring', '', 'A stack-based buffer over-read exists in FoFiTrueType::dumpString in fofi/FoFiTrueType.cc in Xpdf 4.01.01. It can, for example, be triggered by sending crafted TrueType data in a PDF document to the pdftops tool. It might allow an attacker to cause Denial of Service or leak memory data into dump content.', 'https://forum.xpdfreader.com/viewtopic.php?f=3&t=41801', 'CWE-125', '7.1', '5.8', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:H/I:N/A:H', 'AV:N/AC:M/Au:N/C:P/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2019-12493', '1', 'xpdf', '', '', 'stack-buffer-overflow', 'postscriptfunction::transform', 'transform', '', 'A stack-based buffer over-read exists in PostScriptFunction::transform in Function.cc in Xpdf 4.01.01 because GfxSeparationColorSpace and GfxDeviceNColorSpace mishandle tint transform functions. It can, for example, be triggered by sending a crafted PDF document to the pdftops tool. It might allow an attacker to cause Denial of Service or leak memory data.', 'https://forum.xpdfreader.com/viewtopic.php?f=3&t=41806', 'CWE-125', '7.1', '5.8', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:H/I:N/A:H', 'AV:N/AC:M/Au:N/C:P/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2019-12515', '1', 'xpdf', '', '', 'buffer-overflow', 'flatestream::getchar', 'getchar', '', 'There is an out-of-bounds read vulnerability in the function FlateStream::getChar() located at Stream.cc in Xpdf 4.01.01. It can, for example, be triggered by sending a crafted PDF document to the pdftoppm tool. It might allow an attacker to cause Information Disclosure or a denial of service.', 'https://github.com/PanguL4b/pocs/tree/master/xpdf/out-of-bounds-read-in-FlateStream__getChar', 'CWE-125', '7.1', '5.8', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:H/I:N/A:H', 'AV:N/AC:M/Au:N/C:P/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2019-12730', '1', 'ffmpeg', '', '', 'uninitialized_memory_access', null, null, '', 'aa_read_header in libavformat/aadec.c in FFmpeg before 3.2.14 and 4.x before 4.1.4 does not check for sscanf failure and consequently allows use of uninitialized variables.', 'https://git.ffmpeg.org/gitweb/ffmpeg.git/commit/9b4004c054964a49c7ba44583f4cee22486dd8f2###https://git.ffmpeg.org/gitweb/ffmpeg.git/shortlog/n4.1.4###https://github.com/FFmpeg/FFmpeg/commit/ed188f6dcdf0935c939ed813cf8745d50742014b###https://github.com/FFmpeg/FFmpeg/compare/a97ea53...ba11e40', 'CWE-665', '9.8', '7.5', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:N/S:U/C:H/I:H/A:H', 'AV:N/AC:L/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2019-12798', '1', 'mujs', '', '', null, null, null, '', 'An issue was discovered in Artifex MuJS 1.0.5. regcompx in regexp.c does not restrict regular expression program size, leading to an overflow of the parsed syntax list size.', 'http://git.ghostscript.com/?p=mujs.git;h=7f50591861525f76e3ec7a63392656ff8c030af9', 'CWE-185', '9.8', '7.5', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:N/S:U/C:H/I:H/A:H', 'AV:N/AC:L/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2019-12957', '1', 'xpdf', '', '', 'information_leak', 'fofitype1c::converttotype1', 'converttotype1', '', 'In Xpdf 4.01.01, a buffer over-read could be triggered in FoFiType1C::convertToType1 in fofi/FoFiType1C.cc when the index number is larger than the charset array bounds. It can, for example, be triggered by sending a crafted PDF document to the pdftops tool. It allows an attacker to use a crafted pdf file to cause Denial of Service or an information leak, or possibly have unspecified other impact.', 'https://forum.xpdfreader.com/viewtopic.php?f=3&t=41813', 'CWE-125', '7.8', '6.8', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:H/I:H/A:H', 'AV:N/AC:M/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2019-12958', '1', 'xpdf', '', '', 'buffer-overflow', 'fofitype1c::converttotype0', 'converttotype0', '', 'In Xpdf 4.01.01, a heap-based buffer over-read could be triggered in FoFiType1C::convertToType0 in fofi/FoFiType1C.cc when it is trying to access the second privateDicts array element, because the privateDicts array has only one element allocated.', 'https://forum.xpdfreader.com/viewtopic.php?f=3&t=41815', 'CWE-125', '5.5', '4.3', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2019-12972', '1', 'binutils', '', '', 'buffer-overflow', '_bfd_doprnt', '_bfd_doprnt', '', 'An issue was discovered in the Binary File Descriptor (BFD) library (aka libbfd), as distributed in GNU Binutils 2.32. There is a heap-based buffer over-read in _bfd_doprnt in bfd.c because elf_object_p in elfcode.h mishandles an e_shstrndx section of type SHT_GROUP by omitting a trailing \'\\0\' character.', 'https://sourceware.org/bugzilla/show_bug.cgi?id=24689###https://sourceware.org/git/gitweb.cgi?p=binutils-gdb.git;h=890f750a3b053532a4b839a2dd6243076de12031', 'CWE-125', '5.5', '4.3', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2019-13108', '1', 'exiv2', '', '0.27.1', 'integer-overflow', 'pngimage::readmetadata', 'readmetadata', '', 'An integer overflow in Exiv2 through 0.27.1 allows an attacker to cause a denial of service (SIGSEGV) via a crafted PNG image file, because PngImage::readMetadata mishandles a zero value for iccOffset.', 'https://github.com/Exiv2/exiv2/issues/789###https://github.com/Exiv2/exiv2/pull/794', 'CWE-190', '6.5', '4.3', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2019-13109', '1', 'exiv2', '', '0.27.1', 'integer-overflow', 'pngimage::readmetadata', 'readmetadata', '', 'An integer overflow in Exiv2 through 0.27.1 allows an attacker to cause a denial of service (SIGSEGV) via a crafted PNG image file, because PngImage::readMetadata mishandles a chunkLength - iccOffset subtraction.', 'https://github.com/Exiv2/exiv2/issues/790###https://github.com/Exiv2/exiv2/pull/795', 'CWE-190', '6.5', '4.3', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2019-13110', '1', 'exiv2', '', '0.27.1', 'integer-overflow', 'ciffdirectory::readdirectory', 'readdirectory', '', 'A CiffDirectory::readDirectory integer overflow and out-of-bounds read in Exiv2 through 0.27.1 allows an attacker to cause a denial of service (SIGSEGV) via a crafted CRW image file.', 'https://github.com/Exiv2/exiv2/issues/843###https://github.com/Exiv2/exiv2/pull/844', 'CWE-190', '6.5', '4.3', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2019-13111', '1', 'exiv2', '', '0.27.1', 'integer-overflow', 'webpimage::decodechunks', 'decodechunks', '', 'A WebPImage::decodeChunks integer overflow in Exiv2 through 0.27.1 allows an attacker to cause a denial of service (large heap allocation followed by a very long running loop) via a crafted WEBP image file.', 'https://github.com/Exiv2/exiv2/issues/791###https://github.com/Exiv2/exiv2/pull/797', 'CWE-190', '5.5', '4.3', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2019-13112', '1', 'exiv2', '', '0.27.1', 'excessive_memory_allocation', 'pngchunk::parsechunkcontent', 'parsechunkcontent', '', 'A PngChunk::parseChunkContent uncontrolled memory allocation in Exiv2 through 0.27.1 allows an attacker to cause a denial of service (crash due to an std::bad_alloc exception) via a crafted PNG image file.', 'https://github.com/Exiv2/exiv2/issues/845###https://github.com/Exiv2/exiv2/pull/846', 'CWE-400', '6.5', '4.3', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2019-13113', '1', 'exiv2', '', '0.27.1', 'assertion_failure', null, null, '', 'Exiv2 through 0.27.1 allows an attacker to cause a denial of service (crash due to assertion failure) via an invalid data location in a CRW image file.', 'https://github.com/Exiv2/exiv2/issues/841###https://github.com/Exiv2/exiv2/pull/842', 'CWE-20', '6.5', '4.3', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2019-13114', '1', 'exiv2', '', '0.27.1', 'null_pointer_dereference', null, null, '', 'http.c in Exiv2 through 0.27.1 allows a malicious http server to cause a denial of service (crash due to a NULL pointer dereference) by returning a crafted response that lacks a space character.', 'https://support.f5.com/csp/article/K45429077?utm_source=f5support&amp;utm_medium=RSS###https://github.com/Exiv2/exiv2/issues/793###https://github.com/Exiv2/exiv2/pull/815', 'CWE-476', '6.5', '4.3', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2019-13238', '1', 'Bento4', '', '', 'null_pointer_dereference', null, null, '', 'An issue was discovered in Bento4 1.5.1.0. A memory allocation failure is unhandled in Core/Ap4SdpAtom.cpp and leads to crashes. When parsing input video, the program allocates a new buffer to parse an atom in the stream. The unhandled memory allocation failure causes a direct copy to a NULL pointer.', 'https://github.com/axiomatic-systems/Bento4/issues/396', 'CWE-400', '7.5', '5', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:N/S:U/C:N/I:N/A:H', 'AV:N/AC:L/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2019-13281', '1', 'xpdf', '', '', 'information_leak', 'dctstream::decodeimage', 'decodeimage', '', 'In Xpdf 4.01.01, a heap-based buffer overflow could be triggered in DCTStream::decodeImage() in Stream.cc when writing to frameBuf memory. It can, for example, be triggered by sending a crafted PDF document to the pdftotext tool. It allows an attacker to use a crafted pdf file to cause Denial of Service, an information leak, or possibly unspecified other impact.', 'https://forum.xpdfreader.com/viewtopic.php?f=3&t=41841', 'CWE-119', '7.8', '6.8', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:H/I:H/A:H', 'AV:N/AC:M/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2019-13282', '1', 'xpdf', '', '', 'information_leak', 'sampledfunction::transform', 'transform', '', 'In Xpdf 4.01.01, a heap-based buffer over-read could be triggered in SampledFunction::transform in Function.cc when using a large index for samples. It can, for example, be triggered by sending a crafted PDF document to the pdftotext tool. It allows an attacker to use a crafted pdf file to cause Denial of Service or an information leak, or possibly have unspecified other impact.', 'https://forum.xpdfreader.com/viewtopic.php?f=3&t=41842', 'CWE-125', '7.8', '6.8', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:H/I:H/A:H', 'AV:N/AC:M/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2019-13283', '1', 'xpdf', '', '', 'information_leak', 'fofitype1::parse', 'parse', '', 'In Xpdf 4.01.01, a heap-based buffer over-read could be triggered in strncpy from FoFiType1::parse in fofi/FoFiType1.cc because it does not ensure the source string has a valid length before making a fixed-length copy. It can, for example, be triggered by sending a crafted PDF document to the pdftotext tool. It allows an attacker to use a crafted pdf file to cause Denial of Service or an information leak, or possibly have unspecified other impact.', 'https://forum.xpdfreader.com/viewtopic.php?f=3&t=41843', 'CWE-119', '7.8', '6.8', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:H/I:H/A:H', 'AV:N/AC:M/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2019-13286', '1', 'xpdf', '', '', 'buffer-overflow', 'jbig2stream::readtextregionseg', 'readtextregionseg', '', 'In Xpdf 4.01.01, there is a heap-based buffer over-read in the function JBIG2Stream::readTextRegionSeg() located at JBIG2Stream.cc. It can, for example, be triggered by sending a crafted PDF document to the pdftoppm tool. It might allow an attacker to cause Information Disclosure.', 'https://github.com/PanguL4b/pocs/tree/master/xpdf/heap-buffer-overflow_JBIG2Stream__readTextRegionSeg', 'CWE-125', '5.5', '4.3', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:H/I:N/A:N', 'AV:N/AC:M/Au:N/C:P/I:N/A:N');
INSERT INTO `cve_general` VALUES ('CVE-2019-13287', '1', 'xpdf', '', '', 'buffer-overflow', 'splashxpath::strokeadjust', 'strokeadjust', '', 'In Xpdf 4.01.01, there is an out-of-bounds read vulnerability in the function SplashXPath::strokeAdjust() located at splash/SplashXPath.cc. It can, for example, be triggered by sending a crafted PDF document to the pdftoppm tool. It might allow an attacker to cause Information Disclosure. This is related to CVE-2018-16368.', 'https://github.com/PanguL4b/pocs/tree/master/xpdf/out-of-bounds-read-in-SplashXPath__strokeAdjust', 'CWE-125', '5.5', '4.3', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:H/I:N/A:N', 'AV:N/AC:M/Au:N/C:P/I:N/A:N');
INSERT INTO `cve_general` VALUES ('CVE-2019-13288', '1', 'xpdf', '', '', 'stack-overflow', 'parser::getobj', 'getobj', '', 'In Xpdf 4.01.01, the Parser::getObj() function in Parser.cc may cause infinite recursion via a crafted file. A remote attacker can leverage this for a DoS attack. This is similar to CVE-2018-16646.', 'https://github.com/PanguL4b/pocs/tree/master/xpdf/stack-overflow_dos_Parser__getObj', 'CWE-400', '5.5', '4.3', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2019-13289', '1', 'xpdf', '', '', 'use-after-free', 'jbig2stream::close', 'close', '', 'In Xpdf 4.01.01, there is a use-after-free vulnerability in the function JBIG2Stream::close() located at JBIG2Stream.cc. It can, for example, be triggered by sending a crafted PDF document to the pdftoppm tool.', 'https://github.com/PanguL4b/pocs/tree/master/xpdf/heap-use-after-free_JBIG2Stream', 'CWE-416', '7.8', '6.8', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:H/I:H/A:H', 'AV:N/AC:M/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2019-13291', '1', 'xpdf', '', '', 'buffer-overflow', 'dctstream::readscan', 'readscan', '', 'In Xpdf 4.01.01, there is a heap-based buffer over-read in the function DCTStream::readScan() located at Stream.cc. It can, for example, be triggered by sending a crafted PDF document to the pdftops tool. It might allow an attacker to cause Information Disclosure.', 'https://forum.xpdfreader.com/viewtopic.php?f=3&t=41818', 'CWE-119', '5.5', '4.3', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:H/I:N/A:N', 'AV:N/AC:M/Au:N/C:P/I:N/A:N');
INSERT INTO `cve_general` VALUES ('CVE-2019-13312', '1', 'ffmpeg', '', '', 'buffer-overflow', 'block_cmp', 'block_cmp', '', 'block_cmp() in libavcodec/zmbvenc.c in FFmpeg 4.1.3 has a heap-based buffer over-read.', 'https://trac.ffmpeg.org/ticket/7980', 'CWE-125', '8.8', '6.8', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:R/S:U/C:H/I:H/A:H', 'AV:N/AC:M/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2019-13390', '1', 'ffmpeg', '', '', 'FloatingPointException', null, null, '', 'In FFmpeg 4.1.3, there is a division by zero at adx_write_trailer in libavformat/rawenc.c.', 'https://trac.ffmpeg.org/ticket/7979', 'CWE-369', '6.5', '4.3', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2019-13504', '1', 'exiv2', '', '0.27.2', 'buffer-overflow', 'exiv2::mrwimage::readmetadata', 'readmetadata', '', 'There is an out-of-bounds read in Exiv2::MrwImage::readMetadata in mrwimage.cpp in Exiv2 through 0.27.2.', 'https://fuzzit.dev/2019/07/11/discovering-cve-2019-13504-cve-2019-13503-and-the-importance-of-api-fuzzing/###https://github.com/Exiv2/exiv2/pull/943', 'CWE-125', '6.5', '4.3', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2019-13734', '1', 'sqlite', '', '', 'memory_corruption', null, null, '', 'Out of bounds write in SQLite in Google Chrome prior to 79.0.3945.79 allowed a remote attacker to potentially exploit heap corruption via a crafted HTML page.', 'https://chromereleases.googleblog.com/2019/12/stable-channel-update-for-desktop.html###https://crbug.com/1025466', 'CWE-787', '8.8', '6.8', 'CVSS:3.1/AV:N/AC:L/PR:N/UI:R/S:U/C:H/I:H/A:H', 'AV:N/AC:M/Au:N/C:P/I:P/A:P');
