INSERT INTO `cve_general` VALUES ('CVE-2019-14973', '1', 'libtiff', '', '4.0.10', 'integer-overflow', null, null, '', '_TIFFCheckMalloc and _TIFFCheckRealloc in tif_aux.c in LibTIFF through 4.0.10 mishandle Integer Overflow checks because they rely on compiler behavior that is undefined by the applicable C standards. This can, for example, lead to an application crash.', 'https://gitlab.com/libtiff/libtiff/merge_requests/90###http://packetstormsecurity.com/files/155095/Slackware-Security-Advisory-libtiff-Updates.html', 'CWE-190', '6.5', '4.3', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
