INSERT INTO `cve_general` VALUES ('CVE-2019-15282', '1', 'tcpdump', '', '', '', '', '', '', 'A vulnerability in the web-based management interface of Cisco Identity Services Engine (ISE) Software could allow an unauthenticated, remote attacker read tcpdump files generated on an affected device. The vulnerability is due an issue in the authentication logic of the web-based management interface. An attacker could exploit this vulnerability by sending a crafted request to the web interface. A successful exploit could allow the attacker to read a tcpdump file generated with a particular naming scheme.', '', 'CWE-306', '5.3', '5', 'CVSS:3.1/AV:N/AC:L/PR:N/UI:N/S:U/C:L/I:N/A:N', 'AV:N/AC:L/Au:N/C:P/I:N/A:N');
INSERT INTO `cve_general` VALUES ('CVE-2019-15547', '1', 'ncurses', '', '', null, null, null, '', 'An issue was discovered in the ncurses crate through 5.99.0 for Rust. There are format string issues in printw functions because C format arguments are mishandled.', 'https://rustsec.org/advisories/RUSTSEC-2019-0006.html', 'CWE-134', '7.5', '6.4', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:N/S:U/C:N/I:H/A:N', 'AV:N/AC:L/Au:N/C:N/I:P/A:P');
