INSERT INTO `cve_general` VALUES ('CVE-2019-16927', '1', 'xpdf', '', '', 'buffer-overflow', 'textpage::findgaps', 'findgaps', '', 'Xpdf 4.01.01 has an out-of-bounds write in the vertProfile part of the TextPage::findGaps function in TextOutputDev.cc, a different vulnerability than CVE-2019-9877.', 'https://forum.xpdfreader.com/viewtopic.php?f=3&t=41885', 'CWE-787', '5.5', '4.3', 'CVSS:3.1/AV:L/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2019-17064', '1', 'xpdf', '', '', 'null_pointer_dereference', null, null, '', 'Catalog.cc in Xpdf 4.02 has a NULL pointer dereference because Catalog.pageLabels is initialized too late in the Catalog constructor.', 'http://packetstormsecurity.com/files/154713/Xpdf-4.02-NULL-Pointer-Dereference.html###https://forum.xpdfreader.com/viewtopic.php?f=3&t=41890', 'CWE-476', '5.5', '4.3', 'CVSS:3.1/AV:L/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2019-17402', '1', 'exiv2', '', '0.27.2', null, 'exiv2::getulong', 'getulong', '', 'Exiv2 0.27.2 allows attackers to trigger a crash in Exiv2::getULong in types.cpp when called from Exiv2::Internal::CiffDirectory::readDirectory in crwimage_int.cpp, because there is no validation of the relationship of the total size to the offset and size.', 'https://github.com/Exiv2/exiv2/issues/1019', 'CWE-120', '6.5', '4.3', 'CVSS:3.1/AV:N/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2019-17450', '1', 'binutils', '', '', 'stack-overflow', null, null, '', 'find_abstract_instance in dwarf2.c in the Binary File Descriptor (BFD) library (aka libbfd), as distributed in GNU Binutils 2.32, allows remote attackers to cause a denial of service (infinite recursion and application crash) via a crafted ELF file.', 'https://security.netapp.com/advisory/ntap-20191024-0002/###https://sourceware.org/bugzilla/show_bug.cgi?id=25078', 'CWE-674', '6.5', '4.3', 'CVSS:3.1/AV:N/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2019-17451', '1', 'binutils', '', '', 'integer-overflow', '_bfd_dwarf2_find_nearest_line', '_bfd_dwarf2_find_nearest_line', '', 'An issue was discovered in the Binary File Descriptor (BFD) library (aka libbfd), as distributed in GNU Binutils 2.32. It is an integer overflow leading to a SEGV in _bfd_dwarf2_find_nearest_line in dwarf2.c, as demonstrated by nm.', 'https://security.netapp.com/advisory/ntap-20191024-0002/###https://sourceware.org/bugzilla/show_bug.cgi?id=25070###https://sourceware.org/git/gitweb.cgi?p=binutils-gdb.git;h=336bfbeb1848f4b9558456fdcf283ee8a32d7fd1', 'CWE-190', '6.5', '4.3', 'CVSS:3.1/AV:N/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2019-17452', '1', 'Bento4', '', '', 'null_pointer_dereference', 'ap4_descriptorlistinspector::action', 'action', '', 'Bento4 1.5.1.0 has a NULL pointer dereference in AP4_DescriptorListInspector::Action in Core/Ap4Descriptor.h, related to AP4_IodsAtom::InspectFields in Core/Ap4IodsAtom.cpp, as demonstrated by mp4dump.', 'https://github.com/axiomatic-systems/Bento4/issues/434', 'CWE-476', '6.5', '4.3', 'CVSS:3.1/AV:N/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2019-17453', '1', 'Bento4', '', '', 'null_pointer_dereference', 'ap4_descriptorlistwriter::action', 'action', '', 'Bento4 1.5.1.0 has a NULL pointer dereference in AP4_DescriptorListWriter::Action in Core/Ap4Descriptor.h, related to AP4_IodsAtom::WriteFields in Core/Ap4IodsAtom.cpp, as demonstrated by mp4encrypt or mp4compact.', 'https://github.com/axiomatic-systems/Bento4/issues/436###https://github.com/axiomatic-systems/Bento4/issues/437', 'CWE-476', '6.5', '4.3', 'CVSS:3.1/AV:N/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
