INSERT INTO `cve_general` VALUES ('CVE-2019-17528', '1', 'Bento4', '', '', 'SEGV', 'ap4_tfhdatom::setdefaultsamplesize', 'setdefaultsamplesize', '', 'An issue was discovered in Bento4 1.5.1.0. There is a SEGV in the function AP4_TfhdAtom::SetDefaultSampleSize at Core/Ap4TfhdAtom.h when called from AP4_Processor::ProcessFragments in Core/Ap4Processor.cpp.', 'https://github.com/TeamSeri0us/pocs/tree/master/bento4###https://github.com/axiomatic-systems/Bento4/issues/432', 'CWE-119', '7.5', '4.3', 'CVSS:3.1/AV:N/AC:L/PR:N/UI:N/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2019-17529', '1', 'Bento4', '', '', 'buffer-overflow', 'ap4_cencsampleencryption::doinspectfields', 'doinspectfields', '', 'An issue was discovered in Bento4 1.5.1.0. There is a heap-based buffer over-read in AP4_CencSampleEncryption::DoInspectFields in Core/Ap4CommonEncryption.cpp when called from AP4_Atom::Inspect in Core/Ap4Atom.cpp.', 'https://github.com/TeamSeri0us/pocs/tree/master/bento4###https://github.com/axiomatic-systems/Bento4/issues/430', 'CWE-125', '7.8', '6.8', 'CVSS:3.1/AV:L/AC:L/PR:N/UI:R/S:U/C:H/I:H/A:H', 'AV:N/AC:M/Au:N/C:P/I:P/A:P');
