INSERT INTO `cve_general` VALUES ('CVE-2019-17539', '1', 'ffmpeg', '', '', 'null_pointer_dereference', 'close', 'close', '', 'In FFmpeg before 4.2, avcodec_open2 in libavcodec/utils.c allows a NULL pointer dereference and possibly unspecified other impact when there is no valid close function pointer.', 'https://bugs.chromium.org/p/oss-fuzz/issues/detail?id=15733###https://github.com/FFmpeg/FFmpeg/commit/8df6884832ec413cf032dfaa45c23b1c7876670c', 'CWE-476', '9.8', '7.5', 'CVSS:3.1/AV:N/AC:L/PR:N/UI:N/S:U/C:H/I:H/A:H', 'AV:N/AC:L/Au:N/C:P/I:P/A:P');
