INSERT INTO `cve_general` VALUES ('CVE-2019-17546', '1', 'libtiff', '', '4.0.10', 'integer-overflow', null, null, '', 'tif_getimage.c in LibTIFF through 4.0.10, as used in GDAL through 3.0.1 and other products, has an integer overflow that potentially causes a heap-based buffer overflow via a crafted RGBA image, related to a \"Negative-size-param\" condition.', 'https://bugs.chromium.org/p/oss-fuzz/issues/detail?id=16443###https://github.com/OSGeo/gdal/commit/21674033ee246f698887604c7af7ba1962a40ddf###https://gitlab.com/libtiff/libtiff/commit/4bb584a35f87af42d6cf09d15e9ce8909a839145', 'CWE-190', '8.8', '6.8', 'CVSS:3.1/AV:N/AC:L/PR:N/UI:R/S:U/C:H/I:H/A:H', 'AV:N/AC:M/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2019-17594', '1', 'ncurses', '', '', 'buffer-overflow', '_nc_find_entry', '_nc_find_entry', '', 'There is a heap-based buffer over-read in the _nc_find_entry function in tinfo/comp_hash.c in the terminfo library in ncurses before 6.1-20191012.', 'https://lists.gnu.org/archive/html/bug-ncurses/2019-10/msg00017.html###https://lists.gnu.org/archive/html/bug-ncurses/2019-10/msg00045.html', 'CWE-125', '5.3', '4.6', 'CVSS:3.1/AV:L/AC:L/PR:L/UI:N/S:U/C:L/I:L/A:L', 'AV:L/AC:L/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2019-17595', '1', 'ncurses', '', '', 'buffer-overflow', 'fmt_entry', 'fmt_entry', '', 'There is a heap-based buffer over-read in the fmt_entry function in tinfo/comp_hash.c in the terminfo library in ncurses before 6.1-20191012.', 'https://lists.gnu.org/archive/html/bug-ncurses/2019-10/msg00013.html###https://lists.gnu.org/archive/html/bug-ncurses/2019-10/msg00045.html', 'CWE-125', '5.4', '5.8', 'CVSS:3.1/AV:N/AC:L/PR:N/UI:R/S:U/C:L/I:N/A:L', 'AV:N/AC:M/Au:N/C:P/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2019-18214', '1', 'ffmpeg', '', '', 'excessive_memory_allocation', null, null, '', 'The Video_Converter app 0.1.0 for Nextcloud allows denial of service (CPU and memory consumption) via multiple concurrent conversions because many FFmpeg processes may be running at once. (The workload is not queued for serial execution.)', 'https://github.com/PaulLereverend/NextcloudVideo_Converter/issues/22', 'CWE-772', '7.7', '6.8', 'CVSS:3.1/AV:N/AC:L/PR:L/UI:N/S:C/C:N/I:N/A:H', 'AV:N/AC:L/Au:S/C:N/I:N/A:C');
INSERT INTO `cve_general` VALUES ('CVE-2019-18359', '1', 'mp3gain', '', '1.6.2', 'buffer-overflow', null, null, '', 'A buffer over-read was discovered in ReadMP3APETag in apetag.c in MP3Gain 1.6.2. The vulnerability causes an application crash, which leads to remote denial of service.', 'https://sourceforge.net/p/mp3gain/bugs/46/', 'CWE-119', '5.5', '4.3', 'CVSS:3.1/AV:L/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2019-19035', '1', 'jhead', '', '3.03', 'buffer-overflow', null, null, '', 'jhead 3.03 is affected by: heap-based buffer over-read. The impact is: Denial of service. The component is: ReadJpegSections and process_SOFn in jpgfile.c. The attack vector is: Open a specially crafted JPEG file.', 'https://bugzilla.redhat.com/show_bug.cgi?id=1765647', 'CWE-125', '5.5', '4.3', 'CVSS:3.1/AV:L/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
