INSERT INTO `cve_general` VALUES ('CVE-2019-19959', '1', 'sqlite', '', '', null, null, null, '', 'ext/misc/zipfile.c in SQLite 3.30.1 mishandles certain uses of INSERT INTO in situations involving embedded \'\\0\' characters in filenames, leading to a memory-management error that can be detected by (for example) valgrind.', 'https://github.com/sqlite/sqlite/commit/1e490c4ca6b43a9cf8637d695907888349f69bec###https://github.com/sqlite/sqlite/commit/d8f2d46cbc9925e034a68aaaf60aad788d9373c1', 'NVD-CWE-Other', '7.5', '5', 'CVSS:3.1/AV:N/AC:L/PR:N/UI:N/S:U/C:N/I:H/A:N', 'AV:N/AC:L/Au:N/C:N/I:P/A:N');
