INSERT INTO `cve_general` VALUES ('CVE-2019-20091', '1', 'Bento4', '', '', 'null_pointer_dereference', 'ap4_descriptor::gettag', 'gettag', '', 'An issue was discovered in Bento4 1.5.1.0. There is a NULL pointer dereference in AP4_Descriptor::GetTag in mp42ts when called from AP4_DecoderConfigDescriptor::GetDecoderSpecificInfoDescriptor in Ap4DecoderConfigDescriptor.cpp.', 'https://github.com/axiomatic-systems/Bento4/issues/462', 'CWE-476', '5.5', '4.3', 'CVSS:3.1/AV:L/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2019-20092', '1', 'Bento4', '', '', 'null_pointer_dereference', 'ap4_descriptor::gettag', 'gettag', '', 'An issue was discovered in Bento4 1.5.1.0. There is a NULL pointer dereference in AP4_Descriptor::GetTag in mp42ts when called from AP4_EsDescriptor::GetDecoderConfigDescriptor in Ap4EsDescriptor.cpp.', 'https://github.com/axiomatic-systems/Bento4/issues/462', 'CWE-476', '5.5', '4.3', 'CVSS:3.1/AV:L/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2019-20218', '1', 'sqlite', '', '', null, null, null, '', 'selectExpander in select.c in SQLite 3.30.1 proceeds with WITH stack unwinding even after a parsing error.', 'https://github.com/sqlite/sqlite/commit/a6c1a71cde082e09750465d5675699062922e387', 'CWE-755/NVD-CWE-Other', '7.5', '5', 'CVSS:3.1/AV:N/AC:L/PR:N/UI:N/S:U/C:N/I:N/A:H', 'AV:N/AC:L/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2019-20421', '1', 'exiv2', '', '0.27.2', 'infinite_loop', 'jp2image::readmetadata', 'readmetadata', '', 'In Jp2Image::readMetadata() in jp2image.cpp in Exiv2 0.27.2, an input file can result in an infinite loop and hang, with high CPU consumption. Remote attackers could leverage this vulnerability to cause a denial of service via a crafted file.', 'https://github.com/Exiv2/exiv2/commit/a82098f4f90cd86297131b5663c3dec6a34470e8###https://github.com/Exiv2/exiv2/issues/1011', 'CWE-835', '6.5', '7.1', 'CVSS:3.1/AV:N/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:C');
INSERT INTO `cve_general` VALUES ('CVE-2019-5018', '1', 'sqlite', '', '', 'use-after-free', 'window', 'window', '', 'An exploitable use after free vulnerability exists in the window function functionality of Sqlite3 3.26.0. A specially crafted SQL command can cause a use after free vulnerability, potentially resulting in remote code execution. An attacker can send a malicious SQL command to trigger this vulnerability.', 'https://security.netapp.com/advisory/ntap-20190521-0001/###http://packetstormsecurity.com/files/152809/Sqlite3-Window-Function-Remote-Code-Execution.html###https://talosintelligence.com/vulnerability_reports/TALOS-2019-0777', 'CWE-416', '8.1', '6.8', 'CVSS:3.0/AV:N/AC:H/PR:N/UI:N/S:U/C:H/I:H/A:H', 'AV:N/AC:M/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2019-5827', '1', 'sqlite', '', '', 'memory_corruption', null, null, '', 'Integer overflow in SQLite via WebSQL in Google Chrome prior to 74.0.3729.131 allowed a remote attacker to potentially exploit heap corruption via a crafted HTML page.', 'https://chromereleases.googleblog.com/2019/04/stable-channel-update-for-desktop_30.html###https://crbug.com/952406', 'CWE-190', '8.8', '6.8', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:R/S:U/C:H/I:H/A:H', 'AV:N/AC:M/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2019-6128', '1', 'libtiff', '', '4.0.10', 'memory_leak', 'tifffdopen', 'tifffdopen', '', 'The TIFFFdOpen function in tif_unix.c in LibTIFF 4.0.10 has a memory leak, as demonstrated by pal2rgb.', 'https://gitlab.com/libtiff/libtiff/commit/0c74a9f49b8d7a36b17b54a7428b3526d20f88a8###http://bugzilla.maptools.org/show_bug.cgi?id=2836###http://packetstormsecurity.com/files/155095/Slackware-Security-Advisory-libtiff-Updates.html', 'CWE-399', '8.8', '6.8', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:R/S:U/C:H/I:H/A:H', 'AV:N/AC:M/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2019-6132', '1', 'Bento4', '', '', 'memory_leak', 'ap4_descriptorfactory::createdescriptorfromstream', 'createdescriptorfromstream', '', 'An issue was discovered in Bento4 v1.5.1-627. There is a memory leak in AP4_DescriptorFactory::CreateDescriptorFromStream in Core/Ap4DescriptorFactory.cpp when called from the AP4_EsdsAtom class in Core/Ap4EsdsAtom.cpp, as demonstrated by mp42aac.', 'https://github.com/axiomatic-systems/Bento4/issues/357', 'CWE-119', '7.5', '5', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:N/S:U/C:N/I:N/A:H', 'AV:N/AC:L/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2019-6966', '1', 'Bento4', '', '', 'excessive_memory_allocation', 'ap4_array<ap4_elstentry>::ensurecapacity', 'ensurecapacity', '', 'An issue was discovered in Bento4 1.5.1-628. The AP4_ElstAtom class in Core/Ap4ElstAtom.cpp has an attempted excessive memory allocation related to AP4_Array<AP4_ElstEntry>::EnsureCapacity in Core/Ap4Array.h, as demonstrated by mp42hls.', 'https://github.com/axiomatic-systems/Bento4/issues/361', 'CWE-400', '6.5', '4.3', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2019-7663', '1', 'libtiff', '', '4.0.10', 'SEGV', 'cpseparatebuftocontigbuf', 'cpseparatebuftocontigbuf', '', 'An Invalid Address dereference was discovered in TIFFWriteDirectoryTagTransferfunction in libtiff/tif_dirwrite.c in LibTIFF 4.0.10, affecting the cpSeparateBufToContigBuf function in tiffcp.c. Remote attackers could leverage this vulnerability to cause a denial-of-service via a crafted tiff file. This is different from CVE-2018-12900.', 'https://gitlab.com/libtiff/libtiff/commit/802d3cbf3043be5dce5317e140ccb1c17a6a2d39###http://bugzilla.maptools.org/show_bug.cgi?id=2833', 'CWE-119', '6.5', '4.3', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2019-7697', '1', 'Bento4', '', '', 'assertion_failure', 'ap4_atomlistwriter::action', 'action', '', 'An issue was discovered in Bento4 v1.5.1-627. There is an assertion failure in AP4_AtomListWriter::Action in Core/Ap4Atom.cpp, leading to a denial of service (program crash), as demonstrated by mp42hls.', 'https://github.com/axiomatic-systems/Bento4/issues/351', 'CWE-20', '6.5', '4.3', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2019-7698', '1', 'Bento4', '', '', 'excessive_memory_allocation', 'ap4_array<ap4_cttstableentry>::ensurecapacity', 'ensurecapacity', '', 'An issue was discovered in AP4_Array<AP4_CttsTableEntry>::EnsureCapacity in Core/Ap4Array.h in Bento4 1.5.1-627. Crafted MP4 input triggers an attempt at excessive memory allocation, as demonstrated by mp42hls, a related issue to CVE-2018-20095.', 'https://github.com/axiomatic-systems/Bento4/issues/354', 'CWE-400', '6.5', '4.3', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2019-7699', '1', 'Bento4', '', '', 'buffer-overflow', 'ap4_bitstream::writebytes', 'writebytes', '', 'A heap-based buffer over-read occurs in AP4_BitStream::WriteBytes in Codecs/Ap4BitStream.cpp in Bento4 v1.5.1-627. Remote attackers could leverage this vulnerability to cause an exception via crafted mp4 input, which leads to a denial of service.', 'https://github.com/axiomatic-systems/Bento4/issues/355', 'CWE-119', '6.5', '4.3', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2019-8378', '1', 'Bento4', '', '', 'buffer-overflow', 'ap4_bitstream::readbytes', 'readbytes', '', 'An issue was discovered in Bento4 1.5.1-628. A heap-based buffer over-read exists in AP4_BitStream::ReadBytes() in Codecs/Ap4BitStream.cpp, a similar issue to CVE-2017-14645. It can be triggered by sending a crafted file to the aac2mp4 binary. It allows an attacker to cause a Denial of Service (Segmentation fault) or possibly have unspecified other impact.', 'https://github.com/axiomatic-systems/Bento4/issues/363###https://research.loginsoft.com/bugs/a-heap-buffer-overflow-vulnerability-in-the-function-ap4_bitstreamreadbytes-bento4-1-5-1-628/', 'CWE-125', '8.8', '6.8', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:R/S:U/C:H/I:H/A:H', 'AV:N/AC:M/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2019-8380', '1', 'Bento4', '', '', 'null_pointer_dereference', 'ap4_track::getsampleindexfortimestampms', 'getsampleindexfortimestampms', '', 'An issue was discovered in Bento4 1.5.1-628. A NULL pointer dereference occurs in AP4_Track::GetSampleIndexForTimeStampMs() located in Core/Ap4Track.cpp. It can triggered by sending a crafted file to the mp4audioclip binary. It allows an attacker to cause a Denial of Service (Segmentation fault) or possibly have unspecified other impact.', 'https://github.com/axiomatic-systems/Bento4/issues/366###https://research.loginsoft.com/bugs/null-pointer-dereference-vulnerability-in-function-ap4_trackgetsampleindexfortimestampms-bento4-1-5-1-628/', 'CWE-476', '8.8', '6.8', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:R/S:U/C:H/I:H/A:H', 'AV:N/AC:M/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2019-8382', '1', 'Bento4', '', '', 'null_pointer_dereference', null, null, '', 'An issue was discovered in Bento4 1.5.1-628. A NULL pointer dereference occurs in the function AP4_List:Find located in Core/Ap4List.h when called from Core/Ap4Movie.cpp. It can be triggered by sending a crafted file to the mp4dump binary. It allows an attacker to cause a Denial of Service (Segmentation fault) or possibly have unspecified other impact.', 'https://github.com/axiomatic-systems/Bento4/issues/364###https://research.loginsoft.com/bugs/null-pointer-dereference-vulnerability-in-function-ap4_listfind-bento4-1-5-1-628/', 'CWE-476', '8.8', '6.8', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:R/S:U/C:H/I:H/A:H', 'AV:N/AC:M/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2019-8457', '1', 'sqlite', '', '', null, 'rtreenode', 'rtreenode', '', 'SQLite3 from 3.6.0 to and including 3.27.2 is vulnerable to heap out-of-bound read in the rtreenode() function when handling invalid rtree tables.', 'https://security.netapp.com/advisory/ntap-20190606-0002/###https://www.oracle.com/security-alerts/cpujan2020.html###https://www.oracle.com/technetwork/security-advisory/cpuoct2019-5072832.html###https://www.sqlite.org/releaselog/3_28_0.html###https://www.sqlite.org/src/info/90acdbfce9c08858', 'CWE-125', '9.8', '7.5', 'CVSS:3.1/AV:N/AC:L/PR:N/UI:N/S:U/C:H/I:H/A:H', 'AV:N/AC:L/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2019-9070', '1', 'binutils', '', '', 'buffer-overflow', 'd_expression_1', 'd_expression_1', '', 'An issue was discovered in GNU libiberty, as distributed in GNU Binutils 2.32. It is a heap-based buffer over-read in d_expression_1 in cp-demangle.c after many recursive calls.', 'https://security.netapp.com/advisory/ntap-20190314-0003/###https://support.f5.com/csp/article/K13534168###https://gcc.gnu.org/bugzilla/show_bug.cgi?id=89395###https://sourceware.org/bugzilla/show_bug.cgi?id=24229', 'CWE-125', '7.8', '6.8', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:H/I:H/A:H', 'AV:N/AC:M/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2019-9071', '1', 'binutils', '', '', 'stack-overflow', 'd_count_templates_scopes', 'd_count_templates_scopes', '', 'An issue was discovered in GNU libiberty, as distributed in GNU Binutils 2.32. It is a stack consumption issue in d_count_templates_scopes in cp-demangle.c after many recursive calls.', 'https://security.netapp.com/advisory/ntap-20190314-0003/###https://support.f5.com/csp/article/K02884135###https://gcc.gnu.org/bugzilla/show_bug.cgi?id=89394###https://sourceware.org/bugzilla/show_bug.cgi?id=24227', 'CWE-399', '5.5', '4.3', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
