INSERT INTO `cve_general` VALUES ('CVE-2019-9074', '1', 'binutils', '', '', 'buffer-overflow', 'bfd_getl32', 'bfd_getl32', '', 'An issue was discovered in the Binary File Descriptor (BFD) library (aka libbfd), as distributed in GNU Binutils 2.32. It is an out-of-bounds read leading to a SEGV in bfd_getl32 in libbfd.c, when called from pex64_get_runtime_function in pei-x86_64.c.', 'https://security.netapp.com/advisory/ntap-20190314-0003/###https://support.f5.com/csp/article/K09092524###https://sourceware.org/bugzilla/show_bug.cgi?id=24235', 'CWE-125', '5.5', '4.3', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2019-9075', '1', 'binutils', '', '', 'buffer-overflow', '_bfd_archive_64_bit_slurp_armap', '_bfd_archive_64_bit_slurp_armap', '', 'An issue was discovered in the Binary File Descriptor (BFD) library (aka libbfd), as distributed in GNU Binutils 2.32. It is a heap-based buffer overflow in _bfd_archive_64_bit_slurp_armap in archive64.c.', 'https://security.netapp.com/advisory/ntap-20190314-0003/###https://support.f5.com/csp/article/K42059040###https://sourceware.org/bugzilla/show_bug.cgi?id=24236', 'CWE-119', '7.8', '6.8', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:H/I:H/A:H', 'AV:N/AC:M/Au:N/C:P/I:P/A:P');
