INSERT INTO `cve_general` VALUES ('CVE-2019-9077', '1', 'binutils', '', '', 'buffer-overflow', 'process_mips_specific', 'process_mips_specific', '', 'An issue was discovered in GNU Binutils 2.32. It is a heap-based buffer overflow in process_mips_specific in readelf.c via a malformed MIPS option section.', 'https://security.netapp.com/advisory/ntap-20190314-0003/###https://support.f5.com/csp/article/K00056379###https://sourceware.org/bugzilla/show_bug.cgi?id=24243', 'CWE-119', '7.8', '6.8', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:H/I:H/A:H', 'AV:N/AC:M/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2019-9143', '1', 'exiv2', '', '0.27', 'SEGV', 'exiv2::image::printtiffstructure', 'printtiffstructure', '', 'An issue was discovered in Exiv2 0.27. There is infinite recursion at Exiv2::Image::printTiffStructure in the file image.cpp. This can be triggered by a crafted file. It allows an attacker to cause Denial of Service (Segmentation fault) or possibly have unspecified other impact.', 'https://github.com/Exiv2/exiv2/issues/711###https://research.loginsoft.com/bugs/uncontrolled-recursion-loop-in-exiv2imageprinttiffstructure-exiv2-0-27/', 'CWE-400', '8.8', '6.8', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:R/S:U/C:H/I:H/A:H', 'AV:N/AC:M/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2019-9144', '1', 'exiv2', '', '0.27', 'SEGV', 'bigtiffimage::printifd', 'printifd', '', 'An issue was discovered in Exiv2 0.27. There is infinite recursion at BigTiffImage::printIFD in the file bigtiffimage.cpp. This can be triggered by a crafted file. It allows an attacker to cause Denial of Service (Segmentation fault) or possibly have unspecified other impact.', 'https://github.com/Exiv2/exiv2/issues/712###https://research.loginsoft.com/bugs/uncontrolled-recursion-loop-in-exiv2anonymous-namespacebigtiffimageprintifd-exiv2-0-27/', 'CWE-400', '8.8', '6.8', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:R/S:U/C:H/I:H/A:H', 'AV:N/AC:M/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2019-9544', '1', 'Bento4', '', '', 'buffer-overflow', 'ap4_cttstableentry::ap4_cttstableentry', 'ap4_cttstableentry', '', 'An issue was discovered in Bento4 1.5.1-628. An out of bounds write occurs in AP4_CttsTableEntry::AP4_CttsTableEntry() located in Core/Ap4Array.h. It can be triggered by sending a crafted file to (for example) the mp42hls binary. It allows an attacker to cause Denial of Service (Segmentation fault) or possibly have unspecified other impact.', 'https://github.com/axiomatic-systems/Bento4/issues/374###https://research.loginsoft.com/bugs/out-of-bounds-write-in-function-ap4_cttstableentryap4_cttstableentry-bento4-1-5-1-0/', 'CWE-787', '8.8', '6.8', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:R/S:U/C:H/I:H/A:H', 'AV:N/AC:M/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2019-9587', '1', 'xpdf', '', '', 'stack-overflow', 'md5round1', 'md5round1', '', 'There is a stack consumption issue in md5Round1() located in Decrypt.cc in Xpdf 4.01. It can be triggered by sending a crafted pdf file to (for example) the pdfimages binary. It allows an attacker to cause Denial of Service (Segmentation fault) or possibly have unspecified other impact. This is related to Catalog::countPageTree.', 'https://forum.xpdfreader.com/viewtopic.php?f=3&t=41263###https://research.loginsoft.com/bugs/stack-based-buffer-overflow-vulnerability-in-function-md5round1-xpdf-4-01/', 'CWE-400', '7.8', '6.8', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:H/I:H/A:H', 'AV:N/AC:M/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2019-9588', '1', 'xpdf', '', '', 'SEGV', 'gatomicincrement', 'gatomicincrement', '', 'There is an Invalid memory access in gAtomicIncrement() located at GMutex.h in Xpdf 4.01. It can be triggered by sending a crafted pdf file to (for example) the pdftops binary. It allows an attacker to cause Denial of Service (Segmentation fault) or possibly have unspecified other impact.', 'https://forum.xpdfreader.com/viewtopic.php?f=3&t=41261###https://research.loginsoft.com/bugs/invalid-memory-access-in-gatomiccounter-gatomicincrement-xpdf-4-01/', 'CWE-119', '7.8', '6.8', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:H/I:H/A:H', 'AV:N/AC:M/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2019-9589', '1', 'xpdf', '', '', 'null_pointer_dereference', 'psoutputdev::setupresources', 'setupresources', '', 'There is a NULL pointer dereference vulnerability in PSOutputDev::setupResources() located in PSOutputDev.cc in Xpdf 4.01. It can be triggered by sending a crafted pdf file to (for example) the pdftops binary. It allows an attacker to cause Denial of Service (Segmentation fault) or possibly have unspecified other impact.', 'https://forum.xpdfreader.com/viewtopic.php?f=3&t=41262###https://research.loginsoft.com/bugs/null-pointer-dereference-vulnerability-in-function-psoutputdevsetupresources-xpdf-4-01/', 'CWE-476', '7.8', '6.8', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:H/I:H/A:H', 'AV:N/AC:M/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2019-9718', '1', 'ffmpeg', '', '', null, null, null, '', 'In FFmpeg 4.1, a denial of service in the subtitle decoder allows attackers to hog the CPU via a crafted video file in Matroska format, because ff_htmlmarkup_to_ass in libavcodec/htmlsubtitles.c has a complex format argument to sscanf.', 'https://git.ffmpeg.org/gitweb/ffmpeg.git/commit/1f00c97bc3475c477f3c468cf2d924d5761d0982', 'CWE-125', '6.5', '4.3', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2019-9721', '1', 'ffmpeg', '', '', null, null, null, '', 'A denial of service in the subtitle decoder in FFmpeg 4.1 allows attackers to hog the CPU via a crafted video file in Matroska format, because handle_open_brace in libavcodec/htmlsubtitles.c has a complex format argument to sscanf.', 'https://git.ffmpeg.org/gitweb/ffmpeg.git/commit/894995c41e0795c7a44f81adc4838dedc3932e65', 'CWE-125', '6.5', '4.3', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:R/S:U/C:N/I:N/A:H', 'AV:N/AC:M/Au:N/C:N/I:N/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2019-9877', '1', 'xpdf', '', '', 'SEGV', 'textpage::findgaps', 'findgaps', '', 'There is an invalid memory access vulnerability in the function TextPage::findGaps() located at TextOutputDev.c in Xpdf 4.01, which can (for example) be triggered by sending a crafted pdf file to the pdftops binary. It allows an attacker to cause Denial of Service (Segmentation fault) or possibly have unspecified other impact.', 'https://forum.xpdfreader.com/viewtopic.php?f=3&t=41265###https://research.loginsoft.com/bugs/invalid-memory-access-in-textpagefindgaps-xpdf-4-01/', 'CWE-119', '7.8', '6.8', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:H/I:H/A:H', 'AV:N/AC:M/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2019-9878', '1', 'xpdf', '', '', 'SEGV', 'gfxindexedcolorspace::mapcolortobase', 'mapcolortobase', '', 'There is an invalid memory access in the function GfxIndexedColorSpace::mapColorToBase() located in GfxState.cc in Xpdf 4.0.0, as used in pdfalto 0.2. It can be triggered by (for example) sending a crafted pdf file to the pdftops binary. It allows an attacker to cause Denial of Service (Segmentation fault) or possibly have unspecified other impact.', 'https://github.com/kermitt2/pdfalto/issues/46###https://research.loginsoft.com/bugs/invalid-memory-access-in-gfxindexedcolorspacemapcolortobase-pdfalto-0-2/', 'CWE-119', '7.8', '6.8', 'CVSS:3.0/AV:L/AC:L/PR:N/UI:R/S:U/C:H/I:H/A:H', 'AV:N/AC:M/Au:N/C:P/I:P/A:P');
INSERT INTO `cve_general` VALUES ('CVE-2019-9936', '1', 'sqlite', '', '', 'information_leak', null, null, '', 'In SQLite 3.27.2, running fts5 prefix queries inside a transaction could trigger a heap-based buffer over-read in fts5HashEntrySort in sqlite3.c, which may lead to an information leak. This is related to ext/fts5/fts5_hash.c.', 'https://security.netapp.com/advisory/ntap-20190416-0005/###https://sqlite.org/src/info/b3fa58dd7403dbd4###https://www.mail-archive.com/sqlite-users@mailinglists.sqlite.org/msg114382.html###https://www.mail-archive.com/sqlite-users@mailinglists.sqlite.org/msg114394.html###https://www.oracle.com/security-alerts/cpujan2020.html###https://www.oracle.com/technetwork/security-advisory/cpuoct2019-5072832.html', 'CWE-125', '7.5', '5', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:N/S:U/C:H/I:N/A:N', 'AV:N/AC:L/Au:N/C:P/I:N/A:N');
INSERT INTO `cve_general` VALUES ('CVE-2019-9937', '1', 'sqlite', '', '', 'null_pointer_dereference', null, null, '', 'In SQLite 3.27.2, interleaving reads and writes in a single transaction with an fts5 virtual table will lead to a NULL Pointer Dereference in fts5ChunkIterate in sqlite3.c. This is related to ext/fts5/fts5_hash.c and ext/fts5/fts5_index.c.', 'https://security.netapp.com/advisory/ntap-20190416-0005/###https://sqlite.org/src/info/45c73deb440496e8###https://www.mail-archive.com/sqlite-users@mailinglists.sqlite.org/msg114383.html###https://www.mail-archive.com/sqlite-users@mailinglists.sqlite.org/msg114393.html###https://www.oracle.com/security-alerts/cpujan2020.html###https://www.oracle.com/technetwork/security-advisory/cpuoct2019-5072832.html', 'CWE-476', '7.5', '5', 'CVSS:3.0/AV:N/AC:L/PR:N/UI:N/S:U/C:N/I:N/A:H', 'AV:N/AC:L/Au:N/C:N/I:N/A:P');
